INSERT INTO `duoxiang` VALUES ('1', '马克思主义中国化根据在于（   ） ', 'ABC', '中国革命正反两方面的实践经验', '马克思主义理论的内在要求', '解决中国问题的需要', '中国优秀文化的创新逻辑');
INSERT INTO `duoxiang` VALUES ('2', '从“马克思主义中国化”科学命题提出的过程可知它本身是（   ) ', 'ABCD', '一个历史过程', '一个由浅入深的过程', '一个由感性到理性的过程', '一个由少数人认识到形成全党共识的过程');
INSERT INTO `duoxiang` VALUES ('3', '从“马克思主义中国化”科学命题提出的过程中可知（   ) ', 'ABCD', '李大钊最早就有过运用马克思主义解决中国实际问题的思考', '马克思主义中国化思想不是哪一个人的突发奇想，而是领导集体思想发展的必然结果', '毛泽东1938', '中国革命的思想主题就是“马克思主义中国化”');
INSERT INTO `duoxiang` VALUES ('4', '马克思主义中国化的科学内涵是指（   ) ', 'ABC', '运用马克思主义理论解决中国的具体问题', '将中国革命的实践经验.历史经验提升为理论', '把马克思主义融入中国优秀文化之中形成有中国特色的马克思主义理论', '实现中国优秀文化的创造性转换');
INSERT INTO `duoxiang` VALUES ('5', '马克思主义中国化就是运用马克思主义解决中国革命.建设和改革的实际问题。这就意味着（   ) ', 'ABCD', '中国革命.建设和改革必然遇到许多特殊的复杂问题', '照搬马克思主义和外国经验不可能解决中国的特殊问题', '马克思主义中国化就是为了解决中国的具体间题', '要真正运用马克思主义解决中国的具体问题，必须紧密结合中国国情和时代条件');
INSERT INTO `duoxiang` VALUES ('6', '马克思主义中国化含义之一就是把马克思主义根植于中国的优秀文化之中。这就是说（   ) ', 'ABCD', '马克思主义作为外来文化要为中国人民广泛接受并发挥作用就必须民族化', '马克思主义中国化不是照搬马克思主义，不能把马克思主义当教条', '马克思主义中国化也不是对中国文化的复制', '要批判继承，使马克思主义与中国文化中的优秀成果结合并且创造出新的文明成果');
INSERT INTO `duoxiang` VALUES ('7', '马克思主义中国化的历史进程表明中国共产党的历史在一定意义上是（   ) ', 'ABCD', '提出和探索马克思主义中国化的历史', '在实践中不断推进马克思主义中国化的历史', '不断产生中国化马克思主义新成果的历史', '克服各种僵化教条和自由化倾向的过程');
INSERT INTO `duoxiang` VALUES ('8', '第一次马克思主义中国化所克服的错误倾向主要有（   ) ', 'AB', '马克思主义教条化', '共产国际和苏联经验神圣化', '马克思主义民族化', '马克思主义封建化');
INSERT INTO `duoxiang` VALUES ('9', '马克思主义中国化的重要意义在于马克思主义中国化的理论成果（   ) ', 'ABCD', '引领中国革命.建设.改革不断取得伟大胜利', '提供使全国各族人民团结奋斗的强大精神支柱', '不断开拓马克思主义在中国发展的新境界', '证明与时俱进是马克思主义的本质特征');
INSERT INTO `duoxiang` VALUES ('10', '马克思主义与中国实际相结合的两次艰辛与成功的探索和两次历史性飞跃成果是（   ) ', 'ABCD', ' 一次是新民主主义革命和社会主义革命建设探索', '一次是改革开放和现代化建设实践', ' 一次历史性飞跃的理论成果是毛泽东思想', '一次历史性飞跃的理论成果是中国特色社会主义理论体系');
INSERT INTO `duoxiang` VALUES ('11', '中国特色社会主义理论体系是马克思主义中国化第二次历史性飞跃所产生的第二大理论成果，到目前为止主要包括（   ) ', 'BCD', '毛泽东思想', '邓小平理论', ' “三个代表”重要思想', '科学发展观');
INSERT INTO `duoxiang` VALUES ('12', '改革开放以来我们取得一切成绩和进步的根本原因，归结起来就是（   ) ', 'AB', '开辟了中国特色社会主义道路', '形成了中国特色社会主义理论体系', '坚持党的基本路线', '加强社会主义民主与法制建设');
INSERT INTO `duoxiang` VALUES ('13', '中共十七大指出，中国特色社会主义道路，就是（   ） ', 'ABCD', '在中国共产党领导下，立足基本国情', '以经济建设为中心，坚持四项基本原则，坚持改革开放', '解放和发展社会生产力，巩固和完善社会主义制度，建设社会主义市场经济.社会主义民主政治.社会主义先进文化.社会主义和谐社会', '建设富强民主文明和谐的社会主义现代化国家');
INSERT INTO `duoxiang` VALUES ('14', ' 中国特色社会主义伟大旗帜是（   ）', 'AB', ' 当代中国发展进步的旗帜', ' 全党全国各族人民团结奋斗的旗帜', ' 世界和平发展的旗帜', ' 第三世界国家现代化建设的旗帜');
INSERT INTO `duoxiang` VALUES ('15', ' 中国特色社会主义理论体系改变了（   ） ', 'ABC', ' 中国人民的面貌', '社会主义中国的面貌', ' 中国共产党的面貌', '世界人民的面貌');
INSERT INTO `duoxiang` VALUES ('16', '中国共产党人在如何运用马克思列宁主义普遍原理来解决中国革命实践中的问题上，曾出现过的主要错误倾向是（   ) ', 'AD', '教条主义', '投降主义', '冒险主义', '经验主义');
INSERT INTO `duoxiang` VALUES ('17', '毛泽东思想形成的思想文化条件主要有（   ) ', 'BD', '近代西方资产阶级思想在中国的传播', '新文化运动', '五四群众运动', '马克思主义在中国的广泛传播');
INSERT INTO `duoxiang` VALUES ('18', '毛泽东思想产生的时代背景和社会历史条件是（   ) ', 'ABCD', '俄国十月革命开辟了无产阶级社会主义革命的时代', '五四新文化运动的兴起和马克思主义在中国的广泛传播', '中国工人阶级发展壮大并且在五四运动中登上中国政治舞台', '中国共产党领导的新民主主义革命的伟大实践');
INSERT INTO `duoxiang` VALUES ('19', '中共七大确立毛泽东思想为党的指导思想（   ) ', 'ABCD', '是毛泽东思想发展史上的里程碑', '标志着党在理论上的成熟', '标志着马列主义同中国实际的结合产生了第一次历史性的飞跃', '使全党的认识在马列主义.毛泽东思想的基础上统一起来，达到了空前的团结');
INSERT INTO `duoxiang` VALUES ('20', '在中国共产党历史上对毛泽东思想做出系统概括和阐述的文献有（   ) ', 'AB', '刘少奇在中共七大上所作的《关于修改党章的报告》', '1981年《关于建国以来党的若干历史问题的决议》', '《关于若干历史问题的决议》', '《中共十一届三中全会决议》');
INSERT INTO `duoxiang` VALUES ('21', '土地革命战争后期和抗日战争时期，毛泽东思想得到系统总结与多方面展开而达到成熟，主要体现在（   ) ', 'ABCD', '建构了毛泽东思想的哲学体系', '系统阐述了新民主主义革命的基本理论和基本纲领', '阐述了人民战争的思想', '完善和发展了党的建设理论');
INSERT INTO `duoxiang` VALUES ('22', '解放战争时期和新中国成立以后，毛泽东思想继续发展的成果主要是（   ) ', 'ABCD', '人民民主专政理论', '社会主义改造和建立社会主义制度的基本方略', '提出马克思列宁主义同中国实际“第二次结合”的任务', '建设社会主义道路的艰辛探索');
INSERT INTO `duoxiang` VALUES ('23', '毛泽东思想是一个体系，它具有的鲜明特点是（   ) ', 'ABC', '科学性', '独创性', '完整性', '思维性');
INSERT INTO `duoxiang` VALUES ('24', '毛泽东思想的活的灵魂，是贯穿于毛泽东思想全部内容及其各个部分的立场.观点和方法。主要包括的基本方面是（   ) ', 'ABD', '实事求是', '群众路线', '统一战线', '独立自主');
INSERT INTO `duoxiang` VALUES ('25', '群众路线是毛泽东思想活的灵魂的三个基本方面之一。具体是指（   ) ', 'AB', '把人民群众是历史的创造者的唯物史观运用到党的全部活动中', '坚持一切为了群众，一切依靠群众，从群众中来，到群众中去的根本工作路线', '做到发展成果由全体人民共享', '做到权为民所用，利为民所谋，情为民所系');
INSERT INTO `duoxiang` VALUES ('26', '独立自主是毛泽东思想活的灵魂的三个基本方面之一。具体包括（   ) ', 'ABC', '坚持独立思考，走自己的路', '坚定不移地维护民族独立，国家主权', '把立足点放在自己力量的基础上，同时积极争取外援，学习外国有益的东西', '把独立自主.自力更生与对外开放相结合');
INSERT INTO `duoxiang` VALUES ('27', '毛泽东在马克思主义中国化方面的重要地位及特殊作用主要有（   ) ', 'ABCD', '第一次明确提出马克思主义中国化的历史任务', '系统阐述了马克思主义中国化的科学理论', '开辟了马克思主义在中国发展的现实道路，并且取得了第一次飞跃的伟大理论成果', '提出“第二次结合”的科学命题，为中国特色社会主义指出了方向');
INSERT INTO `duoxiang` VALUES ('28', '正确认识毛泽东思想的历史地位和指导意义，有一个怎样科学评价毛泽东和毛泽东思想的问题.这个问题关系到（   ) ', 'ABC', '党和国家几十年奋斗的成就和历史', '党的团结.人民的凝聚和国家安定', '党和国家现在与未来的发展道路', '中国与世界革命的旗帜');
INSERT INTO `duoxiang` VALUES ('29', ' “文化大革命”结束后，在对毛泽东思想的认识问题上，存在过的两种错误倾向是（   ) ', 'AD', '认为凡是毛泽东作出的一切决策.指示都必须坚决维护', '认为毛泽东的所有主张都是有正确.错误之分', '认为只要按照毛泽东的既定方针办，就能解决一切问题', '借口毛泽东晚年错误全面否定毛泽东.毛泽东思想');
INSERT INTO `duoxiang` VALUES ('30', '邓小平和我们党对毛泽东的正确评价认为，毛泽东是伟大的马克思主义者.伟大的无产阶级革命家.战略家和理论家。毛泽东晚年特别是在“文化大革命”中确实犯有严重错误，但是，就他的一生来说，(    ) ', 'ABCD', '他的功绩是第一位的，错误是第二位的', '他的功绩远远大于他的过失', '他的错误是一个伟大的革命家，一个伟大的马克思主义者在探索中所犯的错误', '他的错误与林彪.“四人帮”利用毛泽东的信任篡权谋私有本质区别');
INSERT INTO `duoxiang` VALUES ('31', '刘少奇在七大《 关于修改党章的报告》 中，对毛泽东思想作了深刻的理论分析，指出毛泽东思想是（   )', 'ABCD', '马克思列宁主义的理论与中国革命的实践之统一的思想', '马克思主义民族化的优秀典型', '中国人民百余年来历史选择的结果和斗争经验的总结，中国共产党唯一正确的指导思想和总路线', '中国的马克思主义和中国人民完整的革命建国理论');
INSERT INTO `duoxiang` VALUES ('32', '中国共产党成立以来在历史上具有深远意义的伟大转折是（    ） ', 'AC', '遵义会议', '中共七大', '中共十一届三中全会', '中共十四大');
INSERT INTO `duoxiang` VALUES ('33', '邓小平理论形成与发展的时代背景和社会历史条件主要是指（   ) ', 'ABCD', '时代依据是和平与发展成为时代主题', '理论依据是马克思列宁主义.毛泽东思想', '历史依据是我国和其他国家社会丰义建设的历史经验', '现实依据是我国改革开放和现代化建设的伟大实践');
INSERT INTO `duoxiang` VALUES ('34', ' 1992 年春的南方谈话，是邓小平理论的集大成之作，其中的主要观点有（   ) ', 'ABC', '姓“资”姓“社”的问题', ' “三个有利于”的判断标准', '社会主义本质的科学论断', '科学发展观');
INSERT INTO `duoxiang` VALUES ('35', ' 邓小平理论形成和发展过程中的两个“宣言书”是指（   ） ', 'AC', ' 解放思想，实事求是，团结一致向前看', '党的十一届三中全会公报', ' 1992年邓小平南方谈话', '党的十五大报告');
INSERT INTO `duoxiang` VALUES ('36', ' 邓小平理论是（   ） ', 'ABCD', ' 马克思列宁主义基本原理与当代中国实践和时代特征相结合的产物', '毛泽东思想在新的历史条件下的继承和发展', ' 马克思主义在中国发展的新阶段', '引领着中国特色社会主义事业不断前进');
