INSERT INTO `duoxiang` VALUES ('40', '邓小平理论是（   ） ', 'ABCD', '马克思主义在中国发展的新阶段', '与马克思列宁主义.毛泽东思想一脉相承的科学体系', '马克思主义与时代特征和当代中国实践相结合的产物', '中国共产党的指导思想，中华民族的精神支柱');
INSERT INTO `duoxiang` VALUES ('41', '邓小平理论之所以与马克思列宁主义.毛泽东思想是一脉相承的科学体系，是因为它（   ） ', 'BCD', '在所要解决的历史课题上是一致的', '在基本立场上是一致的', '在思想基础上是一致的', '在基本原则上是一致的');
INSERT INTO `duoxiang` VALUES ('42', ' 毛泽东思想.邓小平理论都是（   ） ', 'ABCD', ' 马克思主义基本原理与中国实际相结合的产物', '党和人民集体智慧和实践经验的总结', ' 指引中国人民推动社会发展和进步的精神力量', '在实践中不断丰富和发展的理论');
