INSERT INTO `duoxiang` VALUES ('148', '下列关于近代中国城市小资产阶级的说法正确的是(   ) ', 'ACD', '是中国革命的可靠同盟军和基本动力之一', '属于地主阶级的一部分', '属于劳动者', '包括知识分子.青年学生.小商人.手工业者和自由职业者');
INSERT INTO `duoxiang` VALUES ('149', '下列有关中国资产阶级的说法正确的是(   ) ', 'ABCD', '中国共产党同资产阶级建立和被迫分裂统一战线是中国革命的一个基本特点', '中国资产阶级分为买办资产阶级和民族资产阶级两个部分，前者大部分时期是革命的对象，后者是革命的动力之一', '中国民族资产阶级同官僚资产阶级既有矛盾和对立的一面，又有千丝万缕的联系', '无产阶级领导的革命统一战线，要争取民族资产阶级参加，并在特殊条件下(如抗日战争时期)把大资产阶级也包括在内');
INSERT INTO `duoxiang` VALUES ('150', '毛泽东对民族资产阶级的分析包括(   ) ', 'ABCD', '是一个既有革命性又有妥协性的阶级', '他们同帝国主义.封建主义有着千丝万缕的联系', '他们的政治主张是实现资产阶级民主共和国', '他们代表资本主义的生产关系');
INSERT INTO `duoxiang` VALUES ('151', '新民主主义革命必须由无产阶级领导，这是(   ) ', 'ABC', '由无产阶级的特性决定的', '近代中国革命发展的必然结果', '由中国革命的性质和前途决定的', '由无产阶级的天然领导权决定的');
INSERT INTO `duoxiang` VALUES ('152', '中国无产阶级除具备世界无产阶级的一般优点外，还有下列特殊优点(   ) ', 'ABC', '深受帝国主义.封建主义和资本主义的三重压迫，革命最坚决，', '大多来自于破产的农民，易于结成工农联盟', '分布相当集中，便于组织起来', '与先进的生产方式相联系，富于组织性.纪律性');
INSERT INTO `duoxiang` VALUES ('153', '下列关于新民主主义革命的说法正确的是(   ) ', 'ABCD', '在性质上仍属于资产阶级民主革命', '在范畴上属于世界无产阶级社会主义革命的一部分', '实质是无产阶级领导的农民革命', '革命前途是社会主义方向的');
INSERT INTO `duoxiang` VALUES ('154', ' 新民主义总路线包括的具体内容是(    ) ', 'ABCD', '革命的对象', '革命的动力', '革命的领导权', '革命的性质和前途');
INSERT INTO `duoxiang` VALUES ('155', '“民主主义革命是社会主义革命的必要准备，社会主义革命是民主主义革命的必然趋势。”对这段话理解正确的有(   ) ', 'BCD', '忽视了中国革命的发展规律', '廓清了民主主义革命和社会主义革命的界限', '指出了民主主义革命与社会主义革命的继承关系', '论证了民主主义革命和社会主义革命的联系');
INSERT INTO `duoxiang` VALUES ('156', '关于新民主主义革命时期对中国的资本主义的认识正确的是(   ) ', 'BD', '分为官僚资本主义和民族资本主义两部分，都是中国革命的对象', '官僚资本主义是中国革命的对象，而民族资本主义是中国革命要保护的对象', '官僚资产阶级和民族资产阶级都是中国革命的敌人', '官僚资产阶级是中国革命的对象，而民族资产阶级是中国革命的动力之一。');
INSERT INTO `duoxiang` VALUES ('157', '毛泽东在《新民主主义论》 中指出了文化与政治.经济的关系，其基本观点包括(   )', 'ABCD', '经济是基础', '一定的文化是一定的社会政治和经济的反映', '文化又给予伟大影响和反作用于一定社会的政治和经济', '政治是经济的集中反映');
INSERT INTO `duoxiang` VALUES ('158', '江泽民在2001 年“七一”讲话中对中国共产党领导的新民主主义革命根本目的作了高度概括，指出革命的根目的是(   ) ', 'ABCD', '取消帝国主义在中国的特权', '消灭地主阶级和官僚资产阶级的剥削和压迫', '改变买办的封建的生产关系以及改变建立在这种经济基础之上的腐朽的政治上层建筑', '确立人民民主专政为核心的新的政治上层建筑，从根本上解放被束缚的生产力');
INSERT INTO `duoxiang` VALUES ('159', '毛泽东在新民主主义革命总路线中所说的“人民大众”，即革命动力是指(   ) ', 'ABCD', '工人阶级', '农民阶级', '城市小资产阶级', '民族资产阶级');
INSERT INTO `duoxiang` VALUES ('160', '毛泽东在《论人民民主专政》一文中提出的“人民”的内涵包括(   ) ', 'ABD', '工人阶级', '农民阶级', '城市小资产阶级', '民族资产阶级');
INSERT INTO `duoxiang` VALUES ('161', '抗日民族统一战线中的进步势力有(   ) ', 'ABCD', '无产阶级', '农民阶级', '城市小资产阶级', '八路军.新四军');
INSERT INTO `duoxiang` VALUES ('162', '抗日民族统一战线中的中间势力包括(   )', 'ACD', '民族资产阶级', '官僚小资产阶级', '开明绅士', '地方实力派');
INSERT INTO `duoxiang` VALUES ('163', '中国共产党在抗日民族统一战线中对顽固派作斗争时采取“有理.有利.有节”的原则，这一原则的依据(    ) ', 'ABCD', '大地主大资产阶级的亲英美派和汉奸亲日派是有区别的', '顽固派在抗日方面有既抗日又动摇但不愿投降的两面性', '顽固派在联共方面有既联共又反共但不愿决裂的两面性', '抗日民族统一战线是坚持抗战.争取胜利的基本条件');
INSERT INTO `duoxiang` VALUES ('164', '毛泽东在《论持久战》 一文中分析了中日双方的基本特点，提出抗日战争持久战的主要依据是(   )', 'ABCD', '敌强我弱', '敌小我大', '敌退步我进步', '敌寡助我多助');
INSERT INTO `duoxiang` VALUES ('165', '毛泽东在《论持久战》 中揭示了抗日战争三阶段的发展规律，三阶段是指(   ) ', 'ABC', '敌之战略进攻.我之战略防御的防御阶段', '敌之战略保守.我之准备反攻的相持阶段', '我之战略反攻.敌之战略退却的决胜阶段', '敌之战略溃败.我之战略决战的决胜阶段');
INSERT INTO `duoxiang` VALUES ('166', '中国共产党通过统一战线争取和实现革命领导权的基本经验是(   ) ', 'ABCD', '必须建立以工农联盟为基础的广泛的统一战线', '在同资产阶级建立统一战线时，实行独立自主.又联合又斗争的方针', '建立一支共产党领导的强大的革命武装', '加强无产阶级政党自身的建设');
INSERT INTO `duoxiang` VALUES ('167', '毛泽东为延安整风运动撰写的指导性文件是(   ) ', 'ACD', '《改造我们的学习》', '《学习和时局》', '《整顿党的作风》', '《反对党八股》');
INSERT INTO `duoxiang` VALUES ('168', '土地革命战争时期，毛泽东同志以马克思主义为指导，发表了《中国的红色政权为什么能够存在？》 .《井冈山斗争》 .《星星之火，可以燎原》 .《反对本本主义》 等重要著作，这些著作(   ) ', 'AD', '是毛泽东思想形成的阶段性成果', '指出了中国革命的经验是人民民主专政', '概括了新民主主义革命的总路线和基本纲领', '提出了农村包围城市.武装夺取政权的革命道路理论');
INSERT INTO `duoxiang` VALUES ('169', '毛泽东指出，中国革命战争的主要特点有(   ) ', 'ABCD', '政治经济发展不平衡', '敌人的强大', '人民军队的弱小', '共产党的领导');
INSERT INTO `duoxiang` VALUES ('170', '毛泽东关于人民军队建设的思想主要包括(   )', 'BCD', '积极防御方针', '保持党对军队的绝对领导', '以全心全意为人民服务为唯一宗旨', '建立健全军队的政工制度，做好思想政治工作');
INSERT INTO `duoxiang` VALUES ('171', '抗日民族统一战线的特点包括(   ) ', 'ACD', '国共两个政权.两个军队的合作', '采取党外合作的方式', '处于一个既有利又复杂的国际大环境中', '广泛的民族性和极大的复杂性37');
INSERT INTO `duoxiang` VALUES ('172', '新民主主义革命时期的统一战线包括两个联盟，它们是(   ) ', 'BC', '工农联盟', '无产阶级同其他劳动人民的联盟', '无产阶级同可以合作的非劳动人民的联盟', '无产阶级同民族资产阶级的联盟');
INSERT INTO `duoxiang` VALUES ('173', '武装斗争.土地革命.根据地建设在“工农武装割据”中各自的地位是(   ) ', 'ABCD', '武装斗争是主要形式', '土地革命是基本内容', '根据地是开展武装斗争和土地革命的战略依托', '以建立和扩大根据地为中心工作');
INSERT INTO `duoxiang` VALUES ('174', '中华人民共和国成立，标志着我国(   )', 'ABC', '已经结束了半殖民地半封建社会的历史', '新民主主义革命的结束.社会主义革命的开始', '开始进入新民主主义社会', '开始进入社会主义社会');
INSERT INTO `duoxiang` VALUES ('175', '在民主革命时期中国共产党建设所遇到的特殊困难有(   ) ', 'ABCD', '理论准备不足', '党员中农民和小资产阶级出身的占大多数', '中国封建社会历史较长，封建主义思想影响严重', '共产国际在较长一段时间内的教条主义倾向和对中国共产党的不适当干预');
INSERT INTO `duoxiang` VALUES ('176', ' 1945 年4 月，毛泽东在《论联合政府》 中提出的党的优良作风有(   ) ', 'ABD', '理论和实践相结合的作风', '和人民群众紧密地联系在一起的作风', '艰苦奋斗的作风', '批评与自我批评的作风');
INSERT INTO `duoxiang` VALUES ('177', '新民主主义文化是科学的文化，其内涵包括(   ) ', 'ABCD', '科学的内容', '反对一切封建思想和封建迷信', '主张实事求是，理论和实际相统一', '对待中外文化要取其精华，去其糟粕');
INSERT INTO `duoxiang` VALUES ('178', '“工农武装割据”思想的主要内容是(   )', 'ABD', '以土地革命为主要内容', '以武装斗争为主要形式', '加强党的领导', '以农村革命根据地建设为战略阵地');
INSERT INTO `duoxiang` VALUES ('179', '中国红色政权为什么能够存在的原因是(  )', 'ABCD', ' 中国政治经济发展的不平衡性', ' 国民革命的影响', ' 全国革命形势继续向前发展', ' 由相当力量的正式红军的存在和共产党的有力量及政策正确性');
INSERT INTO `duoxiang` VALUES ('180', '中国的武装斗争实质社会是无产阶级领导的农民革命是因为(  )', 'ABCD', '中国革命的基本问题是农民问题', ' 农民是中国革命的主要力量', '农民是中国革命的主要来源', ' 农村革命根据地是中国革命走向胜利的战略阵地');
INSERT INTO `duoxiang` VALUES ('181', '鸦片战争后，由于外国资本主义的入侵给中国带来的变化主要体现在( )', 'ABCD', ' 出现了先进的生产方式', ' 民族资本主义有了一定程度的发展', ' 单一的封建社会变成了半封建社会', ' 中国由一个独立的国家变成一个半殖民地国家');
INSERT INTO `duoxiang` VALUES ('182', '近代以来，中华民族面临两大历史任务：一个是求得民族独立和人民解放，一个是求得国家的繁荣富强和人民的共同富裕。这两大任务的相互关系是( )', 'BCD', ' 前者主要解决生产力问题，后者主要是解决生产关系发展问题', ' 两者的主题.内容好实现方式各不相同，不能相互替代，但又息息相关，不能分离', '前一任务为后一任务扫清障碍，创造必要的前提', '后一任务是前一任务的最终目的和必然要求');
INSERT INTO `duoxiang` VALUES ('183', '在经济文化落后的近代中国，建设一个马克思主义政党面临的特殊困难有( )', 'ACD', ' 党的理论准备不足', ' 党缺乏领导革命的实践经验', '马克思主义中国化的任务非常艰巨', '党内外易受各种非无产阶级思想的侵袭');
INSERT INTO `duoxiang` VALUES ('184', '中华人民共和国的成立标志着(   )', 'ABD', '半殖民地半封建社会结束', '中国进入新民主主义社会', '中国进入社会主义社会', '新民主主义革命基本胜利');
INSERT INTO `duoxiang` VALUES ('185', '中国由新民主主义社会向社会主义社会转变的主客观条件和必然性在于(   )', 'ABCD', '政治上实现了全国大陆的解放和统一，建立了各级地方人民政权，提供了强有力的政治保障', '经济上没收了官僚买办资本，建立了社会主义性质的国家经济，奠定了相当的物质基础', '初步积累了利用.限制.改造私营工商业和引导农民进行互助合作的实践经验', '二战后欧亚一批国家先后走上社会主义道路，形成了以苏联为首的社会主义阵营');
INSERT INTO `duoxiang` VALUES ('186', '新民主主义革命和社会主义革命的关系是(   )', 'CD', '新民主主义革命和社会主义革命的任务相同', '两个革命之间需要有一个资本主义的过渡阶段', '新民主主义革命是社会主义革命的必要准备', '社会主义革命是新民主主义革命的必然趋势');
INSERT INTO `duoxiang` VALUES ('187', '从1949年10月中华人民共和国成立到1956年社会主义改造基本完成的历史定位是(   )', 'ABCD', '新民主主义的社会性质', '不是一个独立的社会形态', '是由新民主主义到社会主义转变的过渡性的社会形态', '是社会主义.资本主义因素同时存在又以社会主义因素为主导的社会形态');
INSERT INTO `duoxiang` VALUES ('188', '在新民主主义社会的五种经济成分中，主要的经济成分是(   )', 'ABC', '社会主义经济', '个体经济', '资本主义', '国家资本主义');
INSERT INTO `duoxiang` VALUES ('189', '新中国成立初期，中国处于新民主主义社会，其经济成分有(   )', 'ABCD', '社会主义国营经济', '个体经济', '私人资本主义经济', '合作社经济及国家资本主义经济');
INSERT INTO `duoxiang` VALUES ('190', '新民主主义社会中的社会主义因素包括(   )', 'ABCD', '国营经济', '合作制经济', '文化上马克思主义的指导地位', '政治上共产党领导的人民民主政权');
INSERT INTO `duoxiang` VALUES ('191', '在新民主主义社会中，国内的主要矛盾是(   )', 'AB', '工人阶级同资产阶级的矛盾', '人民大众同帝国主义.封建主义及其走狗国民党反动派残余的矛盾', '生产力和生产关系之间的矛盾', '上层建筑和经济基础之间的矛盾');
INSERT INTO `duoxiang` VALUES ('192', '建国后的土地改革完成后，我国国内的主要矛盾是(   )', 'AC', '工人阶级和资产阶级的矛盾', '经济基础与上层建筑的矛盾', '社会主义道路与资本主义道路的矛盾', '生产关系与生产力之间的矛盾');
INSERT INTO `duoxiang` VALUES ('193', '社会主义改造中，国家资本主义的高级形式有(   )', 'BD', '经销代销', '个别企业的公私合营', '加工订货', '全行业公私合营');
INSERT INTO `duoxiang` VALUES ('194', '下列关于新民主主义社会的说法是错误的包括(   )', 'BC', '从新中国成立到社会主义改造完成，我国社会性质是新民主主义社会', '它是独立的社会形态', '它属于资本主义体系的社会形态', '它带有过渡性质，是近代中国由半殖民地半封建社会走向社会主义社会的中介和桥梁');
INSERT INTO `duoxiang` VALUES ('195', '过渡时期总路线主要内容被概括为“一化三改造”，其中“三改”是指(   )', 'ABC', '个体农业的社会主义改造', '个体手工业的社会主义改造', '资本主义工商业的社会主义改造', '知识分子思想改造运动');
INSERT INTO `duoxiang` VALUES ('196', '过渡时期总路线“一化三改”，是一条社会主义建设和社会主义改造同时并举的路线，体现的是(   )', 'ABC', '社会主义工业化和社会主义改造的紧密结合', '解放生产力与发展生产力的统一', '变革生产关系与发展生产力的统一', '改造人与改造生产关系的结合');
INSERT INTO `duoxiang` VALUES ('197', '党在过渡时期的总路线和总任务是(   )', 'ABCD', '逐步实现国家的社会主义工业化', '实现对农业的社会主义改造', '实现对手工业的社会主义改造', '实际对资本主义工商业的社会主义改造');
INSERT INTO `duoxiang` VALUES ('198', '过渡时期总路线中的“两翼”是指(   )', 'BCD', '实现国家社会主义工业化', '对农业的社会主义改造', '对手工业的社会主义改造', '对资本工商业的社会主义改造');
INSERT INTO `duoxiang` VALUES ('199', '在对资本主义工商业改造过程中所采取的“四马分肥”是指(   )', 'ABCD', '所得税.', '企业公积金', '：工人福利基金', '资本家的利润(包括股息和红利)');
INSERT INTO `duoxiang` VALUES ('200', '中国社会主义改造道路的特点有(   )', 'ABCD', '社会主义工业化和社会主义改造同时并举', '采取了一系列逐步过渡的由低级到高级的社会主义改造形式', '和平改造特别是对资产阶级实现了和平赎买', '对经济制度的改造与对人的改造相结合');
INSERT INTO `duoxiang` VALUES ('201', '在实现农业合作化的过程中，中国共产党采取循序渐进的步骤，采用的形式为(   )', 'ACD', '农业生产互助组', '变工队', '初级农业合作社', '高级农业合作社');
INSERT INTO `duoxiang` VALUES ('202', '对手工业的社会主义改造经历了由小到大.由低级到高级的三个步骤，具体是(   )', 'ABC', '手工业供销小组具有社会主义萌芽性质', '手工业供销合作社半社会主义性质', '手工业生产合作社是社会主义性质的集体经济', '手工工厂是地方国营经济');
INSERT INTO `duoxiang` VALUES ('203', '我国农业合作化由低级到高级循序渐进的主要阶段与形式是(   )', 'BCD', '私人资本主义性质的单干', '带社会主义萌芽性质的农业生产互助组', '半社会主义性质的初级农业合作社', '完全社会主义性质的农业高级社');
INSERT INTO `duoxiang` VALUES ('204', '对个体农业社会主义改造采取的原则是(   )', 'ACD', '自愿互利', '互助合作', '典型示范', '国家帮助');
INSERT INTO `duoxiang` VALUES ('205', '50年代，我国在对资本主义工商业进行社会主义改造过程中创造的国家资本主义的具体形式有(   )', 'ABCD', '加工订货', '统购包销', '经销代销', '公私合营');
INSERT INTO `duoxiang` VALUES ('206', '建国后到社会主义改造基本完成前的中国新民主主义条件下的国家资本主义是社会主义性质的国营经济与私人资本主义经济之间发生合作关系的一种(   )', 'AD', '带有很大社会主义性质的', '新型社会主义', '混合经济', '新型资本主义经济');
INSERT INTO `duoxiang` VALUES ('207', '具有中国特点的社会主义改造道路的内容是(   )', 'ABCD', '社会主义工业化和社会主义改造同时并举', '通过一系列逐步过渡的由低级到高级的社会主义改造形式', '和平改造特别是对资产阶级实现了和平赎买', '对经济制度的改造与对人的改造相结合');
INSERT INTO `duoxiang` VALUES ('208', '在对资本主义工商业进行的改造中，初级形式的国家资本主义表现为(   )', 'ABCD', '委托加工', '计划订货', '统购包销', '经销代销');
INSERT INTO `duoxiang` VALUES ('209', '我国农业合作化运动的主要特点与经验是(   )', 'ABCD', '先集体化.后机械化', '自愿互利.典型示范.国家帮助.循序渐进', '农业社会主义改造与发展农业生产并举', '农业社会主义改造与资本主义工商业社会主义改造结合');
INSERT INTO `duoxiang` VALUES ('210', '过渡时期总路线反映了中国社会由新民主主义走向社会主义的历史必然，这是因为(   )', 'ABC', '国家工业化是国家独立和富强的必要条件', '对非社会主义经济成分进行改造是建立社会主义和实现工业化的需要', '土地改革完成后，中国国内的主要矛盾已经是无产阶级和资产阶级之间，社会主义道路和资本主义道路之', '发展生产力已经成为全国的中心任务');
INSERT INTO `duoxiang` VALUES ('211', '新中国对资本主义工商业进行改造的主要经验有(   )', 'ABCD', '和平赎买政策', '国家资本主义道路', '把对企业的改造和对资产阶级分子的改造结合起来', '由初级到高级逐步过渡');
INSERT INTO `duoxiang` VALUES ('212', '我国社会主义改造的失误和偏差主要是(   )', 'ABCD', '要求过急', '工作过粗，改变过快', '形式过于简单划一', '1956年以后对一部分原工商业者的使用和处理也不很恰当');
INSERT INTO `duoxiang` VALUES ('213', '在我国社会主义改造历史上，有两个事实是世界历史上各种革命大变动中罕见的，这就是(   )', 'BD', '没有使用暴力，反而是通过和平的方式完成的', '没有造成生产力的破坏，反而促进了工农业和整个国民经济的发展', '没有通过自下而上的方式，反而是通过自上而下方式完成的', '没有引起巨大的社会动荡，反而极大地加强了人民的团结，获得人民普遍拥护下完成的');
INSERT INTO `duoxiang` VALUES ('214', '20世纪50年代初，我国对个体农业进行社会主义改造的成功经验有(   )', 'AB', '在土地改革的基础上，不失时机地引导个体农民走互助合作的道路', '遵循自愿互利.典型示范.国家帮助的原则', '实行“三级所有.队为基础”的农村阶级政策', '全面推广家庭联产承包责任制');
INSERT INTO `duoxiang` VALUES ('215', '我国社会主义改造的重大意义是(   )', 'ABCD', '走出了一条中国特色的社会主义改造道路', '创造性地完成了由新民主主义革命向社会主义革命的转变', '使中国这个占世界人口四分之一的东方大国进入了社会主义社会', '实现了中国历史上最深刻.最伟大的社会变革');
INSERT INTO `duoxiang` VALUES ('216', '20世纪50年代中期，社会主义改造基本完成，标志着(　)', 'ABD', '社会主义制度在我国已经确立', '我国进入了社会主义初级阶段', '我国步入了社会主义改革时期', '我国实现了新民主主义向社会主义过渡');
INSERT INTO `duoxiang` VALUES ('217', '社会主义基本制度的确立，具有重大意义，体现在(　)', 'ABCD', '为社会主义现代化建设创造了制度条件', '使广大劳动人民真正成立国家的主人和生产资料的主人', '是全世界社会主义运动史上的又一个伟大的历史性胜利', '是马克思列宁主义关于社会主义革命理论在中国正确运用和创造性发展的结果');
INSERT INTO `duoxiang` VALUES ('218', '从新民主主义向社会主义过渡的经济条件有(   ) ', 'ABCD', '近代资本主义经济和现代工业的初步发展', '国营经济的壮大', '个体农民有组织起来.走互助合作道路的愿望', '私人资本主义经济开始纳入国家资本主义的轨道');
INSERT INTO `duoxiang` VALUES ('219', '在我国之所以能够争取赎买的方式对资本主义工商业进行和平改造，原因在于(   ) ', 'ABCD', '民族资产阶级有剥削工人取得利润的一面，又有拥护宪法愿意接受社会主义改造的一面', '中国共产党与民族资产阶级长期保持着统一战线的关系', '人民民主专政的国家政权并以强大的国营经济掌握了国家的经济命脉', '国家对粮食和工业原料统购统销');
INSERT INTO `duoxiang` VALUES ('220', '我国社会主义改造之所以存在一些偏差和失误，思想观念上的主要问题是(    )', 'ABCD', '过于追求纯粹单一的社会主义经济成分', '公有制形式的理解和选择过于简单化', '对公有制经济与非公经济可以共同发展缺乏认识', '对什么是社会主义以及社会主义发展阶段并没有完全搞清楚');
INSERT INTO `duoxiang` VALUES ('221', '我国农业合作化由低级到高级循序渐进的主要阶段与形式是(   ) ', 'BCD', '私人资本主义性质的单干', '带社会主义萌芽性质的农业生产互助组', '半社会主义性质的初级农业合作社', '完全社会主义性质的农业高级社');
INSERT INTO `duoxiang` VALUES ('222', '资本主义工商业社会主义改造的重要意义是(   ) ', 'ABCD', '社会主义公有制经济制度基本确立', '劳动人民从此成为生产资料主人，摆脱受剥削受压迫地位', '进一步解放了社会生产力，“一五”提前超额完成', '丰富发展了马克思主义的社会主义改造理论');
INSERT INTO `duoxiang` VALUES ('223', '中国由新民主主义向社会主义过渡的条件有(   ) ', 'ABCD', '社会主义国营经济的建立和不断壮大', '无产阶级及其政党的领导', '苏联社会主义建设的成就及其对中国建设的支援', '人民民主专政政权的巩固');
INSERT INTO `duoxiang` VALUES ('224', '毛泽东在《论十大关系》中提出的观点有(    ) ', 'ABCD', '把国内外一切积极因素调动起来，为社会主义事业服务', '吸取苏联的经验教训，少走弯路，引以为戒', '采用多发展一些农业和轻工业的办法发展重工业', '更多地利用和发展沿海工业，特别是轻工业');
INSERT INTO `duoxiang` VALUES ('225', '下列文章中，毛泽东提出要向一切国家学习的思想是(     )  ', 'AD', '《论十大关系', '《我们党的一些历史经验》', '《纪念孙中山先生', '《关于正确处理人民内部矛盾的问题》');
INSERT INTO `duoxiang` VALUES ('226', '“我国的工业过去集中在沿海。所谓沿海，是指辽宁.河北.北京.天津.河南东部.山东.安徽.江苏.上海.浙江.福建.广东.广西。我国全部轻工业和重工业，都有约百分之七十在沿海，只有百分之三十在内地。这是历史上形成的一种不合理的状况。沿海的工业基地必须充分利用，但是，为了平衡工业发展的布局，内地工业必须大力发展。在这两者的关系问题上，我们也没有犯大的错误，只是最近几年，对于沿海工业有些估计不足，对它的发展不那么十分注重了。这要改变一下。”对这段话分析正确的有(    )  ', 'AB', '这段话论述的实际上是关于生产力布局的问题', '这段话论述了中国东部地区与西部地区发展的关系问题', '这段话认为我们要“改变一下”，要将工业主要布局在内地', '这段话提出沿海工业和内地工业必须同时同步发展');
INSERT INTO `duoxiang` VALUES ('227', '1959年底至1960年初，毛泽东在读苏联《政治经济学教科书》时，认为社会主义社会的发展阶段有(    )  ', 'AB', '不发达的社会主义', '比较发达的社会主义', '发达的社会主义', '社会主义初级阶段和社会主义高级阶段');
INSERT INTO `duoxiang` VALUES ('228', '毛泽东关于社会主义革命和社会主义建设的重要思想，集中体现在下列著作中(    )  ', 'ABCD', '《在中国共产党第七届中央委员会第二次全体会议上的报告》', '《论人民民主专政》', '《论十大关系》', '《在扩大的中央工作会议上的讲话》');
INSERT INTO `duoxiang` VALUES ('229', '20世纪50年代中期，反映以毛泽东为代表的中国共产党人探索中国自己的社会主义建设道路取得的思想理论成果有(    )  ', 'ABC', '毛泽东的《论十大关系》', '刘少奇的中共八大上作的政治报告', '毛泽东的《关于正确处理人民内部矛盾的问题》', '《关于建国以来党的若干历史问题的决议》');
INSERT INTO `duoxiang` VALUES ('230', '1956年召开的中共八大的政治报告指出，我国国内的主要矛盾是(    )  ', 'AD', '人民对建立先进的工业国的要求同落后的农业国的现实之间的矛盾', '工人阶级同民族资产阶级之间的矛盾', '发展重工业和发展农业.轻工业之间的矛盾', '人民对于经济文化迅速发展的需要同当前经济文化不能满足人民需要的状况之间的矛盾');
INSERT INTO `duoxiang` VALUES ('231', '毛泽东认为中国工业化道路中最重要的问题，就是安排好几个方面的发展关系，包括(    )  ', 'ABD', '重工业', '轻工业', '牧业', '农业');
INSERT INTO `duoxiang` VALUES ('232', '毛泽东在《关于正确处理人民内部矛盾的问题》中，根据宪法的原则，提出了在政治生活中判断言论和行动是非的六条标准，并指出其中最重要的是(  )  ', 'BD', '有利于团结全国各族人民，而不是分裂人民', '有利于社会主义改造和社会主义建设，而不是不利于社会主义改造和社会主义建设', '有利于巩固民主集中制，而不是破坏或者削弱这个制度', '有利于巩固共产党的领导，而不是摆脱或者削弱这种领导');
INSERT INTO `duoxiang` VALUES ('233', '毛泽东在《关于正确处理人民内部矛盾的问题》一文中提出的正确处理人民内部矛盾的总方针是(    )  ', 'ABCD', '调动一切积极因素', '化消极因素为积极因素', '团结一切可能团结的力量', '建设社会主义伟大国家');
INSERT INTO `duoxiang` VALUES ('234', '1954年，周恩来在一届人大一次会议上宣布的“四个现代化”目标是(    ) ', 'ABCD', '现代化的工业', '现代化的农业', '现代化的交通运输业', '现代化的国防');
INSERT INTO `duoxiang` VALUES ('235', '毛泽东在《论十大关系》中对我国基本国情的分析是(    ) ', 'ABC', '中国是一个大国', '人口多.底子薄', '生产力不发达', '经济发展不平衡');
INSERT INTO `duoxiang` VALUES ('236', '60年代初，对我国农业生产责任制表示支持和赞成的党和国家领导人有(    )  ', 'BCD', '毛泽东', '刘少奇.邓小平', '陈云.李富春', '邓子恢');
INSERT INTO `duoxiang` VALUES ('237', '在我国人民内部矛盾包括(     )  ', 'ABCD', '工人农民之间的矛盾', '工农和知识分子的矛盾', '人民群众和领导机关的矛盾', '工人阶级同民族资产阶级的矛盾');
INSERT INTO `duoxiang` VALUES ('238', '社会主义社会存在的两类不同性质的矛盾是(    )  ', 'BC', '无产阶级与资产阶级的矛盾', '人民内部矛盾', '敌我矛盾', '经济基础和上层建筑的矛盾');
INSERT INTO `duoxiang` VALUES ('239', '社会主义社会的基本矛盾包括(    )  ', 'AD', '生产力与生产关系的矛盾', '人民内部矛盾', '人民日益增长的物质文化需要与目前生产力不能满足这种需要的矛盾', '经济基础和上层建筑的矛盾');
INSERT INTO `duoxiang` VALUES ('240', '1957年2月毛泽东在最高国务会议上发表《关于正确处理人民内部矛盾的问题》的讲话，还系统阐述了社会主义社会两类不同性质矛盾的理论。这一理论的基本点包括(    ) ', 'ABC', '社会主义社会存在着敌我矛盾和人民内部矛盾两类不同性质的社会矛盾', '两类矛盾的性质不同，解决的方法也不同', '社会主义改造基本完成后，正确处理人民内部矛盾的问题已经成为国际政治生活的主题', '社会主义社会的主要矛盾是落后的社会生产与先进的社会制度的矛盾');
INSERT INTO `duoxiang` VALUES ('241', '毛泽东提出的国民经济建设方针有(    )  ', 'ABCD', ' “农业为基础.工业为主导”', '按照农业.轻工业.重工业的次序安排国民经济发展比例', '重视价值规律的作用', '发展社会主义商品生产');
INSERT INTO `duoxiang` VALUES ('242', '1953年到1964年我国提出的实现现代化目标的“两步走”战略步骤是指(    )  ', 'AD', '第一步建立一个独立的比较完整的工业体系和国民经济体系', '第一步形成了农轻重并举的工业化格局', '第二步全面实现农业.工业.国家和科学技术的现代化', '第二步全面实现“四个现代化”，使我国经济走在世界前列');
INSERT INTO `duoxiang` VALUES ('243', '毛泽东探索中国社会主义道路的重要意义是(    )  ', 'ABCD', '标志着中国共产党开始摆脱单一的苏联模式', '开始寻找独立自主的适合中国国情的社会主义建设道路', '探索中正确的.有价值的思想观点和创造性的思考成为毛泽东思想的重要组成部分和新财富', '对我国社会主义建设取得很大成就起了指导作用');
INSERT INTO `duoxiang` VALUES ('244', '毛泽东社会主义探索的历史局限在于(     )  ', 'ABCD', '艰辛探索但总体上没有摆脱苏联模式', '探索的一些正确成果没有在实践上坚持下去甚至出现了某些严重的背离', '探索中另一种“左”的倾向逐步发展，最终导致“文化大革命”', '最根本的是对“什么是社会主义.怎样建设社会主义”没有完全搞清楚');
INSERT INTO `duoxiang` VALUES ('245', '毛泽东提出的无产阶级革命事业接班人具备的条件有(    )  ', 'ABCD', '必须是真正的马克思列宁主义者', '必须是全心全意为中国和世界的绝大多数人服务的革命者', '必须是能够团结绝大多数人一道工作的无产阶级政治家', '必须是党的民主集中制的模范执行者，必须谦虚谨慎，戒骄戒躁，富于自我批评精神');
INSERT INTO `duoxiang` VALUES ('246', '邓小平在中共八大所作的《关于修改党的章程的报告》中提出执政的中国共产党要接受监督，这种监督主要来自(    )  ', 'ACD', '人民群众的监督', '舆论监督', '民主党派和无党派人士的监督', '共产党内部的监督');
INSERT INTO `duoxiang` VALUES ('247', '建国后毛泽东执政党建设思想的积极成果主要有(    ) ', 'ABCD', '坚持“两个务必”', '扫除官气，不断克服官僚主义', '执政党必须接受来自党内和党外的监督', '加强思想政治工作，培养千百万个无产阶级革命事业的接班人');
INSERT INTO `duoxiang` VALUES ('248', '毛泽东在《在中国共产党全国宣传工作会议上的讲话》里就知识分子问题主要谈及的问题有(    )  ', 'BCD', '知识分子的工人阶级属性问题', '我国知识分子的情况', '知识分子的改造问题', '知识分子同工农群众结合的问题');
INSERT INTO `duoxiang` VALUES ('249', '毛泽东在《在延安文艺座谈会上的讲话》中提出的“为什么人”的问题，他提出文艺服务对象应该包括(    )  ', 'ABCD', '小资产阶级', '农民', '兵士', '工人');
INSERT INTO `duoxiang` VALUES ('250', '毛泽东概括的“中国工业化道路”的主张包括(     )  ', 'ABCD', '我国的经济建设是以重工业为中心', '以重工业为中心发展经济建设的同时，必须充分注意发展农业和轻工业', '尽可能解决经济建设中出现的人民内部的矛盾', '为了使我国变为工业国必须认真学习苏联的先进经验');
INSERT INTO `duoxiang` VALUES ('251', '以毛泽东为代表的中国共产党人在对待传统文化和外国文化方面提出的方针有(     )  ', 'AB', '“古为今用”', '“洋为中用”', '“中体西用”', '“百花齐放”');
INSERT INTO `duoxiang` VALUES ('252', '在毛泽东提出的“三个世界”的理论中(  )  ', 'ABD', '苏联属于第一世界', '美国.苏联是第一世界', '中间派日本.欧洲.加拿大是第一世界', '亚洲(除日本).非洲和拉丁美洲是第三世界');
INSERT INTO `duoxiang` VALUES ('253', '符合“建设规模必须与国力相适应”观点的说法有(  )  ', 'BCD', '这是为纠正大跃进中的错误而提出的经济观点', '确立基本建设规模的大小，必须从国家的财力物力出发', '以国家可能运用的财力物力制约建设规模', '目的是防止经济建设中的冒进和保守');
INSERT INTO `duoxiang` VALUES ('254', '毛泽东在《论十大关系》中论述的中国工业化道路的特点是(    )  ', 'ABCD', '中国工业化道路要从中国国情出发', '发展国民经济要以农业为基础', '正确处理重工业与轻工业和农业发展的关系问题', '按农.轻.重的次序安排国民经济计划');
INSERT INTO `duoxiang` VALUES ('255', '毛泽东提出的关于正确处理农业.轻工业和重工业关系问题的思想有(    ) ', 'ACD', '发展工业和发展农业同时并举', '多发展重工业，少发展一些农业和轻工业', '以农.轻.重为序安排国民经济的方针', '以农业为基础，以工业为主导的总方针');
INSERT INTO `duoxiang` VALUES ('256', '新中国成立后，中国共产党把独立自主.自力更生应用到外交领域和经济建设方面，形成的方针.政策是(     )  ', 'AC', '独立自主的和平外交政策', '调整.巩固.充实.提高的方针', '自力更生为主.争取外援为辅的方针', '以农业为基础，以工业为主导的总方针');
INSERT INTO `duoxiang` VALUES ('257', '毛泽东《论十大关系》的报告中，提出我国经济工作中要正确处理好的主要关系有(    )  ', 'ABCD', '重工业和轻工业.农业的关系', '沿海工业和内地工业的关系', '经济建设和国防建设的关系', '国家.生产单位和生产者个人的关系');
INSERT INTO `duoxiang` VALUES ('258', '陈云在中共“八大”上提出的“三个主体，三个补充”的重要思想是指(    )  ', 'ABC', '在工商业经营方面，国家经营和集体经营为主补充一定的个体经营', '在生产计划方面，计划生产为主补充一定的自由生产', '在市场方面，国家市场为主补充一定范围的自由市场', '在分配方面，以按劳分配为主补充一定的其他分配形式');
INSERT INTO `duoxiang` VALUES ('259', '1956年毛泽东提出的“百花齐放，百家争鸣”的方针，其内涵包括(    ) ', 'ABC', '艺术上不同形式和不同风格可以自由发展，科学上不同学派可以自由争论', '不能用行政的力量强制推行一种风格.一种流派', '艺术和科学上的是非，应当通过艺术界.科学界的自由争论和实践去解决', '指导思想的多元化');
INSERT INTO `duoxiang` VALUES ('260', '“百花齐放，百家争鸣”方针提出的依据有(  )  ', 'ABC', '承认社会主义社会仍然存在着各种矛盾', '适应繁荣社会主义文化的需要', '吸取建国以来文化建设的经验教训', '从中国具体国情出发');
INSERT INTO `duoxiang` VALUES ('261', '毛泽东比较系统地阐述“双百方针”的著作和讲话有(    )  ', 'AB', '《论十大关系》', '《关于正确处理人民内部矛盾的问题》', '《论人民民主专政》', '《不要四面出击》');
INSERT INTO `duoxiang` VALUES ('262', '社会主义初级阶段是(   )', 'ABCD', '我国最大的实际', '我国最基本的国情', '我们党对社会主义和中国国情认识上的一次飞跃', '我们党制定路线方针政策的基本依据和根本出发点');
INSERT INTO `duoxiang` VALUES ('263', '新时期30多年改革开放与现代化建设，使我国各方面发生了巨大的变化，但是有“两个没有变”，主要是指(   )', 'AB', '我国社会主义初级阶段的基本国情没有变', '社会主义初级阶段的社会主要矛盾没有变', '社会主义初级阶段发展生产力的根本任务没有变', '社会主义初级阶段公有制主体地位没有变');
INSERT INTO `duoxiang` VALUES ('264', '建设中国特色社会主义必须从我国现在正处于并将长期处于社会主义初级阶段这一最大的实际，最基本的国情出发，从而意味着我们(   )', 'ABC', '不能从主观愿望出发', '不能从这样那样的外国模式出发', '不能马克思主义教条式理解和主观附加的某些错误观点出发', '不能从社会主义空想出发');
INSERT INTO `duoxiang` VALUES ('265', '建设中国特色社会主义，必须弄清楚(   )', 'ABC', '什么是社会主义', '什么是初级阶段的社会主义', '怎样建设社会主义，怎样建设初级阶段的社会主义', '什么是社会主义优越性');
INSERT INTO `duoxiang` VALUES ('266', '邓小平关于我国当前处于“社会主义初级阶段”的科学论断揭示了我国当前的(   )', 'ABC', '社会制度性质', '社会主义发展水平', '社会发展程度和阶段', '社会发展模式');
INSERT INTO `duoxiang` VALUES ('267', '在发展阶段问题上的经验教训证明，在坚持社会主义的问题上(   )', 'ABCD', '只讲社会主义社会的性质和方向不讲发展程度与水平，会产生“左”的错误', '只讲发展程度与发展水平，不讲性质与方向，又可能离开社会主义', '只有坚持对社会主义初级阶段的社会性质与发展程度水平的统一认识和把握，才能自觉科学推进中国特色社会主义', '全面把握社会性质.方向与发展程度的水平才能克服“左”和“右”的干扰');
INSERT INTO `duoxiang` VALUES ('268', '党的十三大之所以能够把初级阶段问题提到全局高度加以科学论述，是因为(   )', 'AB', '有了改革开放和社会主义现代化的初步实践', '继续推进改革开放.现代化建设迫切需求从根本上解决对我国社会主义建设出发点的认识问题', '有了十二大以来的认识基础', '需要面对各种社会思潮的挑战');
INSERT INTO `duoxiang` VALUES ('269', '邓小平曾经指出：现在虽然说我们也在搞社会主义，但事实上不够格。这里讲的“不够格”主要是指(   )', 'ABC', '不够马克思所讲的“共产主义低级阶段”即社会主义阶段的“资格”', '在物质技术基础方面不够格，无法逾越生产力和商品经济充分发展的阶段', '在社会经济制度和上层建筑方面的不成熟.不完善', '与苏联模式社会主义有很大的差别');
INSERT INTO `duoxiang` VALUES ('270', '社会主义初级阶段这一论断的基本含义是指(   )', 'AC', '从社会的性质看，我国的社会已经是社会主义社会', '从社会的性质看，我国的社会还处于向社会主义过渡的时期', '从社会的发展程度看，我国的社会主义社会还处在不发达的阶段', '从社会的发展程度看，我国的社会主义已达到中等发达水平。');
INSERT INTO `duoxiang` VALUES ('271', '社会主义初级阶段的两层含义，一是明确初级阶段的社会性质，一是规范我国现实中社会主义社会的发展程度，二者关系在于(   )', 'ABCD', '不了解社会性质或判断错误，就会离开社会主义，就不可能正确认识我国社会的基本特征.主要矛盾和根本方向', '不能正确分析当前我国社会所处的发展阶段，就可能超越或落后于形势的发展，从而作出错误的判断', '坚持而不离开社会主义是根本原则，是前提', '正视而不超越发展阶段是保证，是防止急于求成或落后于形势发展的认识基础');
INSERT INTO `duoxiang` VALUES ('272', '社会主义初级阶段与新民主主义社会有许多相似之处，但在社会性质上存在着明显的区别，主要有(   )', 'ABCD', '公有制经济在新民主主义社会居于领导地位，但不是社会经济主体，当时经济总量上占绝对优势的是个体农业.手工业经济，初级阶段的公有制是社会经济主体', '社会主义初级阶段已经进入社会主义，新民主主义社会虽然社会主义因素为主导居于领导地位，但社会主义制度尚未建立', '新民主主义社会主要矛盾是社会主义与资本主义两条道路.资产阶级与无产阶级两个阶级的矛盾，以“共同纲领，为基础加以解决；社会主义初级阶段主要矛盾是人民日益增长的物质文化需要与落后的社会生产之间的矛盾', '解决新民主主义社会主要矛盾的根本途径是工业化与社会主义改造同时并举，通过确立公有制主体地位推进社会主义工业化；社会主义初级阶段解决主要矛盾的根本途径是以经济建设为中心的社会主义现代化');
INSERT INTO `duoxiang` VALUES ('273', '社会主义初级阶段，从发展进程看，其主要特征有(   )', 'ABCD', '逐步摆脱不发达状态，基本实现社会主义现代化的历史阶段', '由自然经济和半自然经济占很大比重，逐步转变为经济市场化程度较高的历史阶段', '由文盲半文盲占很大比重.科技教育文化落后，逐步转变为科技教育文化比较发达的历史阶段', '由贫困人口占很大比重.人民生活水平比较低，逐步转变为全体人民比较富裕的历史阶段');
INSERT INTO `duoxiang` VALUES ('274', '中国特色社会主义道路的基本特征体现在(   )', 'ABCD', '坚持以生产资料社会主义公有制为主体，多种经济成分共同发展', '实行以按劳分配为主体，多种分配方式并存的分配制度', '坚持以工人阶级领导的.以工农联盟为基础的人民民主专政', '坚持以马克思列宁主义.毛泽东思想.邓小平理论“三个代表”重要思想和科学发展观为指导，反对搞指导思想多元化');
INSERT INTO `duoxiang` VALUES ('275', '邓小平同志关于社会主义初级阶段的论断，使我们对社会主义建设的(   )', 'ABCD', '长期性', '紧迫性', ' 复杂性', ' 艰巨性');
INSERT INTO `duoxiang` VALUES ('276', '社会主义初级阶段的长期性，从根本上是决定于(   )', 'AB', '中国进入社会主义的历史条件', '建成社会主义所需要的物质技术基础', '社会主义所需要的经济现代化和商品化.市场化程度', '人民群众的物质文化生活和思想觉悟水平');
INSERT INTO `duoxiang` VALUES ('277', '改革.发展.稳定的关系是(   )', 'ABC', ' 改革是动力', ' 发展是目的', '稳定是前提', ' 发展是手段');
INSERT INTO `duoxiang` VALUES ('278', '邓小平曾指出，改革是(   )', 'ABCD', ' 决定中国命运的一着', ' 中国的第二次革命', '社会主义制度的自我完善', ' 解放生产力');
INSERT INTO `duoxiang` VALUES ('279', '坚持党的基本路线一百年不动摇的原因是(   )', 'ABC', '党的基本路线正确与否对我们党至关重要', '现阶段党的基本路线是建设有中国特色的社会主义理论的核心内容和政治灵魂', ' 坚持党的基本路线一百年不动摇，是与社会主义初级阶段的长期性和战略目标相联系的', ' 它是党的代表会议通过并写进了决议的');
INSERT INTO `duoxiang` VALUES ('280', '基本路线中的“一个中心”和“两个基本点”的内在联系是(   )', 'ABCD', ' 互相储存，不可分割，它们相辅相成，缺一不可', ' 经济建设为核心，是主体，两个基本点都服务于经济建设这个中心', '只有坚持以经济建设为中心，两个基本点才有坚实的基础和正确的方向', '两个基本点是确保经济建设为中心的两个支柱，为发展社会生产力提供了强大动力和政治保证');
INSERT INTO `duoxiang` VALUES ('281', '党的基本路线主要内容包括(   )', 'ABCD', '建设富强民主文明和谐的社会主义现代化国家', '“一个中心，两个基本点”', ' “领导和团结全国各族人民”，领导核心是中国共产党，依靠力量是全国各族人民', '自力更生.艰苦创业的基本方针');
INSERT INTO `duoxiang` VALUES ('282', '坚持党的基本路线关键是坚持以经济建设为中心不动摇，是因为(   )', 'ABCD', '以经济建设为中心是新时期最根本的拨乱反正', '是兴国之要以及党和国家兴旺发达.长治久安的根本保证', '关系到社会主义前途命运和社会主义现代化成败', '如果经济建设为中心发生动摇，党的基本路线就会被动摇，就有丧失物质基础的危险');
INSERT INTO `duoxiang` VALUES ('283', '在建设中国特色社会主义实践中坚持改革开放和四项基本原则的辩证统一，因为(   )', 'ABCD', '四项基本原则是立国之本', '改革开放是强国之路', '离开改革开放.四项基本原则就不能与时俱进，就会变成僵化的教条', '离开四项基本原则，改革开放就失去方向');
INSERT INTO `duoxiang` VALUES ('284', '坚持四项基本原则对改革开放和现代化建设的政治保证作用主要表现在(   )', 'ABC', '有一个坚定正确的政治方向', '有一个团结稳定的环境', '有一个统一的意志和行动', '使综合国力不断增强');
INSERT INTO `duoxiang` VALUES ('285', '四项基本原则是指(   )', 'ABCD', '社会主义道路', '人民民主专政', '中国共产党的领导', '马克思列宁主义.毛泽东思想');
INSERT INTO `duoxiang` VALUES ('286', '坚持四项基本原则，就必须(   )', 'ABCD', ' 旗帜鲜明地反对资产阶级自由化', '理直气壮地运用人民民主专政，巩固人民民主专政', ' 反对“左”的僵化思想', '通过改革开放赋予四项基本原则新的内容');
INSERT INTO `duoxiang` VALUES ('287', '对要坚持党的基本路线一百年不动摇，必须做到(   )', 'ABCD', ' 关键是坚持以经济建设为中心', ' 正确处理两个基本点之间的关系，把二者统一起来', ' 警惕和防止“左”右两种错误倾向', ' 更主要是防止“左”的错误');
INSERT INTO `duoxiang` VALUES ('288', '我国实现社会主义现代化的目标有(   )', 'ABCD', '富强', '民主', ' 文明', '和谐');
INSERT INTO `duoxiang` VALUES ('289', '党在社会主义初级阶段的基本纲领进一步明确了什么是社会主义初级阶段(   )', 'ABD', ' 建设有中国特色社会主义的经济', ' 建设有中国特色社会主义的政治', ' 建设有中国特色社会主义的法制', '建没有中国特色社会主义的文化');
INSERT INTO `duoxiang` VALUES ('290', '十三大制定了党在社会主义初级阶段的基本路线，十七大进一步丰富了基本纲领的内容，主要有(   )', 'ABCD', '明确规定了“一个中心，两个基本点”', '明确规定了党在社会主义初级阶段的奋斗目标', '明确指出了实现初级阶段奋斗目标的领导力量.依靠力量', '明确指出了实现初级阶段奋斗目标必须坚持的基本方针');
INSERT INTO `duoxiang` VALUES ('291', '最高纲领与最低纲领有区别又有联系，统一为实现共产主义的伟大实践中。具体是指(   )', 'ABCD', '最高纲领为最低纲领指明前进方向', '最低纲领为最高纲领的实现准备必要的条件', '坚持最高纲领不能离开现实空谈理想', '实现最低纲领不能只顾眼前迷失方向');
INSERT INTO `duoxiang` VALUES ('292', '1954年，周恩来在一届人大一次会议上宣布的“四个现代化”目标是(   )', 'ABCD', '现代化的工业', '现代化的农业', '现代化的交通运输业', '现代化的国防');
INSERT INTO `duoxiang` VALUES ('293', '1953年到1964年我国提出的实现现代化目标的“两步走”战略步骤是指(   )', 'AD', '第一步建立一个独立的比较完整的工业体系和国民经济体系', '第一步形成了农轻重并举的工业化格局', '第二步全面实现农业.工业.国家和科学技术的现代化', '第二步全面实现“四个现代化”，使我国经济走在世界前列');
INSERT INTO `duoxiang` VALUES ('294', '新时期1987年十三大提出的中国现代化“三步走”战略的重要意义在于(   )', 'ABCD', '突出体现党对国情认识的深化', '体现党的实事求是.在实践中检验真理发展真理的品格', '是第二代第三代领导总结国内外经验的结晶', '是对现代化客观规律的正确反映，对时代特征的深刻把握');
INSERT INTO `duoxiang` VALUES ('295', '我国现代化“三步走”战略特点是(   )', 'ABCD', '雄心壮志与实事求是相结合', '以人民生活为出发点.落脚点，把经济发展与提高人民生活水平和实现社会主义本质要求相统一', '不断完善富强民主文明和谐的现代化奋斗目标，强调全面协调', '体现社会主义制度优越性，更好更快地实现现代化');
INSERT INTO `duoxiang` VALUES ('296', '江泽民十六大政治报告提出的新三步走战略目标是(   )', 'ABCD', '2000年到2010年GDP翻一番', '2010年到2020年全面建设小康社会，GDP翻一番', ' 2020年到21世纪中叶基本实现现代化', '本世纪头20年全面建设小康社会');
INSERT INTO `duoxiang` VALUES ('297', '中国共产党第十七次全国代表大会报告对全面建设小康社会奋斗目标提出了新的更高要求。下列选项中不属于上述要求的是(   )', 'BD', '实现人均国内生产总值到2020年比2000年翻两番', '改善生态环境，以循环经济代替现有经济增长模式', '加快发展社会事业，人人享有基本医疗卫生服务', '加强文化建设，确保高等教育覆盖率增长50%');
INSERT INTO `duoxiang` VALUES ('298', '2007年十七大对全面建设小康社会的奋斗目标提出新的更高要求，具体是(   )', 'ABCD', '强调增强发展协调性，实现经济又好又快发展，建设生态文明', '扩大社会主义民主，更好保障人民权益和公平正义', '加强文化建设，明显提高全民族文化素质', '加快发展社会事业，全面改善人民生活');
INSERT INTO `duoxiang` VALUES ('299', '十七大提出建设生态文明的目标要求包括(   )', 'ABCD', '基本形成节约能源资源和保护生态环境的产业结构.增长方式.消费模式', '循环经济形成较大规模，可再生能源比重显著上升', '主要污染物排放得到有效控制，生态环境质量明显改善', '生态文明观念在全社会牢固树立');
INSERT INTO `duoxiang` VALUES ('300', '在社会主义初级阶段理论的形成发展过程中，具有代表性的是(     )', 'ABCD', '1981年十一届六中全会《决议》正式提出社会主义初级阶段的概念', ' 1982年十二大.1986年十二届六中全会分别对这一阶段的内容作了一些分析', '十三大完成对社会主义初级阶段及其基本路线的科学论述', '十五大制定了社会主义初级阶段的基本纲领，十七大进一步分析了新世纪新阶段的具体阶段性特征');
INSERT INTO `duoxiang` VALUES ('301', '党的十一届三中全会以前，党对我国社会主义社会性质的判断是正确的，认识失误主要是(    )', 'ABC', '发展阶段认识失误', '发展程度判断失误', '任务和政策脱离国情', '发展战略严重滞后');
INSERT INTO `duoxiang` VALUES ('302', '在我国社会主义的历史进程中，社会主义初级阶段是(    ) ', 'ABCD', '新民主主义社会后一个新的历史发展时期', '中国特色社会主义的初始阶段', '1956年社会主义改造基本完成以后到21世纪中叶社会主义现代化基本实现之间的一个历史时期', '中国在1956年后直到中等发达社会主义之前的历史发展时期');
INSERT INTO `duoxiang` VALUES ('303', '实践证明，从我国社会主义初级阶段的实际出发建设社会主义，就是(    )', 'ABCD', '切合中国实际的社会主义', '中国特色的社会主义', '充满活力，人民群众更加拥护的社会主义', '真正坚持社会主义');
INSERT INTO `duoxiang` VALUES ('304', '社会主义所需要的物质技术基础是不断发展变化并且逐步提高的，具体表现为(    ) ', 'ABCD', '马克思设想社会主义物质技术基础是机器大工', '列宁进一步强调社会主义要建立在全国电气化基础上', '邓小平强调现代化五十年代一个样，六十年代一个样，七十年代就变不一样了', '我们党最终确定的是21世纪知识经济时代的现代化');
INSERT INTO `duoxiang` VALUES ('305', '最高纲领与最低纲领有区别又有联系，统一为实现共产主义的伟大实践中。具体是指(    ) ', 'ABCD', '最高纲领为最低纲领指明前进方向', '最低纲领为最高纲领的实现准备必要的条件', '坚持最高纲领不能离开现实空谈理想', '实现最低纲领不能只顾眼前迷失方向');
INSERT INTO `duoxiang` VALUES ('306', '我国现代化“三步走”战略特点是(    ) ', 'ABCD', '雄心壮志与实事求是相结合', '以人民生活为出发点.落脚点，把经济发展与提高人民生活水平和实现社会主义本质要求相统一', '不断完善富强民主文明和谐的现代化奋斗目标，强调全面协调', '体现社会主义制度优越性，更好更快地实现现代化');
INSERT INTO `duoxiang` VALUES ('307', '搞清楚“什么是社会主义.怎样建设社会主义”，(   )', 'ABCD', '是邓小平理论的首要的基本理论问题', '是因为过去对此并没有完全搞清楚', '是我国社会主义建设最根本的经验教训', '改革开放以来的分歧和困惑都是围绕这个问题进行的');
INSERT INTO `duoxiang` VALUES ('308', '邓小平社会主义本质论断的主要内容有(   )', 'ABCD', '突出生产力首要的基础地位及其与社会主义本质的内在联系', '在生产力与生产关系的相互关系中揭示社会主义本质，前两句话讲生产力，后两句讲社会主义生产关系本质特征', '突出共同富裕是社会主义的最终目标和本质要求', '突破计划经济是社会主义基本特征的传统观念');
INSERT INTO `duoxiang` VALUES ('309', '邓小平的社会主义本质论彻底否定了一系列社会主义的错误观念，主要观点是(   )', 'ABCD', '贫穷不是社会主义', '发展太慢不是社会主义', '平均主义不是社会主义', '两极分化也不是社会主义');
INSERT INTO `duoxiang` VALUES ('310', '邓小平社会主义本质论断的重要意义在于(   )', 'ABCD', '是对马克思主义科学社会主义的新发展', '是邓小平理论的首要的基本问题和重要组成部分', '是20世纪国内外社会主义经验教训的科学总结', '反映了时代要求和人民的愿望');
INSERT INTO `duoxiang` VALUES ('311', '邓小平反复强调社会主义最根本的任务是发展生产力，因为大力发展生产力(   )', 'ABCD', '是马克思主义的一个重要思想，是实现共产主义的物质基础', '是社会主义本质的内在要求', '是显示社会主义制度优越性和巩固社会主义制度的需要', '是解决社会主义初级阶段主要矛盾的根本手段');
INSERT INTO `duoxiang` VALUES ('312', '把解放生产力.发展生产力作为社会主义的本质规定，(   )', 'ABCD', '肯定了社会主义制度对促进生产力的巨大作用', '揭示了社会主义制度得以存在和发展的根据', '说明了社会主义的根本任务', '指明了消除两极分化和消灭剥削的根本途径');
INSERT INTO `duoxiang` VALUES ('313', '根据邓小平的社会主义本质论断，共同富裕是(   )', 'ABCD', '社会主义的根本目标', '社会主义的本质内容', '社会主义的根本原则', '社会主义优越性的根本体现');
INSERT INTO `duoxiang` VALUES ('314', '把解放生产力，发展生产力作为社会主义的本质规定性(  )', 'ABCD', '肯定了社会主义制度对促进生产力的巨大作用', '揭示了社会主义制度得以存在和发展的根据', '说明了社会主义的根本任务', '揭示了社会主义改革的客观根据');
INSERT INTO `duoxiang` VALUES ('315', '邓小平关于社会主义本质的论断体现了(   )', 'ABCD', '解放生产力与发展生产力的统一', '生产力与生产关系的统一', '实现社会主义本质与建设社会主义道路的统一', '目的与手段的统一');
INSERT INTO `duoxiang` VALUES ('316', '社会主义本质的新概括纠正了(   )', 'BC', '以往片面强调发展生产力，忽视生产关系变革的片面倾向', '以往忽视发展生产力，片面强调生产关系变革的片面倾向', '以往脱离生产力抽象谈论什么是社会主义的片面倾向', '以往脱离生产关系抽象谈论什么是社会主义的片面倾向');
INSERT INTO `duoxiang` VALUES ('317', '邓小平多次指出，我们必须坚持的社会主义的根本原则是(   )', 'AD', '公有制占主体', '发展多种所有制经济', '让一部分人先富起来', '共同富裕');
INSERT INTO `duoxiang` VALUES ('318', '社会主义本质新概括的实践意义在于(   )', 'ABC', '它为如何建设有中国特色的社会主义开辟了广阔的道路', '它为坚持社会主义的基本制度，发展和完善公有制指明了方向', '它为坚持和发展科学社会主义的理论开辟了道路', '它突破了把计划经济当作社会主义本质特征的传统观念');
INSERT INTO `duoxiang` VALUES ('319', '邓小平关于社会主义本质的论断包含的要点是(   )', 'ACD', '解放和发展生产力', '社会公平', '消灭剥削和消除两极分化', '最终实现共同富裕');
INSERT INTO `duoxiang` VALUES ('320', '社会主义的根本任务是发展生产力，是因为发展生产力是(   )', 'ABCD', '马克思主义的一个基本原则', '社会主义本质的内在要求', '显示社会主义制度的优越性和巩固社会主义制度的需要', '解决社会主义初级阶段主要矛盾的根本手段');
INSERT INTO `duoxiang` VALUES ('321', '邓小平关于社会主义根本任务的主要论点有(   )', 'BD', '社会主义的根本目的就在于发展生产力', '马克思主义最注重发展生产力', '社会主义的根本任务就是解放生产力', '发展生产力是社会主义本质的根本要求');
INSERT INTO `duoxiang` VALUES ('322', '现阶段发展生产力对于我国具有极端重要性和紧迫性的主要原因是(   )', 'ABCD', '提高人民生活水平的需要', '改变经济文化落后的状况的需要', '回应时代和新科技革命的挑战的需要', '解决社会各种矛盾与各种问题的需要');
INSERT INTO `duoxiang` VALUES ('323', '发展是硬道理，因为(   )', 'ACD', '发展是中国解决所有问题的关键', '发展是国民经济持续稳定健康的发展', '发展是坚持党的先进性和发挥社会主义制度优越性的根本体现', '发展是时代的主题，是世界各国的中心任务');
INSERT INTO `duoxiang` VALUES ('324', '邓小平强调“发展才是硬道理”，把发展生产力作为社会主义建设的根本任务，依据是(   )', 'ABCD', '符合马克思主义基本原理，《宣言》就提出无产阶级夺取政权后应该尽可能增加生产总量', '巩固和发展社会主义制度的必然要求，只有发展生产力有优越性，社会主义才能站住脚', '社会主义经验教训的深刻总结，只有生产发展.人民生活水平提高才会有更多的人相信社会主义', '和平发展是时代主题，发展生产力搞好了才能在综合国力竞争中占优势，有主动权');
INSERT INTO `duoxiang` VALUES ('325', '十七大报告指出，必须坚持把发展作为党执政兴国的第一要务。即要(   )', 'ABCD', '牢牢扭住经济建设这个中心', '坚持聚精会神搞建设.一心一意谋发展', '不断解放和发展社会生产力', '努力实现以人为本.全面协调可持续的科学发展');
INSERT INTO `duoxiang` VALUES ('326', '苏联解体.东欧剧变，原因是多方面的，最根本的原因在于这些国家执政的共产党人没有解决好发展问题，即(   )', 'ABCD', '先进生产力没有充分发展', '先进文化没有充分发展', '最广大人民的利益没有充分维护和发展', '动摇和丧失了共产党执政的物质基础和群众基础');
INSERT INTO `duoxiang` VALUES ('327', '始终代表先进生产力的发展要求，具体包括(   )', 'ABCD', '改造落后生产力', '提升传统生产力', '发展先进生产力', '最终整体达到发达生产力的水平');
INSERT INTO `duoxiang` VALUES ('328', '“中国共产党必须始终代表中国先进生产力的发展要求”这个命题主要涉及到以下几方面的问题(       )', 'BCD', '要大力提高我国各级党的领导干部的科技水平', '要大力提高我国各个领域生产力诸要素的科技含量', '要通过继续深化各个方面的改革开放为我国先进生产力的发展扫清障碍', '要慎重对待尚不适应先进生产力和时代发展要求的落后的生产方式');
INSERT INTO `duoxiang` VALUES ('329', '科教兴国战略的基本含义是(   )', 'ABCD', '全面落实科技是第一生产力的思想', '坚持教育为本，把科教摆在经济社会发展的重要位置', '增强国家科技实力与科技成果转化能力', '提高全民族科技文化素质，把经济建设转到依靠科技进步和提高劳动者素质的轨道上来，加速实现国家富强');
INSERT INTO `duoxiang` VALUES ('330', '人才强国战略的基本含义是在建设中国特色社会主义的伟大事业中(   )', 'ABCD', '把人才作为推进事业发展的关键因素', '努力造就数以亿计高素质劳动者和一大批专门人才.创新人才', '建设规模宏大.结构合理.素质较高的人才队伍', '开创人才辈出.人尽其才的新局面，使我国由人才大国转化为人才资源强国');
INSERT INTO `duoxiang` VALUES ('331', '“科学技术是第一生产力”意味着(   )', 'BC', '还有第二.第三生产力存在', '科学技术在历史发展中起第一位的决定作用', '现代科学日益成为生产的主导', '人文社会科学并不重要');
INSERT INTO `duoxiang` VALUES ('332', '邓小平关于社会主义本质的论断体现了  (    )  ', 'ABCD', '解放生产力与发展生产力的统一', '生产力与生产关系的统一', '发展生产力与实现共同富裕的统一', '目的与手段的统一');
INSERT INTO `duoxiang` VALUES ('333', '发展之所以成为中国共产党执政兴国的第一要务，是因为  (    ) ', 'ABCD', '发展是坚持党的先进性的要求', '发展是发挥社会主义制度优越性的要求', '发展是社会主义本质的要求', '发展决定着中国的前途和命运');
INSERT INTO `duoxiang` VALUES ('334', '社会主义的根本任务是发展生产力，这是因为，发展生产力是  (    )', 'ACD', '社会主义本质的内在要求', '建立社会主义市场经济体制的要求', '解决社会主要矛盾的要求', '巩固社会主义制度的要求');
INSERT INTO `duoxiang` VALUES ('335', '中共十七大政治报告指出，改革的目的是(    )', 'ABC', '解放和发展生产力，实现现代化.人民富裕.民族振兴', '推动社会主义制度的自我完善和发展，赋予社会主义新的生机和活力，建设中国特色社会主义', '在引领当代中国发展进步中加强和改进党的建设，保持和发展党的先进性，确保党始终走在时代前列', '实现经济建设.政治建设.文化建设和社会建设的全面协调，在经济社会的全面协调发展中促进人的自由与全面发展');
INSERT INTO `duoxiang` VALUES ('336', '改革开放是一场伟大的革命，是中国的第二次革命，主要是指改革(    )', 'ABCD', '与第一次革命不同，不是指一个阶级推翻另一个阶级的政治革命，而是社会主义的自我完善和发展', '不是个别方面的细枝末节的修修补补，而是对传统计划经济的根本性变革', '改革必然涉及和引起政治.经济.文化.教育.科技等社会生活各个领域的深刻变化，从变革的广度和深度而言实际上是一场革命', '革命是解放生产力，改革也是解放生产力，改革与过去革命的目标一样，都是为了解放生产力');
INSERT INTO `duoxiang` VALUES ('337', '邓小平关于改革有许多重要的评价，主要有(    )', 'ABCD', '改革是中国第二次革命', '改革是决定中国命运的关键一招', '改革是社会主义制度的自我完善和发展', '革命是解放生产力，改革也是解放生产力');
INSERT INTO `duoxiang` VALUES ('338', '社会主义社会的基本矛盾运动理论是邓小平和我们党提出改革决策的理论基础。毛泽东社会主义社会基本矛盾运动的思想主要有(  )', 'ABCD', '社会主义社会仍然存在矛盾并且推动社会主义社会向前发展；社会主义社会的基本矛盾仍然是生产关系和生产力.上层建筑和经济基础的矛盾运动，而且是社会主义社会不断前进的根本动力', '社会主义社会的基本矛盾是在人民利益一致基础上的非对抗性的矛盾，具有“基本适应与部分不适应”的特点', '社会主义可以依靠自身的力量，通过对生产关系和生产力.上层建筑与经济基础之间不相适应的部分进行调整而得到解决，不需要象资本主义社会那样采取激烈阶级斗争的方式', '在社会主义社会基本矛盾的基础上，我国社会存在着敌我矛盾.人民内部矛盾两种不同性质的矛盾，正确处理人民内部矛盾是国家政治生活的主题');
INSERT INTO `duoxiang` VALUES ('339', '邓小平在总结历史经验教训的基础上，对社会主义社会的基本矛盾，特别是对社会主义初级阶段的主要矛盾进行了深入的思考，丰富发展了毛泽东的社会主义社会矛盾学说，直接为社会主义改革提供了理论基础。其主要内容有(    )', 'ABCD', '判断一种生产关系和生产力是否相适应，要从实际出发，具体看它是否适应当时当地生产力的要求，能否推动生产力发展；即使社会主义性质的生产关系一旦超越社会主义初级阶段的生产力水平，也会阻碍生产力的发展', '解放生产力并非只是与一个阶级推翻另一个阶级的政治革命相联系，社会主义社会仍然有解放生产力的问题和挑战', '在明确社会主义社会基本矛盾的基础上，解决社会主要矛盾和确立根本任务具有内在统一性，以经济建设为中心的社会主义现代化建设是解决社会主义初级阶段主要矛盾的根本途径', '社会主义社会的基本矛盾可以而且必须通过社会主义制度的自我完善和发展不断加以解决，改革是社会主义社会的动力，是社会主义条件下解放和发展生产力的必由之路');
INSERT INTO `duoxiang` VALUES ('340', '关于社会主义社会基本矛盾的新观点，是社会主义改革的理论基础。邓小平提出，判断_种生产关系和生产力是否相适应，要从实际出发，具体问题具体分析，主要看它是否适应当时当地生产力的要求，能否推动生产力发展。从而表明(  )', 'ABCD', '即使社会主义社会生产关系，也不一定必然适应生产力的要求', '一些社会主义性质的生产关系，如果超越生产力水平，也会阻碍生产力发展', '一些非公经济，即使不是社会主义性质的生产关系，只要适合社会主义初级阶段的生产力水平，能够推动生产力发展，也是与生产力发展相适应的，也应该允许甚至支持.鼓励其存在和发展', '社会主义初级阶段的生产关系变革不是追求一大二公三纯，关键在于是否适应生产力发展要求，能否推动生产力发展');
INSERT INTO `duoxiang` VALUES ('341', '20世纪70年代末，中国改革首先在农村拉开序幕，农村改革的第一步是(    )', 'AD', '废除人民公社', '改革人民公社', '发展乡镇企业', '建立以家庭联产承包为主，统分结合.双层经营的新型集体所有制');
INSERT INTO `duoxiang` VALUES ('342', '农村改革在废除人民公社，实行家庭联产承包后，出现了乡镇企业异军突起的“意外收获”。乡镇企业异军突起的意义在于(  )', 'ABCD', '为从土地转移出来的剩余劳动力提供出路', '为提高农民收入找到新途径', '开创农村集体所有制改革道路', '推动整个工业和国民经济发展，为提高整个国家的改革承受力奠定了相当的物质基础');
INSERT INTO `duoxiang` VALUES ('343', '改革是一项崭新的事业，如何评价判断改革的成败与是非得失一再成为各种争论的焦点。邓小平在1992年的南方谈话中提出了“三个有利于”的判断标准。内容有(    )', 'ABD', '是否有利于发展社会主义社会生产力', '是否有利于增强社会主义国家的综合国力', '是否有利于提高公有制经济的控制力.影响力', '是否有利于改善.提高人民的生活水平');
INSERT INTO `duoxiang` VALUES ('344', '“三个有利于”的判断标准(    )', 'ABCD', '是由社会主义本质和根本任务决定的', '是判断各方面工作是非得失的根本标准', '是对生产力标准的深化和发展', '充分体现了解放思想.实事求是的思想路线');
INSERT INTO `duoxiang` VALUES ('345', '“三个有利于”的实质是强调(    )', 'BC', '不要争论', '在改革开放的具体措施上不要陷入姓“资”还是姓“社”的抽象争论', '对于改革开放的一些具体措施应该解放思想，放开手脚，大胆地闯，大胆地试', '在改革的性质问题上可以不问姓“资”姓“社”');
INSERT INTO `duoxiang` VALUES ('346', '在改革开放具体政策措施上之所以不能陷入姓“资”还是姓“社”的抽象争论，是因为(    )', 'ABCD', '不能把一切在资本主义条件下产生.广泛应用的经济现象都视为资本主义属性而加以否定', '使对一些确实属于资本主义的经济成分和因素，在社会主义初级阶段也不能一概加以排斥和否定，也要允许其存在和发展，即只要符合“三个有利于”标准的，也就是为社会主义所需要和允许的', '更不能把那些本来姓“社”，又合乎“三个有利于”却被错误地判定为资本主义的东西加以拒绝，同时对那些虽然姓“社”，却不符合“三个有利于”的东西，现在也不能盲目实行', '也不要把那些合乎“三个有利于”，不具有特殊社会制度属性，既不姓“资”也不姓“社”，可以为资本主义所用，也可以为社会主义所用的东西错误判定为资本主义的东西而加以排斥');
INSERT INTO `duoxiang` VALUES ('347', '进入新世纪新阶段，按照科学发展观的要求继续深化改革.扩大开放就是要(    )', 'ABCD', '毫不动摇地坚持改革方向，把改革创新精神贯彻到治国理政的各个环节', '提高改革决策的科学性，增强改革措施的协调性，把坚持改革开放同完善改革开放相结合，既要用科学发展.社会和谐推动和深化改革，又要在深化改革中实现科学发展.社会和谐', '坚持把改善人民生活作为正确处理改革.发展.稳定关系的重要结合点，使改革开放始终得到人民群众的支持和拥护', '着力构建充满活力.富有效率.更加开放.有利于科学发展的体制和机制，为发展中国特色社会主义提供强大动力和体制保障');
INSERT INTO `duoxiang` VALUES ('348', '2007年十七大的主题是“旗帜论”，又是“改革论”，其中关于改革的主要思想主张是(    )', 'ABCD', '在新的实践中坚持和发展中国特色社会主义，就必须坚持改革开放', '只有坚持改革开放才能不断开创中国特色社会主义新局面', '改革开放符合党心民心，顺应时代潮流，方向和道路是完全正确的，成绩和功绩不可否定，停顿和倒退没有出路', '要毫不动摇地坚持改革方向，以科学发展完善改革开放，提高改革开放决策科学性，增强改革措施协调性，使改革开放始终得到人民拥护和支持');
INSERT INTO `duoxiang` VALUES ('349', '改革开放是中国近现代史上一场新的伟大革命，是近代中国三次伟大革命之一，近代中国的三次伟大革命是(    )', 'ABC', '孙中山领导辛亥革命结束封建帝制，为近代中国发展进步打开了闸门', '毛泽东领导新民主主义革命推翻“三座大山”，建立起人民当家作主的新中国和社会主义基本制度，为当代中国发展进步创造了前提', '邓小平领导改革开放，使社会主义制度得到巩固和完善，为当代中国发展进步开辟了道路', '康有为领导戊戌变法，为近代中国开拓了民主思想进步的道路');
INSERT INTO `duoxiang` VALUES ('350', '邓小平领导新时期改革开放的必要性是(    )', 'ABCD', '拨乱反正，走出十年内乱给我国造成的危难与困境，治穷致富实现中华民族伟大复兴', '摆脱高度集中的计划经济体制的束缚，摆脱闭关锁国的封闭状态，增强综合国力，改善人民生活，坚持和发展社会主义', '抓住和平与发展的时代主题，顺应世界范围的新科技革命，使社会主义中国摆脱落后，赶上时代，进入世界先进行列', '正确评价毛泽东历史地位和毛泽东思想，使我们党高举马列主义.毛泽东思想的伟大旗帜继续前进');
INSERT INTO `duoxiang` VALUES ('351', '新时期以思想解放推动改革开放，实现了一系列历史性转变，主要有(    )', 'ABC', '从以阶级斗争为纲到以经济建设为中心', '从封闭半封闭到改革开放', '从计划经济到市场经济', '从僵化教条到解放思想');
INSERT INTO `duoxiang` VALUES ('352', '在改革.发展.稳定三者之间，都具有不同的地位作用，其中(    )', 'BCD', '改革是革命', '改革是动力', '发展是目的', '稳定是前提');
INSERT INTO `duoxiang` VALUES ('353', '十六届六中全会要求从和谐社会的高度处理好改革.发展.稳定的关系，主要指(    )', 'ABC', '以改革促进和谐', '以发展巩固和谐', '以稳定保障和谐', '以改革提升和谐');
INSERT INTO `duoxiang` VALUES ('354', '我们党关于正确处理改革.发展.稳定三者关系的基本经验和主要原则是(    )', 'ABC', '保持改革.发展.稳定的动态协调.相互促进', '把改革的力度.发展的速度和社会可以承受的程度统一起来', '把不断改善人民生活作为正确处理改革.发展.稳定关系的重要结合点', '在稳定中推进改革发展，在改革发展中保持稳定');
INSERT INTO `duoxiang` VALUES ('355', '之所以要把改善人民生活作为处理改革.发展.稳定关系的重要结合点，是因为(    )', 'ABC', '人民群众是改革发展的动力的主体', '人民群众是稳定的力量源泉和基础', '改善人民生活，让人民共享改革发展的成果，是改革.发展.稳定的共同目的', '保持党与人民群众的血肉联系，是稳定的关键');
INSERT INTO `duoxiang` VALUES ('356', '我们把对外开放作为基本国策，最重要的理论依据是邓小平的两个重要观点。它们是(    )', 'AB', '现在的世界是开放的世界', '中国的发展离不开世界', '世界的发展也不能没有中国', '中国是全球化推动力量和世界经济增长的重要引擎');
INSERT INTO `duoxiang` VALUES ('357', '对外开放要正确认识社会主义与资本主义的相互关系，就是要看到(    )', 'ABD', '社会主义与资本主义有相互对立.斗争的一面', '社会主义与资本主义有相互学习.借鉴.合作和利用的一面', '社会主义与资本主义是根本对立的两大阵营', '社会主义要最终取代资本主义，就必须吸收.利用当代资本主义的一切优秀成果建设社会主义');
INSERT INTO `duoxiang` VALUES ('358', '对外开放基本国策的确立依据是(    )', 'ABCD', '历史依据。近代中国落后挨打的主要原因是闭关锁国，在社会主义建设初期遇到挫折的原因也是因为被迫处于封闭和半封闭状态', '时代依据。当今世界处于经济全球化时代，世界各国相互依存，科技革命在世界范围内迅速崛起，生产要素在全球范围内合理配置，离开经济全球化和科技革命的世界潮流，关起门来搞社会主义不可能成功', '理论依据。邓小平强调“中国的发展离不开世界”，“世界的发展不能没有中国”。社会主义本质上是开放的，社会主义要吸收人类一切文明成果，包括当代资本主义的优秀成果', '现实依据。1978年后中国实行对外开放，取得了巨大的成就，实践证明对外开放是正确的，不是权宜之计而是中国的基本国策，是建设中国特色社会主义和中华民族伟大复兴的内在要求');
INSERT INTO `duoxiang` VALUES ('359', '邓小平对外开放思想的主要内容是(    )', 'ABCD', '建设经济特区的思想', '从沿海向内地逐步推进对外开放的思想利用外资的思想', '积极发展对外贸易和开展对外经济合作的思想', '正确处理对外开放和独立自主.自力更生关系的思想');
INSERT INTO `duoxiang` VALUES ('360', '邓小平对外开放思想的理论意义是(    )', 'ABCD', '邓小平对外开放思想是中国特色社会主义理论的有机组成部分', '邓小平对外开放思想丰富和发展了马克思主义', '邓小平对外开放思想为国家制定经济战略和方针政策提供了指导原则', '邓小平对外开放思想为中国对外开放理论的不断发展构建了基础');
INSERT INTO `duoxiang` VALUES ('361', '在领导设立经济特区.实行对外开放的过程中，邓小平曾经提出特区是“窗口”，主要指特区是(    )', 'ABCD', '技术的窗口', '管理的窗口', '知识的窗口', '对外开放的窗口');
INSERT INTO `duoxiang` VALUES ('362', '邓小平对外开放思想的实践意义是指(    )', 'ABCD', '全方位.多层次.宽领域对外开放格局逐步形成；对外贸易不断迈上新台阶，贸易大国地位已经确立', '利用外资从无到有，不断在总量和结构上取得历史性突破；各项对外经济合作实现了重大跨越', '国际经贸合作不断取得进展；对外开放对经济发展.体制改革和社会进步产生了重大推动作用', '中国对外开放对世界经济发展和国际经济格局产生了重要影响');
INSERT INTO `duoxiang` VALUES ('363', '进入21世纪，我国加入世贸组织后形成的对外开放基本格局是(    )', 'ABCD', '区域和双边经贸合作深度推进', '全方位', '多层次', '宽领域');
INSERT INTO `duoxiang` VALUES ('364', '在我国全方位.多层次.宽领域的对外开放格局中，所谓“宽领域”包括(    )', 'ABCD', '国际商品市场.资本市场', '技术市场.劳务市场', '能源.交通等基础产业', '金融.保险.房地产科技.教育.文化.体育.军工.服务业等');
INSERT INTO `duoxiang` VALUES ('365', '对外开放对我国经济发展.体制改革和社会进步产生了重大推动力作用，具体表现为(    )', 'ABC', '对外开放是经济增长的重要动力', '对外开放提升了我国的经济实力和国际地位', '对外开放促进了经济体制改革.市场制度建设和公共管理完善', '对外开放实现了世界贸易一流大国的地位');
INSERT INTO `duoxiang` VALUES ('366', '中国对外开放对世界经济发展和国际经济格局产生了重要影响，主要有(    )', 'ABCD', '中国对外开放带动了世界经济贸易增长', '中国对外开放惠及各国消费者与投资者', '中国对外开放优化了世界经济格局', '拓宽了全球产业结构调整和优化空间');
INSERT INTO `duoxiang` VALUES ('367', '社会主义改革的性质是(    )', 'ABC', '社会主义制度的自我完善', '社会主义制度的自我发展', '在社会主义制度下解放和发展生产力', '社会主义制度的根本变革');
INSERT INTO `duoxiang` VALUES ('368', '对外开放对我国经济安全的不利影响主要有(    )', 'ABCD', '对我国民族产业的生存和发展构成影响与危害', '对我国生态环境产生破坏', '发达国家以不等价交换.新贸易保护主义威胁我国的国际贸易安全', '资本全球流动对我国金融安全的影响');
INSERT INTO `duoxiang` VALUES ('369', '在对外开放中保障我国经济安全的主要措施有(    )', 'ABCD', '不断调整产业结构，实现产业结构优化升级，摆脱在国际分工中地位低下的不利状况，在国际竞争中发挥自己的比较优势', '提高经济实力和国际竞争力，争取在世界经济秩序中的发言权，积极参与国际游戏规则的制订', '强化风险意识，建立国家经济安全监控预警机制', '最根本的是全面落实科教兴国战略，加大科技和教育投入力度，增强科技实力和创造人才优势');
INSERT INTO `duoxiang` VALUES ('370', '在对外开放中影响我国经济安全的主要因素是(    )', 'ABCD', '西方跨国公司以垄断控制我国产业.企业和市场', '国际游资的冲击和国际金融危机的频繁发生', '西方发达国家新贸易保护主义的种种贸易壁垒政策和措施', '西方国家利用知识产权对我国经济活动进行干扰和控制');
INSERT INTO `duoxiang` VALUES ('371', '2000年，中央提出要实施“走出去”的开放战略，其主要意义在于(    )', 'ABC', '极大丰富了我国对外开放的内涵', '提高我国对外开放水平的一个新的战略部署', '标志我国对外开放从此进入“引进来”与“走出去”相结合的新阶段', '表明“走出去”成为我国对外开放的重点');
INSERT INTO `duoxiang` VALUES ('372', '“走出去”从全面提高对外开放水平的角度看，其直接指向主要在于(    )', 'ABCD', '发展对外投资，到国外去投资办厂', '扩大外贸出口，利用国际市场支持本国经济的继续发展', '转移生产能力，调整产业结构，缓解国内市场的过度竞争，培育有竞争力的新产业', '到境外去利用外资，进一步拓展利用外资的方式；利用境外资源，获取或直接利用当地资源');
INSERT INTO `duoxiang` VALUES ('373', '为了坚持在对外开放中解决我国的经济安全问题，应该正确处理的主要关系是(    )', 'ABC', '经济全球化与经济利益的民族性.差异性即国家经济利益的关系', '对外开放与自力更生的关系', '对外开放与利用资本主义.应对资本主义全球战略的关系', '对外开放与国家主权的关系');
INSERT INTO `duoxiang` VALUES ('374', '邓小平在对外开放思想中始终强调独立自主.自力更生是我们的立足点，他的独立自主.自力更生的思想的主要内容有(    )', 'ABCD', '坚决维护国家的主权和安全，中国永远不会接受别国干涉内政', '不信邪.不怕压.不怕制裁，捍卫本国的独立自主权利', '从中国实际出发，依靠中国人民自己的力量，走自己的路，建设中国特色的社会主义', '坚持独立自主的和平外交政策，不称霸，不结盟，不搞军事集团');
INSERT INTO `duoxiang` VALUES ('375', '邓小平强调对外开放的基本观点主要有(    )', 'ABCD', '对外开放是我国历史和现实经验教训的科学总结', '现在的世界是开放的世界，中国的发展离不开世界', '社会主义必须吸收利用资本主义所创造的一切先进文明成果以发展壮大自己', '和平发展的时代为我们提供了对外开放的条件；对外开放是党的基本路线的主要方面');
INSERT INTO `duoxiang` VALUES ('376', '邓小平强调独立自主.自力更生与对外开放不是对立的，而是相辅相成.相互促进的辩证统一。他指出(    )', 'ABCD', '独立自主.自力更生是对外开放的前提和基础，出发点和立足点', '对外开放是增强我国综合国力，更好地坚持独立自主.自力更生的重要条件；独立自主不是闭关自守，自力更生不是盲目排外，中国的发展离不开世界', '对外开放要为增强我国独立自主.自力更生的能力服务', '独立自主.自力更生的能力增强了，就可以更好地实行对外开放');
INSERT INTO `duoxiang` VALUES ('377', '实行改革开放是(  )', 'ABC', '社会主义中国的强国之路', '决定当代中国命运的历史性决策', '新时期中国最鲜明的特征', '社会主义优越性的表现');
INSERT INTO `duoxiang` VALUES ('378', '中共十六大以来，邓小平对外开放的思想与实践进一步发展，主要的思想创新和实践进展有(    )', 'ABCD', '统筹国内发展与对外开放，促进经济社会协调发展', '抓住重要战略机遇期，进一步扩大对外开放', '转变对外经济贸易增长方式，提高对外开放质量；“引进来”与“走出去”相结合，更好地利用两个市场.两种资源', '加强国际经贸合作，完善对外开放的国际环境；扩大对外开放，促进中国与世界各国共同发展');
INSERT INTO `duoxiang` VALUES ('379', '在对外开放的实践中，邓小平同样提出了一系列“两手抓”的政策措施，主要包括(    )', 'ABCD', '既要学习外国的先进技术，利用外资，又不能盲目无计划无选择地引进', '既抓开放搞活，又抓廉政建设，打击经济犯罪', '既要向西方国家学习先进技术和管理经验，引进资金，又要坚持走社会主义道路，警惕与防止和平演变', '既扩大对外文化交流，又要注意肃清资本主义腐朽文化的影响');
INSERT INTO `duoxiang` VALUES ('380', '根据马克思的世界历史思想，邓小平为对外开放提出的理论依据是(    )', 'AB', '现在的世界是开放的世界', '中国的发展离不开世界', '开放的世界也不能没有中国', '开放的世界需要一个开放的中国');
