INSERT INTO `duoxiang` VALUES ('429', '对于建设创新型国家而言，主要是指(    )', 'ABCD', '科技是关键', '人才是核心', '教育是基础', '科技人才是提高自主创新能力的关键所在');
INSERT INTO `duoxiang` VALUES ('430', '创新型国家主要是指(  )', 'ABC', '将科技创新作为国家基本战略', '大幅度提高科技创新能力', '形成强大的国家科技竞争优势', '把经济增长转移到依靠科技进步，提高劳动者素质的轨道上来');
INSERT INTO `duoxiang` VALUES ('431', '提高自主创新能力，建设创新型国家的主要措施(    )', 'ABCD', '加快建设国家创新体系，重点是建设国家为主导.企业为主体.市场为导向，各种科技创新主体有效互动，产学研相结合的技术创新体系', '深化科技管理体制改革，完善科技开发计划，促进科技创新要素与其他社会生产要素有机结合，形成科技与经济发展紧密结合，不断增加科技投入的良好机制', '继续积极引进国外先进技术.科技人才，充分利用国际科技资源，实现引进.消化.吸收再创新的良性循环', '实施知识产权战略，鼓励创新行为，提高创新回报；培养造就世界一流科学家和科技领导人才，造就各种创新人才');
INSERT INTO `duoxiang` VALUES ('432', '提高自主创新能力，建设创新型国家应该正确处理的重要关系有(    )', 'ABC', '科技发展与经济社会发展的关系，科技为经济建设服务，为现代化服务', '技术引进与自主创新的关系，引进为提高自主创新能力服务，自主创新为主，坚持引进.消化.吸收再创新', '基础研究与自主创新的关系，以基础研究支撑科技创新，增强自主创新的后劲', '国家创新与企业创新的关系，国家为主导，企业是最重要的创新主体');
INSERT INTO `duoxiang` VALUES ('433', '建设创新型国家的主要要求或根本途径是以增强自主创新能力作为(    )', 'ABCD', '发展科技的战略基点，走中国特色自主创新道路，推动科技跨越式发展', '调整经济结构，转变经济增长方式的中心环节，建设资源节约型.环境友好型社会，推动国民经济又好又快发展', '国家战略，贯穿到现代化建设各个方面，激发全民族创新精神，培养高水平创新人才，形成有利于自主创新的体制机制', '国家发展战略的核心，大力推进理论创新.制度创新.科技创新，不断巩固和发展社会主义伟大事业');
INSERT INTO `duoxiang` VALUES ('434', '国家创新体系是(    )', 'ABC', '政府为主导', '市场发挥配置资源的基础性作用', '各类创新主体紧密结合，有效互动', '企业为主体');
INSERT INTO `duoxiang` VALUES ('435', '我们国家创新体系中的技术创新体系应该是(    )', 'ABC', '企业为主体', '市场为导向', '产学研相结合', '科技为经济社会发展服务');
INSERT INTO `duoxiang` VALUES ('436', '按照国家创新体系的要求，我们要通过进一步深化科技体制改革，充分发挥(    )', 'ABC', '企业在技术创新中的主体地位', '国家科研机构的骨干和主导作用', '大学的基础和生力军作用', '群众的广泛参与作用');
INSERT INTO `duoxiang` VALUES ('437', '促进国民经济又好又快发展，推动增长方式彻底转变的主要措施是(    )', 'ABCD', '依靠科技进步与创新，提高经济增长的科技含量；形成有利于增长方式转变的体制与机制', '优化产业结构，走新型工业化道路，形成以高新技术产业为主的现代产业体系', '在节约资源.保护环境的基础上实现速度.结构.质量.效益相协调', '把扩大国内需求特别是扩大国家消费需求作为一个长期的根本战略方针，在经济发展与人口.资源.环境相协调的基础上做到消费.投资.出口相协调');
INSERT INTO `duoxiang` VALUES ('438', '经济增长方式彻底转变应该正确处理的主要关系有(    )', 'ABC', '投入和产出的关系', '速度和效益的关系', '节约资源.保护资源和开发利用资源与发展经济的关系', '人口与资源的关系');
INSERT INTO `duoxiang` VALUES ('439', '新型工业化主要内容是(  )', 'ABCD', '以信息化带动工业化，以工业化促进信息化', '科技含量高，经济效益好', '资源消耗低.环境污染少', '人力资源优势得到充分发挥');
INSERT INTO `duoxiang` VALUES ('440', '新型工业化的确立依据是(    )', 'ABC', '是全面总结国内外工业化经验教训作出的重大决策', '是顺应世界科技经济发展大趋势的必然选择', '是充分考虑我国基本国情得出的正确结论', '当前我国经济发展遇到资源.能源.环境的严重问题，迫切需要选择新型工业化道路');
INSERT INTO `duoxiang` VALUES ('441', '走新型工业化道路应该正确处理的主要关系有(    )', 'ABC', '工业化和信息化的关系', '依靠科技进步提高劳动生产率与扩大就业的关系', '发展高新技术产业与发展劳动密集型产业的关系', '虚拟经济与实体经济的关系');
INSERT INTO `duoxiang` VALUES ('442', '新型工业化的产业格局是(    )', 'ABC', '以高新技术产业为先导', '以基础产业制造业为支撑', '服务业全面发展', '以信息产业为基础');
INSERT INTO `duoxiang` VALUES ('443', '根据新型工业化的产业格局，推动产业结构优化升级的主要措施是(    )', 'ABC', '要优先发展信息产业，积极发展高新技术产业', '坚持用高新技术和先进适用技术改造提升传统产业，大力振兴装备制造业，赋予传统产业以新的内容', '加快发展服务业特别是现代服务业', '努力加强第一产业和基础产业，尤其是要加强基础设施建设');
INSERT INTO `duoxiang` VALUES ('444', '扩大国内需求特别是扩大国内消费需求要正确处理的主要关系有(    )', 'ABC', '投资与消费的关系，从投资拉动转到消费拉动的轨道上来', '国内需求与国外需求的关系，在立足扩大国内消费需求的前提下，扩大国外消费需求', '分配与消费的关系，把增加居民收入.扩大消费需求与改善人民生活水平相结合，实现良性发展', '生产与消费的关系，以消费制约和促进生产，生产与消费相协调');
INSERT INTO `duoxiang` VALUES ('445', '十七大报告强调“三农”事关全面建设小康社会的大局，完成全面建设小康社会伟大历史任务的难点.重点都在农村，是因为(  )', 'ABCD', '我国处于社会主义初级阶段，农村尤其不发达', '当前国民经济发展的突出矛盾是农民收人增长缓慢，这不仅是农村经济发展中的紧迫问题，也是贯彻落实扩大内需的方针，促进整个国民经济持续.快速.健康发展的关键问题', '我国农村与城市发展差距拉大，农村生产.生活条件相当落后，农村文化.科技.教育.卫生.体育等现代文明远远落后于城市，农业已经成为整个国民经济发展的薄弱环节', '农村还有5000万左右的贫困人13集中在中西部的老.少.边.穷地区，近8000万人刚刚越过温饱线，收入还很不稳定，增加农民收入，缩小城乡差距，在农村完成全面建设小康社会的任务十分艰巨');
INSERT INTO `duoxiang` VALUES ('446', '建设社会主义新农村的目标与要求是(    )', 'ABCD', '生产发展.生活宽裕', '乡风文明', '村容整洁', '管理民主');
INSERT INTO `duoxiang` VALUES ('447', '新农村建设的主要措施或主要途径是(    )', 'ABCD', '推进现代农业建设是新农村建设的首要任务', '全面深化以农村税费改革为重点的综合改革', '加快发展农村文化教育事业，促进农村精神文明建设与和谐社会建设，明显改善广大农村的生产生活条件和整体面貌', '千方百计增加农民收入，引导富余劳动力向非农产业和大中小城市转移；关键是培养新农民');
INSERT INTO `duoxiang` VALUES ('448', '走中国特色城镇化道路，积极稳妥地推进城镇化的主要原则(    )', 'ABCD', '循序渐进', '节约土地', '集约发展', '合理布局');
INSERT INTO `duoxiang` VALUES ('449', '按照科学发展观的要求，坚持大中小城市和城镇协调发展的方针，走中国特色城镇化道路的主要措施是(  )', 'ABCD', '分类引导人口城镇化，鼓励农村人口进入中小城市和小城镇定居，特大城市要从调整产业结构的源头人手，形成用经济方法等控制人口过快增长的机制', '形成合理的城镇化空间格局，以大城市为龙头，大中小城市协调发展', '加强城市规划建设管理，坚持城镇化发展与人121.资源.环境相协调', '健全城镇化发展的体制机制，加快破除城乡分割的体制障碍，深化户籍制度改革，逐步建立城乡统一的人口登记制度');
INSERT INTO `duoxiang` VALUES ('450', '区域经济协调发展的基本原则是(    )', 'ABCD', '统筹规划.因地制宜', '发挥优势', '分工合作', '协调发展');
INSERT INTO `duoxiang` VALUES ('451', '区域经济协调发展的预期新格局是(    )', 'ABCD', '东中西互动', '优势互补', '相互促进', '共同发展');
INSERT INTO `duoxiang` VALUES ('452', '区域经济协调发展的总体战略是(    )', 'ABCD', '推进西部大开发，振兴东北地区等老工业基地', '促进中部地区崛起', '鼓励东部地区率先发展', '支持革命老区.少数民族地区和边疆地区的发展');
INSERT INTO `duoxiang` VALUES ('453', '可持续发展的目标是(    )', 'ABC', '人类各种需求得到满足', '个人得到充分发展', '保护资源和生态环境，不对后代的生存发展构成威胁', '保障人类无限需求的不断满足');
INSERT INTO `duoxiang` VALUES ('454', '可持续发展的原则是(  )', 'ABC', '公平性原则', '持续性原则', '共同性原则', '共享性原则');
INSERT INTO `duoxiang` VALUES ('455', '可持续发展的核心思想是健康的经济发展应该建立在(    )的基础上', 'ABC', '生态可持续发展能力', '社会公正', '人民积极参与自身发展决策', '开发利用与节约.保持相协调');
INSERT INTO `duoxiang` VALUES ('456', '可持续发展在我国的具体实践是(  )', 'ABCD', '转变经济发展方式，走新型工业化道路', '坚持计划生育的基本国策，严格控制人口增长，要把经济社会发展同人口增长.资源利用.环境保护结合起来', '合理利用资源，建立资源节约型的国民经济体系，选择资源节约型.质量效益型.科技先导性的发展方式，全面推进生态环境保护和治理', '要把实施可持续发展战略同依法治国.加强法制建设结合起来');
INSERT INTO `duoxiang` VALUES ('457', '按照科学发展观的要求，我们应该选择的发展方式是(    )', 'ABC', '资源节约型', '质量效益型', '科技先导性', '环境友好型');
INSERT INTO `duoxiang` VALUES ('458', '十六大提出全面建设小康社会的目标体系中，在可持续方面的规定是(    )', 'ABCD', '可持续发展能力不断增强', '生态环境得到改善，资源利用效率显著提高', '促进人与自然和谐', '整个社会走上生产发展.生活富裕.生态良好的文明发展道路');
INSERT INTO `duoxiang` VALUES ('459', '建设资源节约型.环境友好型社会的主要措施是(    )', 'ABCD', '发展循环经济，坚持开发节约并重，节约优先，按照减量化.再利用.资源化的原则，在资源开采.生产消耗.废物产生.消费等环节，逐步建立全社会的资源循环利用体系', '保护修复自然生态；加大环境保护力度，坚持预防为主，综合治理，强化从源头上防治污染，坚决改变先污染后治理.边治理边污染的状况', '强化资源管理，实行有限开发.有序开发.有偿开发，加强对各种自然资源的保护和管理', '合理利用海洋和气候资源，加强空中水资源.太阳能.风能等的合理开发利用；积极应对全球气候变暖的问题，采取各种措施减少二氧化碳气体排放，努力形成清洁发展机制');
INSERT INTO `duoxiang` VALUES ('460', '在建设资源节约型.环境友好型社会的具体实践中应该正确处理的主要关系有(    )', 'ABD', '节约资源.保护环境与开发资源.发展经济的关系', '开发利用资源与提高资源能源利用率.降低消费的关系', '处理好人与自然的关系，达到人与自然的和谐与协调，实现生产发展.生活富裕.生态良好', '国内资源与国外资源的关系，立足国内资源，积极推进互利合作的国际资源开发利用');
INSERT INTO `duoxiang` VALUES ('461', '邓小平“科学技术是第一生产力”思想的主要内容有(    )', 'ABCD', '科学技术对经济发展起第一位变革作用', '科学技术在生产力诸要素中成为最主要的推动力量', '现代科学使管理日趋现代化.科学化', '高科技在知识经济中的作用更加突出');
INSERT INTO `duoxiang` VALUES ('462', '资源节约型社会的根本特征是(    )', 'AB', '以能源资源高效率利用方式进行生产', '以节约的方式进行消费', '以最低的消费进行生活', '以最高的效率进行生产');
INSERT INTO `duoxiang` VALUES ('463', '资源节约型社会的具体要求是(    )', 'ABC', '在生产.流通.消费的各个领域，在经济社会发展的各个方面，以节约使用能源资源和提高能源资源利用效率为核心', '以节能.节水.节材.节地.资源综合利用为重点', '以尽可能小的资源消耗，获得尽可能大的经济和社会效益，从而保障经济社会的可持续发展', '以人力资源开发为关键，通过人才开发实现经济发展');
INSERT INTO `duoxiang` VALUES ('464', '环境友好型社会是人与自然和谐发展的社会，通过人与自然的和谐来促进人与人.人与社会的和谐。具体是指(    )', 'ABCD', '以人与自然和谐相处为目标，以环境承载能力为基础', '以遵循自然规律为核心，以绿色科技为动力', '保护优先，开发有序，合理进行功能区划分，提倡环境文化和生态文明', '追求经济.社会.环境协调发展的社会体系');
INSERT INTO `duoxiang` VALUES ('465', '建设资源节约型.环境友好型社会，要求转变关于发展的传统观念，主要是指(    )', 'ABC', '从重经济增长轻环境保护转变为保护环境与经济增长并重，在保护环境中求发展', '从环境保护滞后于经济发展转变为环境保护和经济发展同步，努力做到不欠新账，多还旧账，改变先污染后治理.边治理边破坏的状况', '从主要用行政办法保护环境转变为综合运用法律.经济.技术和必要的行政办法解决环境问题，自觉遵循经济规律和自然规律，提高环境保护工作水平', '对自然环境的征服与主宰，转向人与自然的和谐与协调，注意人与自然的相互依存.相互制约');
INSERT INTO `duoxiang` VALUES ('466', '十七大报告第一次明确提出建设生态文明的目标。生态文明建设必须着力做好的主要工作是(    )', 'ABCD', '坚持节约资源和保护环境的基本国策，完善有利于节约能源资源和保护生态环境的法律和政策，加快形成可持续发展体制机制', '发展清洁能源和可再生能源，建设科学合理的能源资源利用体系，提高能源资源利用效率', '加大节能环保投入，重点加强水.大气.土壤等污染防治，改善城乡人居环境', '加强水利.林业.草原建设，促进生态修复');
INSERT INTO `duoxiang` VALUES ('467', '发展循环经济具体要求是(    )', 'ABCD', '以减量化.再利用和资源化为原则，以提高资源利用率为核心', '以资源节约.资源综合利用.清洁生产为重点', '通过调整结构.技术进步和加强管理等措施来实现', '大幅度减少资源消耗.降低废物排放.提高劳动生产率');
INSERT INTO `duoxiang` VALUES ('468', '中共十七大指出，当前和今后一个时期，转变经济发展方式主要做到“两个坚持”.“三个转变”，具体是指(    )', 'ABCD', '坚持走中国特色新型工业化道路，坚持扩大国内需求特别是消费需求的方针', '促进经济增长由主要依靠投资.出口拉动向依靠消费.投资.出口协调拉动转变', '由主要依靠第二产业带动向依靠第一.第二.第三产业协同带动转变', '由主要依靠增加物质资源消耗向主要依靠科技进步.劳动者素质提高.管理创新转变');
INSERT INTO `duoxiang` VALUES ('469', '根据新型工业化的主要内容，我国选择的新型工业化道路是(    )', 'ABCD', '以信息化带动工业化', '以科技为动力的工业化，以提高经济效益和市场竞争力为中心的工业化', '与可持续发展相结合的工业化', '充分发挥我国人力资源优势的工业化');
INSERT INTO `duoxiang` VALUES ('470', '新型工业化道路之所以强调一个“新”字，原因在于(    )', 'ABCD', '它同信息化等现代高科技发展紧密结合', '注重经济发展同资源环境相协调', '坚持城乡协调发展', '实现资金技术密集型产业同劳动密集型产业相结合');
INSERT INTO `duoxiang` VALUES ('471', '转变经济发展方式的基本思路是实现“三个转变”，具体是指(    )', 'ABC', '促进经济增长由主要依靠投资.出口拉动向依靠消费.投资.出口协调拉动转变', '由主要依靠第二产业带动向依靠第一.第二.第三产业协同带动转变', '由主要依靠增加物质资源消耗向主要依靠科技进步.劳动者素质提高.管理创新转变', '由主要依靠低工资带动向高工资.高福利.高科技劳动带动转变');
INSERT INTO `duoxiang` VALUES ('472', '新农村建设的目标与要求是生产发展.生活宽裕.乡风文明.村容整洁.管理民主。其中(    )', 'ABCD', '生产发展是新农村建设的物质基础和中心环节，发展农业生产的首要任务是发展现代农业', '生活宽裕是目的，新农村建设的根本目的是通过增加农民收入不断改善和提高农民生活水平，这也是农村工作的基本尺度或衡量标准', '村容整洁是展现农村新貌的窗口，实现人与自然环境和谐发展的必然要求；乡风文明，是农民素质的反映，体现农村精神文明建设的要求，农村精神文明建设的根本任务是培养新农民，新农民是新农村建设的关键', '管理民主是新农村建设的政治保证，是对农民群众基层民主政治权利的尊重和维护');
INSERT INTO `duoxiang` VALUES ('473', '十七大强调，统筹城乡发展，推进新农村建设要把发展现代农业.繁荣农村经济作为首要任务，走中国特色农业现代化道路。中国特色农业现代化道路的基本内涵.总体思路可以概括为(    )', 'ABCD', '用现代物质条件装备农业，用现代科学技术改造农业，用现代产业体系提升农业', '用现代经营方式推进农业，用现代发展理念引领农业，用培育新型农民发展农业', '提高农业水利化.机械化和信息化水平，提高土地产出率.资源利用率和农业劳动生产率', '提高农业素质.效益和竞争力');
INSERT INTO `duoxiang` VALUES ('474', '党中央对统筹城乡发展提出的新方针.新要求，打破城乡二元结构，加快农业.农村发展，促进农民富裕的根本途径是(    )', 'AB', '建立以工促农.以城带乡长效机制', '形成城乡经济社会发展一体化新格局', '工业反哺农业，城市反哺乡村', '国家和政府大力增加对农业.农村的投资力度，彻底改变农村生活条件.生产条件');
INSERT INTO `duoxiang` VALUES ('475', '贯彻党中央提出的“以工促农.以城带乡”新方针.新要求，具体应该做到(    )', 'ABCD', '推进城乡发展规划一体化，着力推进城乡基础设施建设一体化', '着力推进城乡公共服务一体化', '着力推进城乡劳动力就业一体化', '着力推进城乡社会管理一体化');
INSERT INTO `duoxiang` VALUES ('476', '再分配更加注重公平，主要是指政府运用宏观调控手段调节过大的收入差距。这些政策手段主要是(    )', 'ABCD', '个人所得税', '社会保障', '财政转移支付', '扩大就业，提高工资');
INSERT INTO `duoxiang` VALUES ('477', '调整收入分配差距过分扩大的主要政策是以共同富裕为目标，(    )', 'ABCD', '扩大中等收入者比重', '提高低收入者收入水平', '调节过高收入', '取缔非法收入');
INSERT INTO `duoxiang` VALUES ('478', '根据党的十六届五中全会精神，我国加快完善社会主义市场经济体制，形成有利于转变经济增长方式.促进全面协调可持续发展的机制的重点在(    )', 'AD', '转变政府职能', '进一步扩大对外开放，借鉴国外的先进经验', '倡导市场经济观念，促使人们按经济规律办事', '深化企业.财税.金融等改革');
INSERT INTO `duoxiang` VALUES ('479', '当前中国经济正在发生的三个根本性的转变是(    )', 'ABC', '经济发展方式的根本性转变', '经济体制的根本性转变', '从城乡二元经济到现代经济结构的根本性转变', '单一公有制到混合所有制的根本性转变');
INSERT INTO `duoxiang` VALUES ('480', '我国经济结构调整和优化的基本要求是(    )', 'ABCD', '巩固和加强农业基础地位，加快工业改组改造和结构优化升级', '大力发展服务业，特别要把科技.教育放在突出位置', '加快国民经济和社会信息化发展', '继续加强基础设施建设');
INSERT INTO `duoxiang` VALUES ('481', '我国新型工业化的产业格局和产业结构调整的基本方向是(    )', 'ABD', '以高新技术产业为导向', '以基础产业和制造业为支撑', '以农业为基础', '服务业全面发展');
INSERT INTO `duoxiang` VALUES ('482', '十七大提出，就业是民生之本。国家采取的相应政策有(    )', 'ABCD', '完善支持自主创业.自谋职业政策，加强就业观念教育，使更多劳动者成为创业者', '健全面向全体劳动者的职业教育培训制度，加强农村富余劳动力转移就业培训', '建立统一规范的人力资源市场，形成城乡劳动者平等就业的制度', '完善面向所有困难群众的就业援助制度，及时帮助零就业家庭解决就业困难');
INSERT INTO `duoxiang` VALUES ('483', '十六届三中全会提出的就业方针是(    )', 'ABC', '政府指导就业', '市场调节就业', '个人自主择业', '社会保障就业');
INSERT INTO `duoxiang` VALUES ('484', '邓小平关于市场经济的主要思想有(   )', 'ABCD', '计划和市场都是资源配置的方法，只有对发展生产力有利，就可以利用', '计划多一点还是市场多一点，不是社会主义和资本主义的本质区别', '计划经济不等于社会主义，资本主义也有计划', '市场经济不等于资本主义，社会主义也有市场');
INSERT INTO `duoxiang` VALUES ('485', '市场经济的特点是(   )', 'ACD', '微观性', '宏观性', '事后性', '短期行为');
INSERT INTO `duoxiang` VALUES ('486', '市场经济与计划经济相比较具有的优势是(   )', 'AB', '市场经济对促进经济发展具有更强的适应性', '运行的效率较高', '能自动调节社会总供给与总需求', '具有宏观性');
INSERT INTO `duoxiang` VALUES ('487', '邓小平指出：“社会主义市场经济优越性在哪里？就在四个坚持。”这”四个坚持“是(   )', 'ABCD', '以公有制为主体', '按劳分配为主体', '以共同富裕为目标', '坚持社会主义基本经济制度与市场经济的结合');
INSERT INTO `duoxiang` VALUES ('488', '坚持和完善社会主义基本经济制度的原则是(   )', 'AB', '做到两个“毫不动摇”', '一个“统一”', '“有所为，有所不为”', '“由东到西梯次推进”');
INSERT INTO `duoxiang` VALUES ('489', '在社会主义初级阶段的所有制结构中，公有制经济包括(   )', 'ABC', '国有经济', '集体经济', '混合所有制经济中的国有成分和集体成分', '私营经济');
INSERT INTO `duoxiang` VALUES ('490', '非公有制经济包括(    )', 'ABCD', '个体经济', '私营经济', '外资经济', '混合所有制经济中非公有成分');
INSERT INTO `duoxiang` VALUES ('491', ' 中共十八届三中全会通过的《中共中央关于全面深化改革若干重大问题的决定》指出，除了要紧紧围绕使市场在资源配置中起决定性作用深化经济体制改革，紧紧围绕坚持党的领导.人民当家作主.依法治国有机统一深化政治体制改革以外，还要紧紧围绕 (  ) ', 'ABCD', '建设社会主义核心价值体系.社会主义文化强国深化文化体制改革', '更好保障和改善民生.促进社会公平正义深化社会体制改革', '建设美丽中国深化生态文明体制改革', '提高科学执政.民主执政.依法执政水平深化党的建设制度改革');
INSERT INTO `duoxiang` VALUES ('492', ' 政治体制改革的目标是 (  ) ', 'AB', '增强党和国家的活力', '调动人民积极性', '维持团结稳定', '保证共产党长期执政');
INSERT INTO `duoxiang` VALUES ('493', ' 十八大提出，完善基层民主的重点是 (  ) ', 'ABCD', '扩大有序参与', '推进信息公开', '加强议事协商', '强化权力监督');
INSERT INTO `duoxiang` VALUES ('494', ' 十八大提出，依法治国理政的基本方式，主要包括 (  ) ', 'ABCD', '科学立法', '严格执法', '公正司法', '全民守法');
INSERT INTO `duoxiang` VALUES ('495', ' 在中国特色行政体制改革中，服务型政府的基本要求是 (  ) ', 'ABCD', '职能科学', '结构优化', '廉洁高效', '人民满意');
INSERT INTO `duoxiang` VALUES ('496', ' 十大要求建立健全权力运行制约和监督体系，强调指出，权力正确运行的重要保证是保障人民的 (  ) ', 'ABCD', '知情权', '参与权', '表达权', '监督权');
INSERT INTO `duoxiang` VALUES ('497', ' 社会主义民族关系的基本要求是促进各民族 (  ) ', 'ABC', '和睦相处', '和衷共济', '和谐发展', '和平共处');
INSERT INTO `duoxiang` VALUES ('498', '关于中国人民政治协商会议的论述错误的是是(    )', 'CD', '中国人民政治协商会议是具有广泛代表性爱国统一战线的组织形式', '中国人民政治协商会议是实现中国共产党领导下的多党合作和政治协商制度的重要组织形式', '中国人民政治协商会议不同于一般的人民团体，属于重要的国家机关', '中国人民政治协商会议的主要功能是民主监督.参政议政');
INSERT INTO `duoxiang` VALUES ('499', '人民民主专政所具有的中国特色是(    ) ', 'ABC', '参加国家政权的不仅有工人阶级.农民阶级和城市小资产阶级，而且在一定历史时期还有民族资产阶级', '实行共产党领导的多党合作', '在概念的表述上也有它突出的优点', '是以工人阶级为领导以工农联盟为基础的国家政权');
INSERT INTO `duoxiang` VALUES ('500', '我国人民民主专政与人民代表大会制度的关系是(    ) ', 'ABD', '前者是国体，后者是政体', '前者是后者的依据和前提，后者是前者的性质和要求', '前者是国家权利机关，后者是国家管理机关', '前者是国家性质，后者是政治制度');
INSERT INTO `duoxiang` VALUES ('501', '人民政协以政治协商和民主监督作为自己的主要职能。民主监督主要是指(    ) ', 'ACD', '对国家的宪法和法律法规的实施情况进行监督', '对人民代表大会进行工作监督', '对重大方针政策的贯彻执行情况进行监督', '对国家机关及其工作人员履行职责的情况进行监督');
INSERT INTO `duoxiang` VALUES ('502', '人民政治协商会议制度体现了我国(    ) ', 'ABD', '社会主义民主的广泛', '各民族的平等和团结', '国家结构与政权组织的特点和优点', '领导经济建设的功能');
INSERT INTO `duoxiang` VALUES ('503', '我国的多党合作是一种社会主义的新型政党关系，其主要特点是(    ) ', 'ABCD', '中国共产党是社会主义事业的领导核心，是执政党', '中国共产党同各民主党派合作的政治基础，是坚持中国共产党的领导，坚持四项基本原则', '各民主党派都参加国家政权，参与国家事务的管理', '中国共产党和各民主党派都必须以宪法为根本活动原则');
INSERT INTO `duoxiang` VALUES ('504', '党的十八大报告提出健全社会主义协商民主制度，把中国特色选举民主同社会主义协商民主相结合，这是我国人民民主的重要形式，有利于(  )', 'ABC', '广泛协商，广纳群言.广集民智，增进共识.增强合力', '健全民主制度.丰富民主形式', '发展社会主义政治文明，为实现最广泛的人民民主确立正确方向', '扩大公民的民主权利，保障人民当家作主');
INSERT INTO `duoxiang` VALUES ('505', '坚持走中国特色社会主义政治发展道路就是(    )', 'ABCD', '坚持中国特色社会主义发展道路', '坚持党的领导.人民当家作主.依法治国的有机统一', '坚持和完善人大制度.党领导的多党合作和政治协商制度.民族区域自治制度和基层群众自治制度', '不断推进社会主义政治制度的自我完善与发展');
INSERT INTO `duoxiang` VALUES ('506', '社会主义政治文明建设的基本方针里，具体包括(    )  ', 'ABC', '中国共产党的领导是人民当家作主和依法治国的根本保证', '人民当家作主是社会主义民主政治的本质和核心要求，是社会主义政治文明建设的根本出发点和归宿', '依法治国是党领导人民治理国家的基本方略', '基层民主是社会主义民主政治的基础性工程');
INSERT INTO `duoxiang` VALUES ('507', '人民当家作主是(    )', 'ABCD', '社会主义民主政治本质特征和核心要求', '社会主义民主政治建设出发点和落脚点', '是共产党领导和执政的根本目的', '依法治国的主题和中心内容');
INSERT INTO `duoxiang` VALUES ('508', '依法治国的实质是(    )', 'ABCD', '人民在党的领导下实行', '依照宪法和法律治理国家', '管理社会事物和经济文化事物', '保障自己当家作主的各项民主权利');
INSERT INTO `duoxiang` VALUES ('509', '依法治国的过程，实际上是在党的领导下(    )', 'AB', '维护人民主人翁地位的过程', '保证人民实现当家作主的过程', '维护法律权威的过程', '体现党的领导的过程');
INSERT INTO `duoxiang` VALUES ('510', '我国现阶段坚持人民民主专政的实质，就是要(    )', 'ABC', '不断发展社会主义民主', '切实保护人民的利益', '维护国家的主权.安全.统一与稳定', '维护世界和平');
INSERT INTO `duoxiang` VALUES ('511', '坚持人民民主专政首先要(    )', 'ABCD', '坚持国家的一切权力属于人民', '保证人民依照宪法和法律规定享受应有的权利', '通过各种形式和途径，管理国家事务，管理经济和文化事业，管理社会事务', '保证人民当家作主');
INSERT INTO `duoxiang` VALUES ('512', '人民代表大会制度的确立依据是(    ) ', 'ABCD', '在我国，人民内部虽然还存在各种复杂的矛盾，但全国人民根本利益的一致性，决定了人民可以统一行使自己的国家权力', '中国在实现现代化的进程中，既要解决各种复杂的矛盾和问题，又要追赶发达国家，更需要集中和效率', '在国家机构体系中，人民代表大会作为国家最高权力机关统一行使国家权力，实行民主集中制，集体行使职权，集体解决问题', '保证了国家统一有效地组织各项事业，保证一切权力属于人民');
INSERT INTO `duoxiang` VALUES ('513', '改革开放以来，人民代表大会制度的新发展主要有(    ) ', 'ABCD', '把直接选举人民代表大会代表的范围推广到县，实行普遍的差额选举制度', '完善了全国人民代表大会常务委员会的职权', '在县级以上地方各级人民代表大会设立了常务委员会并赋予其相应职权', '立法工作取得显著进展，以宪法为核心的中国特色社会主义法律体系初步形成');
INSERT INTO `duoxiang` VALUES ('514', '人民代表大会制度建立以来半个多世纪的实践证明它具有强大的生命力和巨大的优越性，主要是指(    ) ', 'ABCD', null, null, null, null);
INSERT INTO `duoxiang` VALUES ('515', '除中国共产党外，中国大陆目前还存在的民主党派有(    ) ', 'ABCD', '中国国民党革命委员会.中国民主同盟', '中国民主建国会.中国民主促进会', '中国农工民主党.中国致公党', '九三学社和台湾民主自治同盟');
INSERT INTO `duoxiang` VALUES ('516', '中国共产党与各民主党派合作的基本方针是(    ) ', 'ABCD', '长期共存', '互相监督', '肝胆相照', '荣辱与共');
INSERT INTO `duoxiang` VALUES ('517', '人民政协的主要职能是(    )', 'ABC', '政治协商', '民主监督', '参政议政', '政策咨询');
INSERT INTO `duoxiang` VALUES ('518', '中国人民政治协商会议的性质是指(    ) ', 'ABCD', '中国人民爱国统一战线的组织', '中国共产党领导的多党合作和政治协商的重要机构和重要体现', '中国政治生活中发扬民主的重要形式', '党和国家实行科学民主决策的重要环节，是党提高执政能力的重要途径');
INSERT INTO `duoxiang` VALUES ('519', '政治协商主要是指(  ) ', 'ABCD', '中国共产党同各民主党派.人民团体及各方面的代表人士，在共同遵守宪法和基本路线的基础上', '就有关国家事务和地方事务的重大问题', '进行各种形式的充分讨论，集中各方面提出的正确意见', '采取协商一致的原则，解决问题');
INSERT INTO `duoxiang` VALUES ('520', '人民政协的民主监督是我国社会主义监督体系的重要组成部分，内容是指(    )', 'ABC', '是在坚持四项基本原则的基础上通过提出意见.批评.建议的方式进行的政治监督', '是参加人民政协的各党派团体和各族各界人士通过政协组织对国家机关及其工作人员进行的监督', '是中国共产党在政协中与各民主党派和无党派人士之间进行的相互监督', '是人民群众对共产党和各民主党派.无党派人士的监督');
INSERT INTO `duoxiang` VALUES ('521', '人民政协的参政议政是对政治.经济.文化和社会生活中的重要问题以及人民群众普遍关心的问题(    ) ', 'ABCD', '开展调查研究', '反映社情民意', '进行协商讨论', '通过调研报告.提案.建议案或其他形式向党和国家机关提出意见和建议');
INSERT INTO `duoxiang` VALUES ('522', '关于中国人民政治协商会议的论述错误的是(    )', 'CD', '中国人民政治协商会议是具有广泛代表性爱国统一战线的组织形式', '中国人民政治协商会议是实现中国共产党领导下的多党合作和政治协商制度的重要组织形式', '中国人民政治协商会议不同于一般的人民团体，属于重要的国家机关', '中国人民政治协商会议的主要功能是民主监督.参政议政');
INSERT INTO `duoxiang` VALUES ('523', '人民政协的两大主题是(    )', 'CD', '科学发展', '和谐社会', '团结', '民主');
INSERT INTO `duoxiang` VALUES ('524', '党领导的多党合作制最突出的特征是(    )', 'AB', '共产党领导.多党派合作', '共产党执政.多党派参政', '共产党决策.多党派咨询', '共产党决策.多党派协商');
INSERT INTO `duoxiang` VALUES ('525', '我国党领导的多党合作的政党制度是一种社会主义新型政党制度，与资本主义国家的多党制.两党制有根本不同，具体在于(    ) ', 'ABCD', '经济基础不同，资本主义政党制度建立在资本主义私有制的基础上，实质是资产阶级不同利益集团之间的竞争在政治上的反映；我国政党制度建立在公有制为主体的社会主义经济基础之上，是人民群众根本利益.整体利益.长远利益在政治上的反映', '共产党是执政党，是领导核心，民主党派是参政党，是接受共产党领导的社会主义友党，不是在野党，更不是反对党，不谋求轮流执政', '多党合作的政治基础是坚持四项基本原则，具有共同的根本利益.目标和任务；各民主党派都参政议政', '共产党和各民主党派在宪法.法律基础上，政治自由.组织独立.法律地位平等');
INSERT INTO `duoxiang` VALUES ('526', '在我国政党制度下，各民主党派是参政党，参政议政的主要内容有(    ) ', 'ABCD', '参加国家政权', '参与国家事务的管理', '参与国家大政方针和国家领导人选的协商', '参与国家方针.政策.法律.法规的制定执行');
INSERT INTO `duoxiang` VALUES ('527', '我国民族区域自治制度的确立依据和优越性有(    ) ', 'ABCD', '统一的多民族国家的长期存在与发展，是我国实行民族区域自治的历史依据；中国在历史上虽有战有和，有统有分，但统一始终是各民族关系的主流', '近代以来在反抗外来侵略斗争中形成的爱国主义精神与共同革命经历，是实行民族区域自治的政治基础', '各民族大杂居.小聚居的人口分布格局，各地区资源条件和发展的差异，是实行民族区域自治的现实条件；大杂居.小聚居的分布格局，你中有我.我中有你.相互依存的人口分布状况，决定了在一些少数民族聚居的地方，建立不同类型和不同行政级别的民族自治地方，有利于民族关系的和谐稳定和各民族的共同发展', '新中国成立后国家的团结.统一.富强与繁荣发展以及国际地位的提高，民族凝聚力的增强，中华民族的伟大振兴，是民族区域自治制度的深刻现实依据');
INSERT INTO `duoxiang` VALUES ('528', '根据我国宪法.法律的规定，我国各民族自治地方的自治机关享有广泛的自治权利，主要有(     ) ', 'ABCD', '自主管理本民族.本地区的内部事务', '享有制定自治条例和单行条例的权利', '享有宗教信仰自由的权利', '享有使用和发展本民族语言文字.按照传统风俗习惯生活及进行社会活动的权利和自由；此外，还拥有自主安排.管理.发展经济建设事业，自主发展教育.科技.文化等其他各项权利');
INSERT INTO `duoxiang` VALUES ('529', '党之所以把基层群众自治制度纳入中国特色社会主义民主政治制度的基本范畴，是因为(    )', 'AB', '发展基层民主是发展社会主义民主的基础性工作。人民是国家的主人，首先必须保证他们在基层的经济.政治.文化和其他社会事务中当好家作好主', '基层民主是社会主义民主最广泛的实践，发展基层直接民主，有利于提高全民的民主素养，为发展社会主义民主进一步创造条件', '基层民主是我国社会主义民主政治的本质特征', '基层民主是我国社会主义优越性的集中体现');
INSERT INTO `duoxiang` VALUES ('530', '依法治国就是(  )', 'ABC', '人民群众在党的领导下，依据宪法和法律管理国家与经济社会文化事务', '保证国家各项工作依法进行，逐步实现社会主义民主制度化.法律化', '使社会主义民主制度与法律不因领导人而改变', '使社会主义民主制度与法律享有最高权威');
INSERT INTO `duoxiang` VALUES ('531', '深刻理解依法治国的科学内涵，应该正确把握的是(    ) ', 'ABCD', '依法治国的主体是党领导下的人民群众，也就是党顿导人民实行依法治国', '依法治国的客体是国家事务.经济文化事业和社会事务，依法治国就是要保证对所有这些事业.事务的管理工作都要依法进行', '依法治国所依据的法，最重要的是宪法和法律', '我国宪法和法律体现了党的主张和人民利益.人民意志的统一');
INSERT INTO `duoxiang` VALUES ('532', '依法治国确定为党执政治理基本方略的重要性在于(    ) ', 'ABCD', '是社会文明进步的显著标志', '是国家长治久安的重要保障', '是社会主义民主政治的基本要求', '是建设中国特色社会主义的经济.政治.文化.构建和谐社会的必然要求');
INSERT INTO `duoxiang` VALUES ('533', '依法治国作为党执政治国基本方略的确立依据是(    ) ', 'ABCD', '依法治国是中国共产党执政方式的重大转变，有利于加强和改善党的领导，保证党始终发挥总揽全局.协调各方的核心作用', '依法治国是发展社会主义民主.实现人民当家作主的根本保证。民主与法治相互依赖，相互促进，密不可分社会主义民主是社会主义法治的基础，社会主义法治是社会主义民主的保障', '依法治国是发展社会主义市场经济和扩大对外开放的客观需要。一个比较成熟的市场经济，必然要求并具有比较完备的法制', '依法治国是国家长治久安的重要保障。要保持社会稳定，就必须依靠法治来协调社会关系，化解社会矛盾。依法打击各种犯罪活动，加强社会治安综合治理，创造良好的社会治安环境');
INSERT INTO `duoxiang` VALUES ('534', '社会主义民主与法治相互依赖.密不可分，相互关系有(    )', 'AB', '社会主义民主是法治的基础，人民只有掌握国家政权，实行人民民主，才能把自己的意志上升为国家法律，建立起法律制度', '社会主义法治是人民民主的保障，人民民主权利.公民基本权利，国家社会各方面的民主制度.民主结构.民主形式.民主程序.都需要法律加以确认.规范，需要通过国家强制力保证实施', '没有社会主义民主，法律可能成为恶法', '没有社会主义民主，人民民主权利就会被践踏.被破坏');
INSERT INTO `duoxiang` VALUES ('535', '社会主义法制建设的基本要求是有法可依.有法必依.执法必严.违法必究，其中(    ) ', 'ABCD', '有法可依是前提', '有法必依是核心', '执法必严是关键', '违法必究是保障');
INSERT INTO `duoxiang` VALUES ('536', '社会主义法制基本要求之一是有法可依，有法可依就是要建立统一.完备.科学的法律体系和制度，即(    ) ', 'ABCD', '立法能够适应不断发展的经济.政治.文化和社会生活的需要，及时对各种社会关系进行规范', '法律体系应当力求完整.科学.严谨.系统；各部门法应当合理划分，彼此协调，共同发挥作用；法律规范应；明确.肯定.具体，具有可操作性', '法律的内容应当体现最广大人民的利益，不能把部门利益法律化', '应当避免和消除各国家机关，不同时期的法律.法规之间的矛盾以及法律.法规与法律解释之间的矛盾');
INSERT INTO `duoxiang` VALUES ('537', '社会主义法制强调“有法必依”，就是指(    ) ', 'ABCD', '保证法律效力的普遍性和有效性，即尽量排除和杜绝立法.执法.司法.守法和法律监督中的随意性.偶然性和腐败现象；坚持法律面前，人人平等，任何组织和个人都不能有超越宪法和法律的特权', '公民不分民族.种族.性别.职业.家庭出身.宗教信仰.教育程度.财产状况.居住期限，都必须平等地遵守宪法.法律和其他法规，依法平等地享有法定的权利和承担法定的义务', '任何公民的合法权益必须毫无例外地受到法律的平等保护', '要建立和健全法律服务机构，保证法律正确实施');
INSERT INTO `duoxiang` VALUES ('538', '社会主义法制强调“执法必严”，就是要确保严格公正的执法和司法。就是指(    ) ', 'ABCD', '司法机关应当独立公正地行使司法权，不受其他机关.社会团体和个人的非法干预', '要按照公正司法.文明执法的要求，完善司法机关的机构设置.职能划分和管理制度，形成权责明确.相互配合.相互制约.高效运行的司法体制和工作机制', '维护司法权威，维护公民.法人和其他组织的合法权益，维护社会公平和正义', '要加强执法.司法队伍的建设和对他们工作的监督，防止他们滥用权力');
INSERT INTO `duoxiang` VALUES ('539', '社会主义法制强调“违法必究”，就是(    ) ', 'AB', '对一切违法犯罪行为都要按照“以事实为依据，以法律为准绳”的原则，给予惩处', '在法律面前，人人平等，不容许任何组织和个人有超越宪法和法律的特权', '禁止以经济赔偿代替法律制裁', '不容许在司法量刑方面搞轻罚轻判');
INSERT INTO `duoxiang` VALUES ('540', '当前及今后的一段时间，加强法制建设的主要任务是(    ) ', 'ABCD', '要坚持科学立法.民主立法，完善中国特色社会主义法律体系', '加强宪法和法律实施，坚持公民在法律面前一律平等，维护社会公平正义，维护社会主义法制的统一.尊严.权威；推进依法行政', '深化司法体制改革，优化司法职权配置，规范司法行为，建设公正高效权威的社会主义司法制度，保证审判机关.检察机关依法独立公正地行使审判权.检察权', '加强政法队伍建设，做到严格.公正.文明执法；深入开展法制宣传教育，弘扬法治精神，形成自觉学法守法用法的社会氛围');
INSERT INTO `duoxiang` VALUES ('541', '对于政治体制改革，邓小平强调三条，它们是指(    )', 'ABC', '看国家的政局是否稳定', '看能否增进人民的团结，改善人民的生活', '看生产力能否得到持续发展', '看是否推进社会主义政治制度自我完善和发展');
INSERT INTO `duoxiang` VALUES ('542', '深化政治体制改革的主要任务是(    ) ', 'ABCD', '健全民主制度，丰富民主形式，拓宽民主渠道', '完善法律制度，维护社会主义法制的统一和尊严，树立法制权威；完善司法体制与机制', '加快行政管理体制改革；健全政府职责体系', '完善制约和监督机制，让权力在阳光下运行；深入开展党风廉政建设和反腐败斗争，推进教育.制度.监督并重的惩治和预防腐败体系建设');
INSERT INTO `duoxiang` VALUES ('543', '健全民主制度，丰富民主形式，拓宽民主渠道主要是指(    ) ', 'ABCD', '依法实行民主选举.民主决策.民主管理.民主监督', '保障人民的知情权.参与权.表达权.监督权', '实现社会主义民主政治制度化.规范化.程序化', '巩固人民当家作主的政治地位');
INSERT INTO `duoxiang` VALUES ('544', '政治体制改革的主要任务之一，是完善法律制度，维护社会主义法制的统一和尊严，树立法制权威，具体是指(    ) ', 'ABCD', '完善司法体制机制，坚持司法为民.公正司法', '推进司法体制和工作机制改革', '建设公平.高效.权威的社会主义司法制度', '发挥司法维护公平正义的职能作用');
INSERT INTO `duoxiang` VALUES ('545', '党和政府反腐倡廉的战略方针是(    ) ', 'ABCD', '党要管党.从严治党', '标本兼治.综合治理', '注重预防', '惩防并举');
INSERT INTO `duoxiang` VALUES ('546', '资本主义民主主要特点有(    ) ', 'ABCD', '建立在资本私有制基础上，以金钱政治为基础', '政权性质上是统治阶级内部的民主，少数剥削者的民主，只是在政治程序上强调少数服从多数，实质上是少数剥削者的阶级统治', '只标榜民主，不承认专政，极力掩盖阶级统治的实质', '理论上强调民主的形式方面，不讲它的实质，企图用比较完备的民主形式掩盖阶级统治的本质，用普世价值');
INSERT INTO `duoxiang` VALUES ('547', '人民民主是社会主义的生命，社会主义民主的主要特点有(    )', 'ABC', '社会主义民主是多数人的民主，本质是人民当家作主，与资本主义民主最大区别在于人民当家作主', '社会主义民主是真实的民主，公开承认民主的阶级性，强调统治阶级的民主同时就是对于被统治阶级的专政，民主与专政不可分割', '公有制为主体是社会主义民主的经济基础，消除了金钱政治的弊端，真实的民主，是民主本质与形式的内在统一', '是迄今为止人类历史上最高形态的民主，最完善的民主');
INSERT INTO `duoxiang` VALUES ('548', '民主与社会主义的内在联系在于：民主是(    ) ', 'ABCD', '建设社会主义的政治前提', '体现社会主义的本质要求', '反映社会主义的根本目标之一', '显示社会主义社会的重要特征和优越性');
INSERT INTO `duoxiang` VALUES ('549', '民主是(    ) ', 'ABCD', '国家制度', '阶级的', '历史的', '具体的');
INSERT INTO `duoxiang` VALUES ('550', '社会主义民主是新型民主和最高类型的民主，是由于(    ) ', 'ABCD', '民主权利主体的空前广泛性', '民主行使范围的空前普遍性', '民主权利的空前真实性，即公有制从根本上保证了人民当家作主的权利', '民主与集中的内在统一性');
INSERT INTO `duoxiang` VALUES ('551', '我国人民民主专政实质上是无产阶级专政，主要表现在(    ) ', 'ABCD', '是以工人阶级为领导.以工农联盟为基础的国家政权', '是新型民主和新型专政的国家政权', '取得全国政权后，担负社会主义改造和社会主义建设的任务', '以马克思主义作为国家的指导思想');
INSERT INTO `duoxiang` VALUES ('552', '人民民主专政是中国特色的无产阶级专政，体现在(    ) ', 'ABCD', '从阶级结构看，人民范围具有更大的广泛性', '从政党关系看，是共产党领导的多党合作制', '从政权组织形式看，是人民代表大会制度', '从概念表述看，人民民主与人民专政是相互联系的两个方面，提法更全面');
INSERT INTO `duoxiang` VALUES ('553', '下列国家机关由全国人民代表大会选举产生并对全国人民代表大会负责的有(    ) ', 'ABCD', '国务院', '中央军事委员会', '最高人民法院', '最高人民检察院');
INSERT INTO `duoxiang` VALUES ('554', '人民代表大会制度遵循的基本原则主要有(    )', 'ABC', '一切权力属于人民', '议行合一', '民主集中制', '少数服从多数');
INSERT INTO `duoxiang` VALUES ('555', '资本主义国家“三权分立”制度在本质上是(    )', 'CD', '防止国家政权侵犯人民自由的手段', '各阶级分掌统治权', '资产阶级统治阶级的内部职权分工', '资产阶级专政的工具');
INSERT INTO `duoxiang` VALUES ('556', '中国共产党领导的多党合作制度是(    )', 'ABC', '我国的一项基本政治制度', '我国政治制度的一大特点和优点', '我国的一种新型的社会主义政党关系', '我国社会主义民主政治完善性的体现');
INSERT INTO `duoxiang` VALUES ('557', '在共产党领导的多党合作制度中，各民主党派(    )', 'AD', '是社会主义性质的政党', '是无产阶级政党', '是执政党', '是参政党');
INSERT INTO `duoxiang` VALUES ('558', '中国共产党和各民主党派长期合作的政治和法律基础是(    )', 'ACD', '坚持四项基本原则', '坚持共产党的领导', '都以宪法为根本活动准则，共同坚持“长期共存.互相监督.肝胆相照.荣辱与共”的基本方针', '享有宪法规定范围内的政治自由.组织独立和法律上的平等地位)');
INSERT INTO `duoxiang` VALUES ('559', '民族区域自治的基本含义是(    )', 'ABC', '中央政权统一领导下的少数民族区域自治', '以少数民族聚居的地方为基础建立相应的自治地方', '设立自治机关，行使自治权', '自治区具有高度自治的特点');
INSERT INTO `duoxiang` VALUES ('560', '我国基层民主自治体系主要包括(    )', 'ABC', '农村村民委员会', '城市居民委员会', '企业职工代表大会', '连队士兵委员会');
INSERT INTO `duoxiang` VALUES ('561', '城市居民委员会是(    )', 'ABC', '我国基层民主自治体系的主要方面之一', '我国城市居民自我管理.自我教育.自我服务的基层群众自治性组织', '在城市基层实现直接民主', '城市的社会管理组织');
INSERT INTO `duoxiang` VALUES ('562', '坚持和完善民族区域自治制度要正确掌握好的关系是(    )', 'CD', '坚持民族区域自治制度和发展少数民族地区经济的关系', '汉族离不开各少数民族，各少数民族也离不开汉族的关系', '维护国家统一与完善民族区域自治的关系', '保证国家整体利益与兼顾民族区域利益的关系');
INSERT INTO `duoxiang` VALUES ('563', '建设社会主义民主政治是一个长期的过程，主要是因为(    )', 'BCD', '我国目前还存在一定范围的阶级斗争', '封建意识残余和资产阶级思想影响还长期存在', '我国尚不具备高度民主所需要的高度发达的经济文化', '认识和把握建设社会主义民主政治的客观规律，尚需一个过程');
INSERT INTO `duoxiang` VALUES ('564', '我国民主党派参政作用表现在(    )', 'ABD', '参加国家政权', '参与国家事务管理', '参加政府，成为执政党之一', '参与国家大政方针.政策和国家领导人的选举和协商');
INSERT INTO `duoxiang` VALUES ('565', '人民政协的民主监督职能主要是指(    )', 'BCD', '对国家工作人员提出质疑.弹劾', '对国家宪法和法律法规的实施情况进行监督', '对国家机关及其工作人员履行职责的情况进行监督', '对重大方针政策的贯彻执行情况进行监督');
INSERT INTO `duoxiang` VALUES ('566', '依法治国必须坚持的原则是(    )', 'AB', '共产党的领导和社会主义方向', '必须保证广大人民充分行使民主权利', '必须坚持社会主义法制的基本要求', '必须坚持四项基本原则');
INSERT INTO `duoxiang` VALUES ('567', '1954年9月15日，第一届全国人民代表大会第一次会议在北京召开，标志着人民代表大会制度在全国范围内建立起来。这次会议通过的关于人民代表大会制度的规定是(    )', 'ABC', '中华人民共和国的一切权力属于人民', '人民行使权力的机关是全国人民代表大会和地方各级人民代表大会', '全国人民代表大会和地方各级人民代表大会都实行民主集中制', '全国人民代表大会实行共产党领导的原则');
INSERT INTO `duoxiang` VALUES ('568', '关于我国实行民族区域自治制度，下列说法正确的是(    ) ', 'ABCD', '在各少数民族聚居地区实行区域自治，各民族自治地方都是中华人民共和国不可分割的部分', '民族自治地方享有广泛的自治权力', '在民族自治地方必须警惕和反对狭隘的民族主义和地方主义', '是中国共产党解决民族问题的一个独特创造');
INSERT INTO `duoxiang` VALUES ('569', '我国实行民族区域自治制度的基本依据有(    ) ', 'ABCD', '包括各少数民族在内的中华民族长期生活在一个统一的国家内', '中华民族大家庭的各民族在历史上很早就建立了经济.文化和政治上的密切联系和交融', '中国国内民族分布的特点是汉族为主体的大杂居.小聚居局面', '中国共产党的政治纲领代表了各民族的共同利益');
INSERT INTO `duoxiang` VALUES ('570', '中共十八届三中全会通过的《中共中央关于全面深化改革若干重大问题的决定》提出，建设社会主义文化强国，增强国家文化软实力，必须坚持 (  )', 'ABCD', '社会主义先进文化前进方向', '中国特色社会主义文化发展道路', '以人民为中心的工作导向，进一步深化文化体制改革', '完善文化管理体制，建立健全现代文化市场体系，构建现代公共文化服务体系，提高文化开放水平');
INSERT INTO `duoxiang` VALUES ('571', ' 中国特色社会主义文化发展道路主要内容是 (  ) ', 'ABCD', '坚持为人民服务.为社会主义服务的方向', '坚持百花齐放.百家争鸣的方针', '坚持贴近实际.贴近生活.贴近群众的原则', '推动精神文明和物质文明全面发展，建设面向现代化.面向世界.面向未来的社会主义先进文化');
INSERT INTO `duoxiang` VALUES ('572', ' 十八大提出社会主义核心价值观的三个倡导是 (  ) ', 'ABC', '倡导富强.民主.文明.和谐', '倡导自由.平等.公正.法治', '倡导爱国.敬业.诚信.友善', '倡导忠孝.仁勇.俭让.节义');
INSERT INTO `duoxiang` VALUES ('573', '所谓文化强国，是指这个国家具有强大的文化力量，这种力量表现为( )', 'ABC', '具有高度素养的国民', '发达的文化产业', '强大的文化软实力', '有悠长的文化历史');
INSERT INTO `duoxiang` VALUES ('574', '建设社会主义文化强国，需要(     )', 'ABCD', '培养高度的文化自觉自信', '大力发展文化产业', '大力发展文化事业', '加快文化体制改革');
INSERT INTO `duoxiang` VALUES ('575', '《中共中央关于构建社会主义和谐社会若干重大问题的决定》指出，社会主义核心价值体系的基本内容有(    )', 'ABCD', '马克思主义指导思想', '中国特色社会主义共同理想', '以爱国主义为核心的民族精神和以改革创新为核心的时代精神', '社会主义荣辱观');
INSERT INTO `duoxiang` VALUES ('576', '以“八荣八耻”为主要内容的社会主义荣辱观与科学发展观.构建社会主义和谐社会.加强党的先进性建设紧密相连.有机统一，是(    )', 'ABD', '对马克思主义道德观的精辟概括，对社会主义道德的系统总结', '是社会主义核心价值体系的基础', '是社会主义核心价值体系的灵魂', '是对社会主义思想道德体系全面系统.准确通俗的表达');
INSERT INTO `duoxiang` VALUES ('577', '中华民族精神的内容是(    )', 'ABCD', '爱国主义', '团结统一', '爱好和平', '勤劳勇敢.自强不息');
INSERT INTO `duoxiang` VALUES ('578', ' 社会主义精神文明(    ) ', 'ABCD', '是社会主义社会的重要特征', '是社会主义制度优越性的重要体现', '是社会主义现代化建设的重要目标', '是社会主义现代化建设的精神动力.思想保证和智力支持');
INSERT INTO `duoxiang` VALUES ('579', '建设中国特色社会主义文化对公民素质提出的综合要求是(    )', 'ABCD', '有理想', '有道德', '有文化', '有纪律');
INSERT INTO `duoxiang` VALUES ('580', '中国特色社会主义文化建设的基本方针是(    )', 'ABCD', '坚持以马克思主义为指导，为人民服务.为社会主义服务', '坚持百花齐放.百家争鸣的方针', '坚持立足当代又继承民族文化传统，立足本国又充分吸收世界优秀文化成果', '坚持接近实际.贴近生活.贴近群众，不断推进文化创新');
INSERT INTO `duoxiang` VALUES ('581', '深化文化体制改革，要坚持(    )', 'ABCD', '以发展为主题，以改革为动力', '以体制机制创新为重点', '发展政府主导的文化服务体系—经营性文化产业', '创造生产更多更好适应人民群众需求的精神文化产品为目标');
INSERT INTO `duoxiang` VALUES ('582', '中国特色社会主义文化建设的根本任务是(    )', 'ABCD', '以马克思主义为指导', '以培育有理想.有道德.有文化.有纪律的公民为目标', '全面贯彻科学发展观', '提高全民族的思想道德素质和科学文化素质');
INSERT INTO `duoxiang` VALUES ('583', '社会主义荣辱观的主要内容有(    ) ', 'ABCD', '以热爱祖国为荣.以危害祖国为耻，以服务人民为荣.以背离人民为耻', '以崇尚科学为荣.以愚昧无知为耻，以辛勤劳动为荣.以好逸恶劳为耻', '以团结互助为荣.以损人利己为耻，以诚实守信为荣.以见利忘义为耻', '以遵纪守法为荣.以违法乱纪为耻，以艰苦奋斗为荣.以骄奢淫逸为耻');
INSERT INTO `duoxiang` VALUES ('584', '下列选项中，正确的观点是(    )', 'AD', '在社会主义社会，国家.集体和个人的利益在根本上是一致的', '在社会主义市场经济条件下，我们要宣扬个人主义，这是市场经济本身的要求', '在社会主义市场经济条件下，“人人为我，我为人人”已不现实，取而代之的必然结果是“人人为自己，上帝为大家”', '在社会主义市场经济条件下建设精神文明，应当引导人们形成把国家和集体利益放在首位而又充分尊重个人正当利益的社会主义义利观');
INSERT INTO `duoxiang` VALUES ('585', '发展社会主义科学文化事业必须坚持“二为”方向。其内涵是(    )', 'BCD', '为改革开放服务，为发展市场经济服务', '为人民服务，为社会主义服务', '人民是社会的主人，社会主义文化应属于人民，为人民服务', '社会主义文化是社会政治.经济的反映，又反作用于社会主义的政治和经济，为社会主义制度服务');
INSERT INTO `duoxiang` VALUES ('586', '社会主义道德的主要内容是(    ) ', 'ABCD', '正确把握“为人民服务”的社会主义道德建设核心', '始终坚持社会主义道德的集体主义原则，加强理想.信念和世界观.人生观.价值观教育', '发扬爱祖国.爱人民.爱劳动.爱科学.爱社会主义的基本道德观', '大力加强社会公德.职业道德和家庭美德建设');
INSERT INTO `duoxiang` VALUES ('587', '中国特色社会主义文化建设的基本目标是(    ) ', 'AD', '培育有理想.有道德.有文化.有纪律的公民', '为人民服务，为社会主义服务', '繁荣学术和文艺', '为经济发展和社会进步提供精神动力和智力支持，提高全民族的思想道德素质和科学文化素质');
INSERT INTO `duoxiang` VALUES ('588', '构建社会主义核心价值体系的主要措施是(    ) ', 'ABCD', '坚持马克思主义在意识形态领域的指导地位', '形成中国特色社会主义的共同理想', '弘扬以爱国主义为核心的民族精神和以改革创新为核心的时代精神', '树立社会主义荣辱观');
INSERT INTO `duoxiang` VALUES ('589', '社会主义精神文明建设必须坚持(    )', 'ACD', '以马克思列宁主义.毛泽东思想.邓小平理论为指导思想', '以物质文明发展的需求为方向', '改革实开放', '四项基本原则');
INSERT INTO `duoxiang` VALUES ('590', '与其他社会的精神文明相比，社会主义精神文明是新型的精神文明，其特点有(    )', 'ACD', '社会主义精神文明是以马克思主义为指导的', '是以追求共产主义精神风尚为共同目标的', '成果归人民享有', '批判地继承了以往人类精神文明的全部优秀成果');
INSERT INTO `duoxiang` VALUES ('591', '要实现我国社会主义现代化建设的目标，必须努力建设中国特色社会主义文化，这是因为中国特色社会主义文化能够为现代化建设提供(   )', 'BCD', '民主基础', '智力支持', '精神动力', '思想保证');
INSERT INTO `duoxiang` VALUES ('592', '树立社会主义荣辱观，要建立相应的践行机制，主要包括要建立社会主义荣辱观的(    ) ', 'BCD', '利益机制', '奖罚机制', '监督机制', '评价机制');
INSERT INTO `duoxiang` VALUES ('593', '构建社会主义核心价值体系的基本内容是(    ) ', 'ABCD', '马克思主义指导思想', '建设中国特色社会主义共同理想', '以爱国主义为核心的民族精神和以改革创新为核心的时代精神', '社会主义荣辱观，核心是“八荣八耻”');
INSERT INTO `duoxiang` VALUES ('594', '构建社会主义核心价值体系应该正确处理的主要关系有(    )', 'ABC', '核心价值体系与社会多样化发展.思想多样性的关系', '中华民族的传统民族精神与改革创新的时代精神的关系', '爱国主义与社会主义的关系', '集体主义与个人主义的关系');
INSERT INTO `duoxiang` VALUES ('595', '构建核心价值体系最重要的是要正确处理社会主义核心价值体系与思想多样性和社会多样化发展的关系，就是要(    ) ', 'ABCD', '尊重多样性', '承认差异性', '思想多样性要以核心价值体系为导向', '核心价值体系尽可能以广泛共识为基础');
INSERT INTO `duoxiang` VALUES ('596', '社会主义荣辱观强调“以热爱祖国为荣.以危害祖国为耻”，是指(    )', 'AB', '以建设祖国.保卫祖国.富强祖国为最大光荣', '以损害祖国的荣誉.尊严和利益为最大耻辱', '要高度警惕可能出现的种种背离人民利益的倾向', '在全社会形成讲科学.爱科学.学科学.用科学的社会风尚');
INSERT INTO `duoxiang` VALUES ('597', '社会主义荣辱观强调“以服务人民为荣.以背离人民为耻”，就是指(    )', 'AB', '每个公民不论职务高低.能力大小，都要在不同岗位.通过不同形式为人民服务', '要高度警惕可能出现的种种背离人民利益的倾向', '通过不同形式为人民服务', '以建设祖国.保卫祖国.富强祖国为最大光荣');
INSERT INTO `duoxiang` VALUES ('598', '社会主义荣辱观强调“以崇尚科学为荣.以愚昧无知为耻”，就是指(    )', 'AB', '必须把科学技术真正置于优先发展的战略地位', '在全社会形成讲科学.爱科学.学科学.用科学的社会风尚', '要高度警惕可能出现的种种背离人民利益的倾向', '通过不同形式为人民服务');
INSERT INTO `duoxiang` VALUES ('599', '社会主义荣辱观强调“以辛勤劳动为荣.以好逸恶劳为耻”，就是指(    )', 'AB', '在各行各业包括新兴行业中培养爱劳动的思想', '使爱劳动的思想成为衡量公民道德品质的价值尺度', '在全社会形成讲科学.爱科学.学科学.用科学的社会风尚', '在全社会形成爱劳动的良好风气');
INSERT INTO `duoxiang` VALUES ('600', '社会主义荣辱观强调“以团结互助为荣.以损人利己为耻”，就是指(    )', 'BD', '必须把科学技术真正置于优先发展的战略地位', '在市场经济条件下形成良好的社会风气', '通过不同形式为人民服务', '避免种种损人利已的行为');
INSERT INTO `duoxiang` VALUES ('601', '社会主义荣辱观强调“以诚实守信为荣.以见利忘义为耻”，就是指(    )', 'ABC', '要将诚实守信作为个人行为的基本准则', '要将诚实守信作为企业.事业单位乃至政府工作的基本准则', '对假冒伪劣.不讲信誉等行为予以严厉谴责', '避免种种损人利己的行为');
INSERT INTO `duoxiang` VALUES ('602', '社会主义荣辱观强调“以艰苦奋斗为荣.以骄奢淫逸为耻”，就是指(    )', 'BCD', '要增强法律意识，自觉维护法律尊严', '要认识到我们现代化建设的路还很长，任务还很艰巨', '必须在新时期发扬艰苦奋斗的精神', '形成“节约光荣.浪费可耻”的社会风尚');
INSERT INTO `duoxiang` VALUES ('603', '构建社会主义核心价值体系的重要性在于(    ) ', 'ABCD', '每个国家.民族.文明都应该有自己的核心价值体系', '构建核心价值体系是建设社会主义和谐文化的根本', '核心价值体系是中华民族团结奋斗的向心力.凝聚力.精神动力', '核心价值体系是中国特色社会主义的理论基础.价值导向和思想保证');
INSERT INTO `duoxiang` VALUES ('604', '毛泽东历来十分重视文化的作用。他多次论述了文化与政治.经济的关系，提出了一些新民主主义和社会主义建设的指导方针，他的思想方法主要有(    ) ', 'ABCD', '任何社会没有文化就建设不起来；要把一个被旧文化统治而愚昧落后的中国，变为一个被新文化统治因而文明先进的中国', '强调建设无产阶级文化思想为领导的民族的.科学的.大众的新民主主义文化', '文学艺术要为人民大众服务，吸收民族文化的精华.剔除其封建性的糟粕等思想', '提倡百花齐放.百家争鸣.古为今用.洋为中用等，成为文化建设的重要指导方针');
INSERT INTO `duoxiang` VALUES ('605', '在改革开放的新时期，邓小平主要从社会主义精神文明建设的角度提出，形成了他的文化建设思想，主要有(    ) ', 'ABCD', '社会主义精神文明是社会主义社会的重要特征，要在建设高度的物质文明的同时，建设高度的社会主义精神文明', '物质文明和精神文明都搞好，才是有中国特色的社会主义', '要一手抓物质文明，一手抓精神文明，“两手都要抓，两手都要硬”', '提高全民族的思想道德素质和科学文化素质，培育有理想.有道德.有文化.有纪律的社会主义新人');
INSERT INTO `duoxiang` VALUES ('606', '党的十三届四中全会以后，江泽民的文化建设思想突出地强调“先进性”，强调多样化发展与弘扬主旋律的相互关系，体现了党对精神文明建设.文化建设规律的认识不断深化。他的主要思想有(    ) ', 'ABCD', '中国共产党要始终代表中国先进文化前进的方向，坚持以科学的理论武装人，以正确的舆论引导人，以高尚的精神塑造人，以优秀的作品鼓舞人', '中国特色社会主义文化是综合国力的重要标志', '建设有中国特色社会主义文化，就是以马克思主义为指导，以培育有理想.有道德.有文化.有纪律的公民为根本任务，发展面向现代化.面向世界.面向未来的，民族的.科学的.大众的社会主义文化', '要把弘扬主旋律和提倡多样化统一起来，把依法治国和以德治国紧密结合起来');
INSERT INTO `duoxiang` VALUES ('607', '文化建设的“双百方针”是指(   )', 'AB', '百花齐放', '百家争鸣', '洋为中用', '古为今用');
INSERT INTO `duoxiang` VALUES ('608', '人是文化的主体。建设中国特色社会主义文化，归根到底是为了(    ) ', 'ABCD', '满足人民群众日益增长的精神文化需要', '不断丰富人们的精神世界', '增强人们的精神力量', '促进人的全面发展');
INSERT INTO `duoxiang` VALUES ('609', '改革文化体制，解放和发展文化生产力，要坚持(   )的统一(    ) ', 'BC', '文化效益', '经济效益', '社会效益', '人文效益');
INSERT INTO `duoxiang` VALUES ('610', '毛泽东非常关注培养革命事业的接班人，邓小平在新时期把培养社会主义事业接班人的基本要求概括为“四有”，其中(    ) ', 'ABCD', '理想是核心和精神支柱', '道德是行为的规范和理想的体现', '纪律是实现理想.维护道德的重要保证', '文化是基础，是形成理想信念.道德情操和纪律观念的重要条件');
INSERT INTO `duoxiang` VALUES ('611', '培养社会主义“四有”公民的重要意义在于(    )', 'ABC', '培育有理想.有道德.有文化.有纪律的社会主义公民，是建设社会主义先进文化对公民素质提出的综合要求', '培育有理想.有道德.有文化.有纪律的社会主义公民，提高全体公民的素质，是促进人的全面发展的需要', '培育有理想.有道德.有文化.有纪律的社会主义公民，是一个复杂的系统工程，是我国文化建设面临的一项长期而艰巨的任务', '是防止和平演变，反修防修的根本保证');
INSERT INTO `duoxiang` VALUES ('612', '中国特色文化建设的基本方针(    )', 'ABCD', '坚持以马克思主义为指导，为人民服务，为社会主义服务。坚持以什么为指导，为什么人的问题，是文化建设的根本问题。', '坚持百花齐放.百家争鸣的方针。弘扬主旋律，提倡多样化，充分调动广大文艺工作者的积极性和创造性，促进学术研究和艺术创作的繁荣发展', '坚持贴近实际.贴近生活.贴近群众，不断推进文化创新。只有坚持贴近实际.贴近生活.贴近群众，文化建设才能回答和解决现实问题.反映生活本质.满足群众的文化需求', '坚持立足当代又继承民族优秀文化传统，立足本国又充分吸收世界优秀文化成果；同时坚持一手抓繁荣，一手抓管理，繁荣是文化建设的永恒主题');
INSERT INTO `duoxiang` VALUES ('613', '社会主义核心价值体系之所以是社会主义意识形态的本质特征，是因为(    ) ', 'ABCD', '马克思主义是社会主义核心价值体系的灵魂', '中国特色社会主义共同理想是社会主义核心价值体系的主题', '以爱国主义为核心的民族精神和以改革创新为核心的时代精神是社会主义核心价值体系的精髓', '社会主义荣辱观是社会主义核心价值体系的基础');
INSERT INTO `duoxiang` VALUES ('614', '构建社会主义核心价值体系是社会主义文化建设的根本。因为在文化建设中只有抓住核心价值体系才能(    ) ', 'ABCD', '形成全社会的共同理想，增强全社会的凝聚力', '树立全社会的和谐理念，培育全社会的和谐精神', '形成全社会的良好道德风尚，形成全社会的和谐人际关系；营造全社会的和谐舆论氛围，塑造全社会的和谐心态', '在尊重差异中扩大社会认同，在包容多样中增进思想共识，形成全民族奋发向上的精神力量和团结和睦的精');
INSERT INTO `duoxiang` VALUES ('615', '马克思主义是社会主义核心价值体系的灵魂，我们要把坚持和发展马克思主义统一于中国特色社会主义实践中，自觉做到(    )', 'AB', '坚持马克思主义的立场.观点.方法，坚持马克思主义的基本原理，这一点要坚定不移，不能含糊', '贯彻解放思想.实事求是的思想路线，坚持勇于追求真理和探索真理的革命精神，这一点也要坚定不移，不能含糊', '在坚持马克思主义中发展马克思主义', '在发展马克思主义中坚持马克思主义');
INSERT INTO `duoxiang` VALUES ('616', '社会主义荣辱观是对社会主义思想道德体系全面系统.准确通俗的表达，是因为它(    ) ', 'ABCD', '继承了中华民族传统美德，又发扬了我们党优秀革命道德传统，概括了社会主义思想道德建设的新鲜经验，集中体现了改革开放以来形成的时代精神和时代风尚', '涵盖了个人.集体.国家三者之间的关系，涉及人生态度.社会风尚等方方面面', '体现了爱国主义.集体主义.社会主义思想，体现了依法治国同以德治国的结合', '标志着我们党对社会主义思想道德建设规律认识的深化');
INSERT INTO `duoxiang` VALUES ('617', '社会主义思想道德建设的基本要求是(    )', 'ABC', '按照与社会主义市场经济相适应的要求，进一步培养社会主义思想道德观念，促进社会主义市场经济的健康发展', '按照与社会主义法律规范相协调的要求，进一步把依法治国与以德治国结合起来，使法律和道德相辅相成.相互促进', '按照与中华民族传统美德相承接的要求，进一步把中华民族的优良传统发扬光大，使社会主义思想道德既具有民族特色，又具有鲜明的时代特征', '按照全球化时代的客观要求，把社会主义思想道德建设与全球化的普适价值相结合，在社会主义思想道德建');
INSERT INTO `duoxiang` VALUES ('618', '要深刻认识大力推进文化创新的重要意义，主要有(    ) ', 'ABCD', '推进文化创新是我们党始终代表先进文化前进方向的迫切需要', '推进文化创新是建设国家创新体系的迫切需要，是解放和发展文化生产力的迫切需要', '推进文化创新是激发全民族文化创造活力的迫切需要', '推进文化创新是不断满足人民群众日益增长的精神文化需求的迫切需要');
INSERT INTO `duoxiang` VALUES ('619', '文化体制改革的过程也就是文化创新的过程，具体包括(    ) ', 'ABCD', '创新管理体制', '创新产业格局', '创新市场体系', '创新市场主体');
INSERT INTO `duoxiang` VALUES ('620', '建设中国特色社会主义文化是(   )', 'ABC', '社会主义现代化建设的重要内容', ' 综合国力的重要标志,凝聚和激励全国各族人民的重要力量', ' 为社会主义现代化建设提供智力支持.精神动力和思想保证', '深化科技体制改革的具体形式');
INSERT INTO `duoxiang` VALUES ('621', ' 中国特色社会主义管理体系主要包括 (  ) ', 'ABCD', '加快形成党委领导.政府负责.社会协同.公众参与.法制保障的社会管理体制', '加快形成政府主导.覆盖城乡.可持续的基本公共服务体系', '加快形成政社分开.权责明确.依法自治的现代社会组织体制', '加快形成源头治理.动态管理.应急处置相结合的社会管理体制');
INSERT INTO `duoxiang` VALUES ('622', '创新社会治理的举措主要有(   )', 'ABCD', '改进社会治理方式', '激发社会组织活力', '创新有效预防和化解社会矛盾体制', '健全公共安全体制');
INSERT INTO `duoxiang` VALUES ('623', '党的社会主义和谐社会理论提出过程主要有(    ) ', 'ABCD', '十六大报告明确把社会和谐列为全面建设小康社会的一个主要目标', '十六届四中全会强调形成全体人民和谐相处的社会是巩固党执政基础的必然要求，进一步提出了构建社会主义和谐社会的重要任务，并且明确了构建社会主义和谐社会的主要内容，标志社会主义和谐社会正式提出', '十六届六中全会审议通过《关于构建社会主义和谐社会若干重大问题的决定》，全面深刻阐明了社会主义和谐社会的性质.定位.指导思想.目标任务.工作原则和重大部署，标志社会主义和谐社会理论正式形成', '党的十七大再次强调构建社会主义和谐社会的重要性，并对以改善民生为重点的和谐社会建设作了全面部署，是社会主义和谐社会理论的进一步深化');
INSERT INTO `duoxiang` VALUES ('624', '十六大以后，党中央反复强调要把推进社会主义和谐社会建设作为全面建设小康社会和中国未来发展的重要任务，现实依据是(  ) ', 'ABC', '是我们抓住和用好重要战略机遇期，实现全面建设小康社会宏伟目标的必然要求；我国已进人改革发展的关键时期，社会总体上是和谐的；但是，也存在不少影响社会和谐的矛盾和问题，这些问题处理不好，就会严重影响社会和谐稳定和全面建设小康社会的大局；我们党要坚持以经济建设为中心，把构建社会主义和谐社会摆在更加突出的地位', '是我们把握复杂多变的国际形势.有力应对来自国际环境的各种挑战和风险的必然要求；新世纪新阶段，在复杂多变的国际形势下，我们要有力应对来自外部的各种挑战和风险，就必须首先把国内的事情办好，通过和谐社会建设，始终保持国家统一.民族团结.社会稳定的局面', '是巩固党执政地位的社会基础，实现党执政历史任务的必然要求；构建社会主义和谐社会，是党坚持立党为公.执政为民的必然要求，是实现好.维护好.发展好最广大人民的根本利益的重要体现，也是党实现执政历史任务的重要条件', '国内科学发展.社会和谐，国际上和平发展.和谐世界，是十六大以来党中央领导集体重大的理论创新，具体落实这些理论创新，必然要求推进社会主义和谐社会建设');
INSERT INTO `duoxiang` VALUES ('625', '一般地讲，所谓和谐社会，主要是指(  )', 'AB', '就是人与自然.人与社会.人与人之间和谐统一与协调发展的社会', '就是生产力和生产关系.经济基础和上层建筑之间和谐统一与协调发展的社会', '就是公平正义.各种利益关系得到妥善协调的和谐社会', '就是全社会互帮互助.诚实守信.全体人民平等友爱.融洽相处的和谐社会');
INSERT INTO `duoxiang` VALUES ('626', '十六届六中全会提出，社会要和谐，首先要发展，就是必须(    )', 'ABC', '坚持用发展的办法解决前进中的问题', '坚持大力发展社会生产力，不断为社会和谐创造雄厚的物质基础', '同时注重发展社会事业，推动经济社会全面协调发展', '继续只强调经济发展问题，不断提高人均GDP水平');
INSERT INTO `duoxiang` VALUES ('627', '2006年7月10日至12日，全国统战工作会议在北京举行。中共中央总书记.国家主席.中央军委主席胡锦涛出席会议并发表重要讲话。阐述构建社会主义和谐社会的主要关系，即(    ) ', 'ABCD', '政党关系', '民族关系和宗教关系', '阶层关系', '海内外同胞关系');
INSERT INTO `duoxiang` VALUES ('628', '构建社会主义和谐社会，必须坚持的正确的原则是(    ) ', 'ABCD', '必须坚持以人为本，必须坚持改革开放', '必须坚持民主法治', '必须坚持正确处理改革.发展.稳定的关系', '必须坚持在党的领导下全社会共同建设，加强和改善党的领导，团结一切可以团结的力量，齐心协力促进社会和谐');
INSERT INTO `duoxiang` VALUES ('629', '我们要构建的社会主义和谐社会，是在中国特色社会主义道路上，中国共产党领导全体人民共同建设.共同享有的和谐社会——关于社会主义和谐社会的这一科学定位，深刻揭示了我国和谐社会建设的(    ) ', 'ABCD', '领导核心', '发展道路', '实践主体', '根本目的');
INSERT INTO `duoxiang` VALUES ('630', '十六届四中全会提出“构建社会主义和谐社会”的重大战略举措以后，中国特色社会主义事业的总体布局实现了从“三位一体”到“四位一体”的飞跃。这个“四位一体”是指(    )', 'ABC', '经济建设', '政治建设.文化建设', '社会建设', '国防建设.党的建设');
INSERT INTO `duoxiang` VALUES ('631', '根据2002年2月胡锦涛重要讲话的论述，社会主义和谐社会的科学内涵.主要内容和基本特征是(    )', 'ABD', '民主法治.公平正义', '诚信友爱.充满活力', '自由幸福.互助合作', '安定有序.人与自然和谐相处');
INSERT INTO `duoxiang` VALUES ('632', '构建社会主义和谐社会的重大意义主要是指(    ) ', 'ABCD', '建设中国特色社会主义的新认识', '党在执政理念上的新发展', '在新世纪新阶段适应新形势.应对新挑战的迫切需要', '巩固执政党地位的必然要求.实现全面建设小康社会目标的重要保证');
INSERT INTO `duoxiang` VALUES ('633', '作为构建社会主义和谐社会的基本内涵之一，民主法治就是(    )', 'ABC', '社会主义民主得到充分发扬', '各方面积极因素得到广泛调动', '依法治国方略得到切实落实', '政治参与得到进一步扩大');
INSERT INTO `duoxiang` VALUES ('634', '作为构建社会主义和谐社会的基本内涵之一，公平正义就是(    )', 'ABD', '社会各方面利益得到妥善协调', '人民内部矛盾及其他社会矛盾得到正确处理', '弱势群众的基本利益得到切实保障', '社会公平和正义得到切实维护与实现');
INSERT INTO `duoxiang` VALUES ('635', '作为构建社会主义和谐社会的基本内涵之一，诚信友爱就是(    )', 'AB', '全社会互帮互助.诚实守信', '全体人民平等友爱.融洽相处', '全社会形成普遍的团结认同', '人与人之间形成良好的人际关系');
INSERT INTO `duoxiang` VALUES ('636', '作为构建社会主义和谐社会的基本内涵之一，充满活力就是使一切有利于社会进步的(    ) ', 'ABCD', '创造愿望得到尊重', '创造活动得到支持', '创造成果得到肯定', '创造才能得到发挥');
INSERT INTO `duoxiang` VALUES ('637', '根据和谐社会的科学内涵或者六大特征来讲，和谐社会的六个方面是相互联系.相互促进的，其中包括(    ) ', 'ABCD', '社会关系的和谐.人与自然关系的和谐', '民主与法治的统一.公平与效率的统一', '活力与秩序的统一.科学与人文的统一', '人与自然的统一');
INSERT INTO `duoxiang` VALUES ('638', '完整准确地理解和谐社会的科学含义，应该正确把握几个方面，其中有(    ) ', 'ABCD', '正确把握社会主义和谐社会的性质；不是以往其他社会形态标榜的社会协调与和谐，而是社会主义性质的和谐社会', '正确把握构建社会主义和谐社会同建设社会主义物质文明.政治文明.精神文明的关系；这四者是有机统一的，它们既有不可分割的紧密联系，又有各自的特殊领域和规律', '正确把握构建社会主义和谐社会与全面建设小康社会的关系；构建社会主义和谐社会是贯穿中国特色社会主义事业全过程的长期历史任务和全面建设小康社会的重大现实课题', '正确把握科学发展观与和谐社会的相互关系，没有科学发展就没有和谐社会，没有社会和谐也难以达到科学发展，所谓和谐社会就是科学发展观指导下正确处理各种矛盾的过程和结果');
INSERT INTO `duoxiang` VALUES ('639', '构建社会主义和谐社会与全面建设小康社会，是建设中国特色社会主义的两大社会范畴，两者相互包含.相辅相成，即(    )', 'ABC', '构建社会主义和谐社会是全面建设小康社会的重要内容，又是全面建设小康社会的重要条件', '在全面建设小康社会阶段，构建和谐社会是一项重大的现实任务', '构建社会主义和谐社会又是一个长期的过程，在实现全面建设小康社会的伟大目标之后，还要继续为更高水平的和谐社会长期奋斗', '构建和谐社会贯穿于建设中国特色社会主义始终');
INSERT INTO `duoxiang` VALUES ('640', '党的十六届六中全会提出了构建社会主义和谐社会的基本原则，其中有(    )    ', 'ABCD', '必须坚持以人为本，是和谐社会建设的根本出发点和落脚点', '必须坚持科学发展的工作方针和改革开放，改革开放是和谐社会建设的主要动力', '必须坚持民主法治，是和谐社会建设的政治保证；必须坚持正确处理改革.发展.稳定的关系，是和谐社会建设的重要条件', '必须坚持在党的领导下全体人民共同建设，是构建社会主义和谐社会的领导核心和依靠力量');
INSERT INTO `duoxiang` VALUES ('641', '构建和谐社会关于教育问题的政策方针(    ) ', 'ABCD', '全面贯彻党的教育方针，要坚持育人为本.德育为先，培养德智体全面发展的社会主义建设者和接班人', '优化教育结构，优化教育资源配置，促进义务教育均衡发展，形成各级各类教育全面协调可持续发展的良好格局', '推进教育改革创新，要着眼于构建现代国民教育体系，关键是更新教育观念，改进人才培养模式，深化教学内', '坚持教育公益性质，加大财政对教育的投入，规范教育收费，健全公共财政投入和保障机制，为全体国民提供接受良好教育的机会和条件');
INSERT INTO `duoxiang` VALUES ('642', '教育是民族振兴的基石，教育公平是社会公平的重要基础。党的十七大关于教育公平的方针政策有(    ) ', 'ABCD', '坚持教育公益性质，加大财政对教育的投入，规范教育收费，健全公共财政投入和保障机制，为全体国民提供接受良好教育的机会和条件', '在全国城乡普遍实施九年义务制教育', '扶植贫困地区.民族地区教育，健全学生资助制度，保障困难家庭子女接受义务教育', '保障进城务工人员子女平等地接受义务教育');
INSERT INTO `duoxiang` VALUES ('643', '就业是民生之本，是保障和改善人民生活的重要条件。党的十七大提出实施扩大就业的发展战略，主要措施有(    ) ', 'ABCD', '千方百计增加就业岗位；积极发展经济和调整结构，以发展促进就业，扩大就业规模，改善就业结构', '鼓励自主创新.自谋职业；促进以创业带动就业，是解决就业问题的一个重大方针；积极培育创业主体，使更多劳动者成为创业者，推动创业型社会建设，扩大就业容量', '推进就业体制改革创新；要统筹城市就业和农村劳动力转移就业，形成城乡劳动者平等就业的制度，健全覆盖城乡的就业服务体系', '规范和协调劳动关系，依法维护劳动者权益，发展和谐劳动关系，继续完善和落实国家对农民工的政策');
INSERT INTO `duoxiang` VALUES ('644', '深入分配制度改革，增加城乡居民收入的政策措施有(    )', 'ABC', '坚持和完善按劳分配为主体.多种分配方式并存的分配制度，健全劳动.资本.技术.管理等生产要素按贡献参与分配的制度，放手让一切劳动.知识.技术.管理和资本的活力竞相进发', '逐步提高居民收入在国民收入分配中的比重，提高劳动报酬在初次分配中的比重，调整国民收入分配格局，增加广大劳动者收入，维护劳动者权益', '加大个人收入分配调节力度，合理调整收入分配格局', '初次分配和再分配都要处理好效率与公平的关系，再分配更加注重公平');
INSERT INTO `duoxiang` VALUES ('645', '解决贫富差距过分悬殊的问题，要求之一是加大个人收入分配调节力度，合理调整收人分配格局，具体措施是 (    ) ', 'ABCD', '着力提高低收入者收入，促进农民持续增收，逐步提高扶贫标准和最低工资标准，使城乡居民特别是低收入者收入随着经济发展逐步较多地增加', '努力扩大中等收入者比重，采取多种措施，创造条件让更多群众拥有财产性收入，使更多低收入者进人中等收人者行列', '切实对过高收入进行有效调节，使过高收人者的一部分收入通过税收等形式由国家集中用于再分配', '取缔非法收入，规范垄断行业的收入');
INSERT INTO `duoxiang` VALUES ('646', '党和政府对扩大就业的指导方针是(    ) ', 'ABC', '劳动者自主择业', '市场调节就业', '政府促进就业', '以创业带动就业');
INSERT INTO `duoxiang` VALUES ('647', '我国关于完善社会保障体系的总要求是(    ) ', 'ABCD', '坚持广覆盖.保基本.多层次.可持续的指导方针', '以社会保险.社会救助.社会福利为基础，以基本养老.基本医疗.最低生活保障制度为重点', '以慈善事业.商业保险为补充', '加快建立覆盖城乡居民的社会保障体系');
INSERT INTO `duoxiang` VALUES ('648', '加快建立覆盖城乡居民的社会保障体系的主要措施有(    ) ', 'ABCD', '完善基本养老保险制度，完善社会统筹与个人账户相结合的企业职工养老保险机制，探索建立农村养老保险制度', '完善基本医疗保险制度；完善城市最低生活保障制度', '发展社会救助与慈善事业；积极发挥商业保险的补充作用', '采取多种方式充实社会保障基金，实现保值增值；把解决住房问题放在重要位置，加快建立适应全体居民需要的多层次住房保障体系，特别要健全廉租房制度');
INSERT INTO `duoxiang` VALUES ('649', '建立基本医疗卫生制度，解决看病难.看病贵的问题，提高全民健康水平，实现人人享有基本医疗服务的目标。总的原则和要求是(    ) ', 'ABCD', '坚持公共医疗卫生的公益性质，坚持预防为主.以农村为重点.中西医并重', '实行政事分开.管办分开.医药分开.营利性和非营利性分开，强化政府责任和投入，完善国民健康政策', '鼓励社会参与，建设覆盖城乡居民的公共卫生服务体系.医疗服务体系.医疗保障体系.药品供应保障体系', '为群众提供安全.有效.方便.价廉的医疗卫生服务');
INSERT INTO `duoxiang` VALUES ('650', '构建和谐社会，加强社会管理要形成相应的社会管理局面，即(    ) ', 'ABCD', '党委领导', '政府负责', '社会协同', '公众参与');
INSERT INTO `duoxiang` VALUES ('651', '作为构建社会主义和谐社会的基本内涵之一，安定有序就是(    ) ', 'ABCD', '社会组织机制健全', '社会管理完善.社会秩序良好', '人民群众安居乐业', '社会保持安定团结');
INSERT INTO `duoxiang` VALUES ('652', '作为构建社会主义和谐社会的基本内涵之一，人与自然和谐相处就是(    )', 'ABD', '生产发展', '生活富裕', '生命健康', '生态良好');
INSERT INTO `duoxiang` VALUES ('653', '在构建社会主义和谐社会的进程中，中国需要着力破解的难题有(    ) ', 'ABCD', '理顺分配关系，解决社会公平的“核心问题”；解决就业难题，巩固“民生之本”', '科学调控宏观经济，转变粗放型发展方式；健全社会保障体系，完善“托底”机制', '畅通利益诉求渠道，及时化解社会矛盾', '经济社会并重，克服发展“短腿”现象；把反腐制度落到实处，防止“权力寻租”');
INSERT INTO `duoxiang` VALUES ('654', '胡锦涛指出：和谐社会建设关键在于提高我们党构建社会主义和谐社会的能力，主要是指提高我们党(    )', 'ABC', '激发社会创造活力而又维护社会稳定的本领', '管理社会事务.协调利益关系的本领', '开展群众工作.处理人民内部矛盾的本质', '处理突发事件进行危机管理的本领');
INSERT INTO `duoxiang` VALUES ('655', '十六届六中全会一致认为，社会和谐是中国特色社会主义的(    )', 'ABC', '本质属性', '基本特征', '基本要求', '根本目的');
INSERT INTO `duoxiang` VALUES ('656', '十六届六中全会明确了社会主义和谐社会的科学定位，强调我们构建和谐社会，是在中国特色社会主义道路上，中国共产党领导全体人民(    )', 'AB', '共同建设的和谐社会', '共同享有的和谐社会', '共同设计的和谐社会', '共同追求的和谐社会');
INSERT INTO `duoxiang` VALUES ('657', '构建社会主义和谐社会的主要工作是(    ) ', 'ABCD', '促进经济社会协调发展，为构建和谐社会提供坚定基础', '完善促进公平正义的体制机制，为构建和谐社会提供制度保障；加强思想文化建设，为构建和谐社会提供精神支撑', '加强社会管理，维护社会稳定，为构建和谐社会提供良好环境', '增强社会团结和活力，为构建和谐社会提供广泛力量');
INSERT INTO `duoxiang` VALUES ('658', '构建社会主义和谐社会的重要意义在于(    ) ', 'ABCD', '中国特色社会主义事业“四位一体”总体布局的重要组成部分', '全面建设小康社会的重要目标', '中国最广大人民群众的根本利益所在', '应对外部挑战的重要条件');
INSERT INTO `duoxiang` VALUES ('659', '构建社会主义和谐社会必然要求加强社会管理；维护社会稳定，这就必然要求(    ) ', 'ABCD', '创新社会管理体制，稳定社会管理', '提高社会管理水平', '健全党委领导.政府负责.社会协同.公众参与的社会管理格局', '坚持在服务中实施管理.在管理中体现服务的原则');
INSERT INTO `duoxiang` VALUES ('660', ' 中共十八届三中全会通过的《中共中央关于全面深化改革若干重大问题的决定》提出，建设生态文明，必须建立系统完整的生态文明制度体制，用制度保护生态环境。要 (  ) ', 'ABCD', '健全自然资源资产产权制度和用途管制制度', '划定生态保护红线', '实行资源有偿使用制度和生态补偿制度', '改革生态环境保护管理体制');
INSERT INTO `duoxiang` VALUES ('661', ' 建设生态文明，是关系人民福祉.关乎民族未来的长远大计。面对资源约束趋紧.环境污染严重.生态系统退化的严峻趋势，必须树立正确的生态文明概念是 (  ) ', 'ABC', '尊重自然', '顺应自然', '保护自然', '改造自然');
INSERT INTO `duoxiang` VALUES ('662', ' 生态文明建设的基本国策是 (  ) ', 'AB', '节约资源', '保护环境', '提高资源利用效率', '用好国内国际两种资源');
INSERT INTO `duoxiang` VALUES ('663', ' 生态文明建设的基本方针是 (  ) ', 'ABC', '节约优先', '保护优先', '自然恢复为主', '开发利用为主');
INSERT INTO `duoxiang` VALUES ('664', '中国共产党面向未来的“两个一百年”是(     )', 'AB', '建党100年，全面建成小康社会', '建国100年，建成社会主义现代化国家', '建军100年，成为世界第一强大国家', '改革开放100年，成为世界第一大国');
INSERT INTO `duoxiang` VALUES ('665', '十八大提出，从2010年到2020年要实现“两个倍增”计划是(   )', 'AD', '国内生产总值倍增', '进出口贸易额倍增', '外汇储备量倍增', '城乡居民人均收入倍增');
INSERT INTO `duoxiang` VALUES ('666', '十八大提出，根据我国经济社会发展实际，对全面建设小康的新要求是(  )', 'ABCD', '经济持续健康发展，转变经济发展方式取得重大进展', '人民民主不断扩大，民主制度更加完善，文化软实力显著增强，社会主义核心价值体系深入人心', '人民生活水平全面提高，基本公共服务均等化总体实现', '资源节约型.环境友好型社会建设取得重大进展，主体功能区布局基本形成，资源环境利用体系初步建立');
INSERT INTO `duoxiang` VALUES ('667', '实现祖国的完全统一是(   )', 'ABCD', '祖国繁荣富强和民族伟大复兴的基础', '中华民族的根本利益所在', '中国社会发展不可阻挡的历史潮流', '建设中国特色社会主义的一项重大历史任务');
INSERT INTO `duoxiang` VALUES ('668', '“一国两制”构想(  )', 'ABCD', '是邓小平理论的重要组成部分，为解决国际争端提供了一个范例', '基本内容是一个国家.两种制度', '是实现祖国和平统一的基本方针', '体现了原则性和灵活性的统一');
INSERT INTO `duoxiang` VALUES ('669', '祖国统一是(  )', 'ABC', '中华民族的情感.意志和夙愿的集中体现', '中华民族伟大凝聚力的集中体现', '中华民族的根本利益所在', '中国封建大一统思想的全面继承');
INSERT INTO `duoxiang` VALUES ('670', '我国政府以“一国两制”解决香港.澳门.台湾问题的方针政策主要有(    )', 'ACD', '坚持“一个中国”', '实现祖国统一后，香港.澳门和台湾可设立特别行政区，实行完全自治', '国家主体坚持社会主义制度，港.澳.台保持资本主义制度长期不变', '力争用和平方式实现祖国统一，但不承诺放弃使用武力');
INSERT INTO `duoxiang` VALUES ('671', '邓小平提出“一国两制”解决台湾.香港和澳门问题，实现祖国和平统一的伟大构想(    )', 'ABCD', '体现了实事求是的思想路线', '发展了马克思主义的国家学说', '体现了照顾历史实际和现实可能的灵活性', '体现了维护国家统一和主权的原则性');
INSERT INTO `duoxiang` VALUES ('672', '我国党与政府在台湾问题上的基本立场是(    )', 'ACD', '坚持“和平统一.一国两制”的基本方针和发展两岸关系.推进和平统一进程的八项主张', '台湾问题和香港.澳门问题一样，都是殖民主义侵略遗留下来的问题', '主张用和平方式实现统一，但决不承诺放弃使用武力', '坚持一个中国的原则，反对以任何方式改变台湾是中国一部分的地位');
INSERT INTO `duoxiang` VALUES ('673', '按照我国宪法规定，特别行政区是中华人民共和国不可分割的一部分。这个论断表明(    )', 'ACD', '“一国两制”的前提是坚持一个中国的原则', '特别行政区必须坚持四项基本原则', '特别行政区政府是国家的一级地方政权机关', '特别行政区直辖于中央人民政府');
INSERT INTO `duoxiang` VALUES ('674', '我国政府恢复对澳门行使主权后，对澳门实行“一国两制”.澳人治澳.高度自治的方针。澳门特别行政区在政治和法律方面享有的自治权包括(    )', 'BD', '外交权', '行政管理权和立法权', '防务权', '独立的司法权和终审权');
INSERT INTO `duoxiang` VALUES ('675', '根据《中华人民共和国宪法》的规定，1997年7月1日中国恢复对香港行使主权以后，在香港设立了特别行政区。香港特别行政区由香港当地人自己管理，享有高度自治权，包括(  )', 'ABD', '立法权', '独立的司法权和终审权', '独立的外交权', '行政管理权和财政独立');
INSERT INTO `duoxiang` VALUES ('676', '中国政府提出，我们力争用和平方式实现祖国统一，但决不承诺放弃使用武力，是因为(    )', 'ABCD', '在台湾与祖国大陆统一的问题上始终存在着和平方式与非和平方式两种可能性', '能否以和平方式统一祖国，并不完全取决于我们一方', '不承诺放弃使用武力，决不是针对台湾人民的', '不承诺放弃使用武力，主要是针对外国势力干涉中国统一和“台独”势力搞“台湾独立”的阴谋');
INSERT INTO `duoxiang` VALUES ('677', '香港.澳门的回归标志着(    )', 'ABCD', '中国人民终于洗雪了遭受殖民侵略的百年国耻', '“一国两制”构想切实可行，取得了巨大的成功', '中国人民在完成祖国统一的道路上迈出了重要的一步', '香港.澳门和祖国内地开创了共同发展的新纪元');
INSERT INTO `duoxiang` VALUES ('678', '按照“一国两制”方针实现两岸统一，就是(  )', 'CD', '不用武力方式', '通过经济交流的方式来解决', '主张用和平手段来解决，但不承诺放弃使用武力', '通过政治谈判的方式来解决');
INSERT INTO `duoxiang` VALUES ('679', '台湾问题是(  )', 'BCD', '二战遗留下来的问题', '中美关系中的最大障碍', '中国国内战争遗留下来的问题', '中国的内政问题');
INSERT INTO `duoxiang` VALUES ('680', '2000年2月21日，中国政府发表《一个中国的原则与台湾问题》的白皮书。白皮书中提到，中国政府只能被迫采取断然措施包括使用武力来解决统一的情况或底线是(    )', 'ABC', '如果出现台湾被以任何名义从中国分割出去的重大事变', '如果出现外国侵占台湾', '如果台湾当局无限期地拒绝通过和平谈判解决两岸统一问题', '如果民进党上台');
INSERT INTO `duoxiang` VALUES ('681', '关于台湾问题，下列提法正确的是(    )', 'BCD', '台湾问题和香港.澳门问题一样，都是殖民主义侵略中国遗留下来的问题', '主张用和平方式实现两岸统一，但决不承诺放弃使用武力', '我国对台湾的主权是神圣不可侵犯的', '反对以任何方式改变台湾是中国一部分的地位');
INSERT INTO `duoxiang` VALUES ('682', '“一国两制”构想及其实践的重大历史意义有(    )', 'ABCD', '丰富和发展了马克思主义的国家学说', '丰富了马克思主义原则的坚定性与策略的灵活性相统一的原理', '创造性地把和平共处五项原则用来处理一个国家的内部问题', '为解决国际争端和世界遗留问题提供了新的思路.新的途径和模式');
INSERT INTO `duoxiang` VALUES ('683', '贯彻寄希望于台湾人民的方针，就要大力推动两岸的交流与合作，主要指“三通四流”，即(    )', 'ABCD', '直接通商', '直接通航', '直接通邮', '两岸经济文化交流和人员往来');
INSERT INTO `duoxiang` VALUES ('684', '英国殖民主义者曾曾经强占香港，是通过向中国发动侵略，强迫清政府先后签订的不平等条约有(   )', 'ABD', '《南京条约》', '《北京条约》', '《马关条约》', '《展拓香港界址专条》');
INSERT INTO `duoxiang` VALUES ('685', '为积极推动“和平统一.一国两制”的历史进程，我国外交工作的重点之一是为实现祖国完全统一服务，工作侧重点是(    )', 'ABC', '坚决反对台湾当局在国际上进行的任何分裂活动', '维护国际社会承认一个中国的基本格局', '巩固制约“台独”的外部环境', '打压“台独”的国际活动空间');
INSERT INTO `duoxiang` VALUES ('686', '台.港.澳问题的性质是(   )', 'AB', '台湾问题是内政问题', '香港.澳门问题是殖民地遗留问题', '都是内政问题', '都是殖民地遗留问题');
INSERT INTO `duoxiang` VALUES ('687', '中国政府主张在一个中国的原则上，恢复和进行两岸对话与谈判，这是不能动摇的。十六大报告指出，在一个中国的前提下，什么都可以谈，主要包括(    )', 'ABC', '可以谈正式结束两岸敌对状态', '可以谈与台湾地区身份相适应的国际活动空间', '可以谈台湾当局的政治地位', '可以谈一个中国的具体国名');
INSERT INTO `duoxiang` VALUES ('688', '在两岸统一上寄希望于台湾人民是指(    )', 'ABCD', '把台湾当局的分裂言行与台湾人民区别开来', '把主张维持现状的绝大多数人与主张分裂的少数人区别开来', '把广大民进党党员与少数顽固的“台独”分子区别开来', '把过去坚持过“台独”现已改变立场与仍然坚持“台独”立场的人区别开来');
INSERT INTO `duoxiang` VALUES ('689', '台湾统一后保持原有制度不变，具体是指(  )', 'ABC', '台湾的社会经济制度不变', '台湾的生活方式不变', '台湾同外国的民间关系不变，包括外国在台湾的投资及民间交往不变', '台湾的民主制度不变');
INSERT INTO `duoxiang` VALUES ('690', '“和平统一.一国两制”是一个完整的体系，基本内容就是坚持一个中国的原则，在祖国统一的前提下，国家的主体坚持社会主义制度，同时在香港.澳门.台湾保持原有社会制度.生活方式长期不变，享有高度自治权。具体内容有(    )', 'ABCD', '一个中国；两制并存；高度自治', '尽最大努力争取和平统一，但决不承诺放弃使用武力；解决台湾问题，实现祖国的完全统一，寄希望于台湾人民', '积极促谈，争取通过谈判实现统一；积极促进两岸“三通”和各项交流，为实现和平统一创造条件；集中力量搞好经济建设，是解决国际国内问题的基础，也是实现国家统一的基础', '坚决反对任何“台湾独立”的言行，决不允许任何人以任何方式把台湾从中国分裂出去；坚决反对外国势力插手和干涉台湾问题，解决台湾问题是中国内政，任何国家无权干涉');
INSERT INTO `duoxiang` VALUES ('691', '2005年3月胡锦涛提出新形势发展两岸关系的重要意见，内容是(    )', 'ABCD', '坚持一个中国原则决不动摇', '争取和平统一的努力决不放弃', '寄希望于台湾人民的方针决不改变', '反对“台独”分裂活动决不妥协');
INSERT INTO `duoxiang` VALUES ('692', '党的十七大关于今后一个时期对台工作的指导思想和总体要求是(    )', 'ABCD', '遵循“和平统一.一国两制”的方针和现阶段发展两岸关系.推进祖国和平统一进程的八项主张', '坚持一个中国原则决不动摇，争取和平统一的努力决不放弃，寄希望于台湾人民的方针决不改变，反对“台独”分裂活动决不妥协', '牢牢把握两岸关系和平发展的主题，真诚为两岸同胞谋福祉.为台湾地区谋和平', '维护国家主权和领土完整，维护中华民族根本利益');
INSERT INTO `duoxiang` VALUES ('693', '2004年十六届四中全会曾经提出，“保持香港.澳门长期繁荣稳定是党在新形势下治国理政面临的崭新课题”。党的十七大在总结经验的基础上提出今后在香港.澳门继续推进“一国两制”实践应该坚持的基本原则有(    )', 'ABCD', '坚定不移地贯彻“一国两制”.“港人治港”.“澳人治澳”.高度自治的方针，严格按照特别行政区基本法办事', '全力支持特别行政区政府依法施政，着力发展经济.改善民生，推进民主；把香港.澳门管理好.建设好，两个特别行政区政府是关键', '鼓励香港.澳门各界人士在爱国爱港.爱国爱澳旗帜下和衷共济，促进社会和谐；加强内地与香港.澳门交流合作，实现优势互补.共同发展', '积极支持香港.澳门开展对外交往，坚决反对外部势力干预香港.澳门事务');
INSERT INTO `duoxiang` VALUES ('694', '“一国两制”构想的基本内容，包括(   )', 'ABD', '以“一个中国”为基础，实现“两种制度”', '台.港.澳地区实行高度自治', '台.港.澳地区实现完全自治', '实行“一国两制”长期不变');
INSERT INTO `duoxiang` VALUES ('695', '中国政府所主张的坚持一个中国的原则是指(   )', 'ABC', '世界上只有一个中国', '台.港.澳是中国不可分割的一部分', '中国的主权和领土完整不容分割', '参与国际组织的只能是中华人民共和国');
INSERT INTO `duoxiang` VALUES ('696', '我国宪法规定，特别行政区是国家不可分割的一部分，这表明(   )', 'ACD', '“一国两制”的前提是坚持一个中国', '特别行政区必须坚持四项基本原则', '特别行政区直辖于中央人民政府', '特别行政区政府是国家的一级地方政权机关');
INSERT INTO `duoxiang` VALUES ('697', '50年代，毛泽东.周恩来多次提出和平解决台湾的方针政策，概括起来主要有(   )', 'ABCD', '省亲会友.来去自由', '既往不咎.立功受奖', '国共合作.爱国一家', '和平解放.互不破坏');
INSERT INTO `duoxiang` VALUES ('698', '2013年6月，习近平就坚定不移走两岸关系和平发展道路提出的意见包括(    )', 'ABCD', '坚持从中华民族整体利益的高度把握两岸关系大局。', '坚持在认清历史发展趋势中把握两岸关系前途。', '坚持增进互信.良性互动.求同存异.务实进取。', '坚持稳步推进两岸关系全面发展');
INSERT INTO `duoxiang` VALUES ('699', '邓小平对时代主题的科学判断包括以下几个方面的基本思想 (    )', 'ABD', '我们有可能争取较长时期的和平环境', '和平与发展是当代世界的两大带有全球性的战略问题', '和平与发展是当代世界已经解决的问题', '和平与发展是当代世界主要矛盾的集中体现');
INSERT INTO `duoxiang` VALUES ('700', '我国社会主义现代化建设需要一个长期的和平国际环境，维护和平主要体现在 (    )', 'ABCD', '坚持平等互利原则', '坚持和平共处五项原则', '通过对话解决国与国之间的分歧和争端', '建立国际政治新秩序');
INSERT INTO `duoxiang` VALUES ('701', '进入21世纪，世界和平与发展仍然面临的严峻挑战是 (    )', 'BCD', '国际力量对比严重失衡', '霸权主义和强权政治有新的发展', '南北问题更加突出', '传统安全威胁与非传统安全威胁相互交织');
INSERT INTO `duoxiang` VALUES ('702', '1990年以来，世界上发生的武装80％起因于 (    )', 'ABCD', '能源危机', '宗教矛盾', '民族矛盾', '领土争端');
INSERT INTO `duoxiang` VALUES ('703', '中国共产党十七大报告指出，当今世界仍然很不安宁，世界和平和发展仍然面临着诸多难题和挑战，这些难题和挑战是 (    )', 'ABCD', '霸权主义和强权政治仍然存在', '局部冲突和热点问题此起彼伏', '全球经济关系失衡加剧，南北差距拉大', '传统安全威胁和非传统安全威胁相互交织');
INSERT INTO `duoxiang` VALUES ('704', '恐怖主义是国际社会的一大危害。国际社会应该加强对话和磋商，共同打击国际恐怖主义，中国政府认为 (    )', 'ABCD', '打击恐怖主义要遵守《联合国宪章》的宗旨和原则及公认的国际准则，充分发挥联合国和安理会的作用，一切行动应有利于维持地区及世界和平的长远利益', '不能将恐怖主义与特定的民族或宗教混为一谈，不能对打击恐怖主义采取双重标准', '不能借反恐怖主义推行影响世界和地区稳定与发展的别的政治意图', '无论恐怖主义发生在何时何地.针对何人.以何种方式出现，国际社会都应该共同努力，坚持予以谴责和打击');
INSERT INTO `duoxiang` VALUES ('705', '当今时代的潮流是(  )', 'ABCD', '世界要和平', '人民要合作', '国家要发展', '社会要进步');
INSERT INTO `duoxiang` VALUES ('706', '当前和今后一个时期国际局势发展的基本态势是(    )', 'ACD', '总体和平.局部战乱', '总体一致.局部差异', ' 总体缓和.局部紧张', '总体稳定.局部动荡');
INSERT INTO `duoxiang` VALUES ('707', '在和平发展的大趋势下，当今国际形势的总体特征是(    )', 'ABCD', '世界政治多极化和经济全球化趋势在曲折中发展', '科学技术进步异常迅猛', '恐怖主义已经对全球安全构成严重威胁', ' 霸权主义和强权政治愈演愈烈');
INSERT INTO `duoxiang` VALUES ('708', '我国积极促进世界多极化趋势的发展，是因为(    )', 'ABC', '它有利于世界的和平与发展', '它有利于更好地维护我国的国家利益', '我国是世界多极化和经济全球化的主导', '我国实行独立自主的和平外交政策');
INSERT INTO `duoxiang` VALUES ('709', '经济全球化已成为当今世界经济发展的重要趋势，其明显表现有(    )', 'ABCD', '跨国公司越来越成为世界经济的主导力量', '国际贸易成为各国经济发展不可缺少的组成部分', '国际金融交易总额大大超过国际贸易总额', '国际直接投资迅速增长，投资格局多元化');
INSERT INTO `duoxiang` VALUES ('710', '经济全球化是经济关系国际化发展的一个新阶段，其不同于生产和资本国际化的重要标志是(    )', 'ABD', '世界经济的信息化', '世界经济的市场化', '世界经济的自由化', '国际分工进入一个新阶段');
INSERT INTO `duoxiang` VALUES ('711', '20世纪80年代末90年初，世界格局发生的重大变动可以概括为(    )', 'ACD', '旧的世界格局已经打破', '美国独霸世界的格局已经形成', '新的世界格局尚未形成', '世界处在新旧格局交替的动荡时期');
INSERT INTO `duoxiang` VALUES ('712', '世界多极化格局有利于(    )  ', 'ABCD', '避免新的世界大战的爆发，遏制霸权主义和强权政治', '推动建立公正合理的国际政治经济新秩序', '实现各国人民对和平.稳定.繁荣的新世界的美好追求', '广大发展中国家抓住机遇.发展自己');
INSERT INTO `duoxiang` VALUES ('713', '经济全球化是社会生产力发展的客观要求，有利于(    )  ', 'ABC', '生产要素在全球范围内的优化配置', '各国各地区加强经济技术合作', '世界经济的发展', '国际经济新秩序的建立');
INSERT INTO `duoxiang` VALUES ('714', '中国政府以维护世界和平，促进共同发展为宗旨，就国际政治经济秩序提出了一系列重要主张，以下说法正确的是(    )', 'ABCD', '中国倡导多边主义，主张促进国际关系民主化', '中国主张维护和尊重世界的多样性', '中国主张树立以互信.互利.平等和协作为核心的新安全观', '中国主张促进全球经济.社会的均衡和可持续发展');
INSERT INTO `duoxiang` VALUES ('715', '我国外交政策中的独立自主是指(    )', 'ACD', '国家主权和国家利益高于一切', '天赋人权高于一切', '反对霸权主义，维护世界和平', '中国不同任何国家结盟');
INSERT INTO `duoxiang` VALUES ('716', '我国外交工作的方针是(    )', 'ABC', '坚定不移地奉行独立自主的和平外交政策', '同世界各国建立和发展友好关系', '反对霸权主义和强权政治，维护世界和平', '和平统一，一国两制');
INSERT INTO `duoxiang` VALUES ('717', '我国始终不渝地奉行独立自主的和平外交政策，它的基本含义是(  )', 'ABCD', '对一切国际事务，都要从中国人民和世界人民的根本利益出发，根据事情本身的是非曲直，决定自己的立场', '不屈从于任何外来的压力', '不同任何大国或国家集团结盟', '不搞军事集团，不参加军备竞赛，不进行军事扩张');
INSERT INTO `duoxiang` VALUES ('718', '关于中国对外政策的说法，正确的是(    )', 'ABCD', '促进世界和平与发展是中国对外政策的根本目标', '独立自主是中国对外政策的根本原则', '和平共处五项原则是中国处理国际关系的基本准则', '加强同发展中国家的团结与合作是中国对外政策的基本立足点');
INSERT INTO `duoxiang` VALUES ('719', '在处理国家之间关系上，中国坚持的原则是(    )', 'ABCD', '相互尊重主权和领土完整', '互不侵犯', '互不干涉内政', '平等互利.和平共处');
INSERT INTO `duoxiang` VALUES ('720', '中国共产党与各国政党发展新型的党际交流和合作关系的原则是(    )', 'ABCD', '独立自主', '完全平等', '互相尊重', '互不干涉内部事务');
INSERT INTO `duoxiang` VALUES ('721', '中国努力构筑“面向21世纪新型伙伴关系”框架已经出现，其中有(    )', 'ABCD', '中俄“战略协作伙伴关系”', '中法“全面伙伴关系”', '中美“建设性战略伙伴关系”', '中日“睦邻友好合作关系”');
INSERT INTO `duoxiang` VALUES ('722', '中国政府提出和谐世界应该是(  )', 'ABCD', '民主的世界，坚持民主平等，实现协调合作', '和睦的世界，坚持和睦互信，实现共同安全', '公正的世界，坚持公正互利，实现共同发展', '包容的世界，坚持包容开放，实现文明对话');
INSERT INTO `duoxiang` VALUES ('723', '关于我国处理与周边国家外交关系的主张，正确的是(    )', 'ABCD', '贯彻与邻为善.以邻为伴的周边外交方针', '加强同周边国家的睦邻友好和务实合作', '积极开展区域合作', '共同营造和平稳定.平等互信.合作共赢的地区环境');
INSERT INTO `duoxiang` VALUES ('724', '中国坚持独立自主的和平外交政策，具体就是(    )', 'ABCD', '把国家主权和安全放在第一位，坚定地维护我国的国家利益，反对任何国家损害我国的独立.主权.安全和尊严', '从我国人民和世界人民的根本利益出发，对于一切国际事务，都要根据事情本身的是非曲直，决定自己的立场和政策，不屈从于任何外来压力', '坚持各国的事务应由本国政府和人民决定，世界上的事情应由各国政府和人民平等协商，反对一切形式的霸权主义和强权政治', '不以社会制度和意识形态的异同决定国家关系的亲疏，而是坚持在和平共处五项原则基础上同所有国家建立和发展友好关系；就是坚持不同任何大国或大国集团结盟，不搞军事集团，不参加军备竞赛，不进行军事扩张，永远不谋求霸权');
INSERT INTO `duoxiang` VALUES ('725', '新中国成立初期，毛泽东提出的外交方针是(    )', 'ABC', ' “另起炉灶”', ' “打扫干净屋子再请客”', ' “一边倒”', ' “真正的不结盟”');
INSERT INTO `duoxiang` VALUES ('726', ' 2005年9月15日，国家主席胡锦涛在联合国成立60周年首脑会议上，发表了《努力建设持久和平.共同繁荣的和谐世界》的讲话，他提出(    )', 'ABCD', '坚持多边主义，实现共同安全', '坚持互利合作，实现共同繁荣', '坚持包容精神，共建和谐世界', '坚持积极稳妥方针，推进联合国改革');
INSERT INTO `duoxiang` VALUES ('727', '在处理国际关系和外交关系方面，我们要坚持的外交工作布局是(    )', 'ABCD', '大国是关键', '周边是首要', '发展中国家是基础', '多边是舞台');
INSERT INTO `duoxiang` VALUES ('728', '我国独立自主和平外交政策的基本原则是(    )', 'ABCD', '坚持独立自主地处理一切国际事务的原则', '坚持以和平共处五项原则为指导国家间关系的基本原则', '坚持同发展中国家加强团结与合作的原则', '坚持爱国主义与履行国际义务相统一的原则');
